import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import { useParams } from "react-router";
import InfoErrorApi from '../../Utils/InfoErrorApi';
import InfoExitoApi from '../../Utils/InfoExitoApi';
import ClasificacionRadicadoApi from '../../Api/Services/ClasificacionRadicadoApi';
import Spinner from '../../Utils/Spinner';
import { Navigate } from "react-router-dom";
import ParametrosMasApi from "./../../Api/Services/ParametrosMasApi";
import CierreEtapaApi from '../../Api/Services/CierreEtapaApi';
import { useLocation } from 'react-router-dom'
import { hasAccess } from '../../../components/Utils/Common';
import ListaBotones from '../../Utils/ListaBotones';
import DataTable from 'react-data-table-component';

function ClasificacionRadicadoLista() {


    const [errorApi, setErrorApi] = useState('');
    const [isRedirect, setIsRedirect] = useState(false);

    const [getIdTipoExpediente, setIdTipoExpediente] = useState();

    const [listaTipoExpediente, setListaTipoExpediente] = useState({ data: {} });
    const [respuestaTipoExpediente, setTipoExpediente] = useState(false);

    const [listaTipoQueja, setListaTipoQueja] = useState({ data: {} });
    const [respuestaTipoQueja, setTipoQueja] = useState(false);

    const [listaTerminoRespuesta, setListaTerminoRespuesta] = useState({ data: {} });
    const [respuestaTerminoRespuesta, setTerminoRespuesta] = useState(false);
    const [estadoEtapaCapturaReparto, setEstadoEtapaCapturaReparto] = useState(false);
    const [getEstadoLista, setEstadoLista] = useState('');

    const [perPage, setPerPage] = useState(10);
    const [pageActual, setPageActual] = useState(1);

    const location = useLocation()
    const { from } = location.state
    let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;
    let vigencia = from.vigencia;
    let id_etapa = from.id_etapa;

    const columns = [
        {
            name: 'Tipo de expediente',
            cell: clasificacion_radicado => <div> {clasificacion_radicado.attributes.expediente.nombre} {clasificacion_radicado.attributes.expediente.id === 3 || clasificacion_radicado.attributes.expediente.id === 2 ? clasificacion_radicado.attributes.tipo_queja.nombre : null}
                {clasificacion_radicado.attributes.expediente.id === 1 ? clasificacion_radicado.attributes.tipo_derecho_peticion.nombre : null}
                {clasificacion_radicado.attributes.expediente.id === 4 ? clasificacion_radicado.attributes.fecha_termino : null}
                <br />{clasificacion_radicado.attributes.expediente.id === 4 ? " (" + clasificacion_radicado.attributes.hora_termino + ") horas" : null}
                <br />{clasificacion_radicado.attributes.observaciones}</div>,


            sortable: true,
            width: "20%"
        },
        {
            name: 'Registrado En',
            selector: clasificacion_radicado => 'PENDIENTE',
            sortable: true,
            width: "15%"
        },


        {
            name: 'Registrado por',
            cell: clasificacion_radicado => clasificacion_radicado.attributes.nombre_completo,
            sortable: true
        },
        {
            name: 'Fecha de registro',
            selector: clasificacion_radicado => clasificacion_radicado.attributes.created_at,
            sortable: true,
            width: "20%"
        },
        {
            name: 'Estado actual',
            selector: clasificacion_radicado => 'Asignado',
            sortable: true,
        },
        {
            name: 'Termino',
            selector: clasificacion_radicado => 'Ver Normatividad',
            sortable: true,
        },
    ];


    const [getRoutes, setRoutes] = useState({
        id_etapa: 1,
        id_fase: 1,
        crear_registro: "/ClasificacionRadicadoForm",
        consultar_registros: "/ClasificacionRadicadoLista",
        adjuntar_documento: "/SoporteRadicadoForm",
        repositorio_documentos: "/SoporteRadicadoLista",
        modulo: "CR_ClasificacionRadicado",
        funcionalidad_crear: "Crear",
        funcionalidad_consultar: "Consultar"
    });


    const redirectToRoutes = () => {
        return <Navigate to={`/ClasificacionRadicadoLista/`} state={{ from: from }} />;
    }

    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);
            setEstadoLista("Activos")
            cargarClasificaciones(1, 10, '1');

            // VALIDA CIERRE DE ETAPA EVALUACION
            const dataCierreEtapa = {

                "data": {
                    "type": "cerrar_etapa",
                    "attributes": {
                        "id_proceso_disciplinario": procesoDisciplinarioId,
                        "id_etapa": 1,
                        "id_delegada": "id_delegada",
                        "id_funcionario": "id_funcionario"
                    }
                }
            }

            CierreEtapaApi.getCierreEtapaByIdProcesoDisciplinario(dataCierreEtapa).then(
                datos => {
                    if (!datos.error) {
                        if (datos["data"].length > 0) {
                            setEstadoEtapaCapturaReparto(true);
                        }
                    }
                    else {
                        setErrorApi(datos.error.toString())
                        window.showModal(1)
                    }
                }
            )
        }
        fetchData();
    }, []);


    ///////////////////////////////////////////////////////////
    /////////////////// PARA LA BÚSQUEDA //////////////////////
    ///////////////////////////////////////////////////////////

    const [respuestaClasificacionRadicado, setRespuestaClasificacionRadicado] = useState(false);
    const [clasificacionRadicadoLista, setClasificacionRadicadoLista] = useState({ data: [], links: [], meta: [] });

    const [respuestaClasificacionRadicadoTotal, setRespuestaClasificacionRadicadoTotal] = useState(false);
    const [clasificacionRadicadoListaTotal, setClasificacionRadicadoListaTotal] = useState({ data: [], links: [], meta: [] });

    const handleSearch = (event) => {
        try {
            if ((event.target.value.trim().trim() == "") || (event.target.value.trim().length == 0)) {

                setClasificacionRadicadoLista(clasificacionRadicadoListaTotal);
            }
            else {



                var filteredData = {
                    data: clasificacionRadicadoListaTotal.data.filter(
                        suggestion => ((
                            suggestion.attributes.created_at + suggestion.attributes.estado
                            + suggestion.attributes.observaciones
                            + suggestion.attributes.nombre_completo
                            + ((suggestion.attributes.expediente ? suggestion.attributes.expediente.nombre : "") + " " + (suggestion.attributes.tipo_queja ? suggestion.attributes.tipo_queja.nombre : ""))
                            + (suggestion.attributes.tipo_derecho_peticion ? suggestion.attributes.tipo_derecho_peticion.nombre : "")
                            + (suggestion.attributes.fecha_termino ? suggestion.attributes.fecha_termino : "")
                            + (suggestion.attributes.hora_termino ? suggestion.attributes.hora_termino : "")).toLowerCase().includes(event.target.value.toLowerCase()))
                    )
                };
                setClasificacionRadicadoLista(filteredData)
            }
        } catch (error) {
            console.error(error);
        }

    };

    const listaClasificacionRadicado = () => {

        if (clasificacionRadicadoLista.data != null && typeof (clasificacionRadicadoLista.data) != 'undefined') {
            return (


                clasificacionRadicadoLista.data.map((clasificacion_radicado, i) => {
                    return (
                        cargarTabla(clasificacion_radicado)
                    )
                })
            )
        }
        else {
            return (

                clasificacionRadicadoLista.map((clasificacion_radicado, i) => {
                    return (
                        cargarTabla(clasificacion_radicado)
                    )
                })
            )
        }


    }

    const cargarTabla = (clasificacion_radicado) => {
        return (
            <tr key={clasificacion_radicado.id}>
                <td>
                    {clasificacion_radicado.attributes.expediente.nombre} {clasificacion_radicado.attributes.expediente.id === 3 || clasificacion_radicado.attributes.expediente.id === 2 ? clasificacion_radicado.attributes.tipo_queja.nombre : null}
                    {clasificacion_radicado.attributes.expediente.id === 1 ? clasificacion_radicado.attributes.tipo_derecho_peticion.nombre : null}
                    {clasificacion_radicado.attributes.expediente.id === 4 ? clasificacion_radicado.attributes.fecha_termino : null}
                    {clasificacion_radicado.attributes.expediente.id === 4 ? " (" + clasificacion_radicado.attributes.hora_termino + ") horas" : null}
                    <br />{clasificacion_radicado.attributes.observaciones}
                </td>
                <td>PENDIENTE</td>
                <td>{clasificacion_radicado.attributes.nombre_completo}</td>
                <td>{clasificacion_radicado.attributes.created_at}</td>
                <td>Asignado</td>
                <td>Ver Normatividad</td>
            </tr>
        )
    }

    const cargarClasificaciones = (page, perPage, estado) => {

        const data = {
            "data": {
                "type": "clasificacion_radicado",
                "attributes": {
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "id_etapa": "1",
                    "id_tipo_expediente": "1",
                    "estado": estado,
                    'per_page': perPage,
                    'current_page': page
                }
            }
        }
        console.log(JSON.stringify(data));

        if (hasAccess('CR_ClasificacionRadicado', 'Consultar')) {
            ClasificacionRadicadoApi.getAllClasificacionRadicadoByIdProDisciplinario(data, procesoDisciplinarioId).then(
                datos => {
                    if (!datos.error) {
                        setClasificacionRadicadoLista(datos)
                        setRespuestaClasificacionRadicado(true)
                        setClasificacionRadicadoListaTotal(datos)
                        setRespuestaClasificacionRadicadoTotal(true)

                        if (datos.data.length > 0) {
                            from.idTipoExpediente = datos.data[0]["attributes"]["id_tipo_expediente"];
                        }
                        window.showSpinner(false);
                    }
                    else {
                        setErrorApi(datos.error.toString())
                        window.showSpinner(false);
                        window.showModal()
                    }
                }
            )
        }
        else {
            window.showSpinner(false)
        }
    }

    const handlePageChange = page => {
        window.showSpinner(true);
        setPageActual(page);
        cargarClasificaciones(page, perPage, '1');
    }

    const handlePerRowsChange = async (newPerPage, page) => {
        window.showSpinner(true);
        setPerPage(newPerPage);
        setPageActual(page);
        cargarClasificaciones(page, newPerPage, '1');

    }

    const handleCallback = (childData) => {
        try {
            window.showSpinner(true);
            setEstadoLista(childData == '0' ? "Inactivos" : "Activos")
            cargarClasificaciones(1, 10, childData);
        } catch (error) {

        }

    }

    ///////////////////////////////////////////////////////////
    /////////////////// PARA LA BÚSQUEDA //////////////////////
    ///////////////////////////////////////////////////////////


    return (
        <>
            <Spinner />
            <Formik>
                <Form>
                    <div className="col-md-12">
                        <div className="block block-rounded block-bordered">
                            <div className="block-content">
                                <nav aria-label="breadcrumb">
                                    <ol className="breadcrumb breadcrumb-alt push">
                                        <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                        <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`} state={{ from: from }}><small>Ramas del proceso</small></Link></li>
                                        <li class="breadcrumb-item"> <small>Clasificacion Radicado Lista</small></li>
                                    </ol>
                                </nav>
                            </div>
                        </div>
                    </div>

                    <div className="block block-themed">
                        <div className="block-header">
                            <h3 className="block-title">SINPROC No {radicado} :: <strong>Consultar lista de Clasificación del radicado {getEstadoLista}</strong></h3>
                        </div>

                        <div className="block-content">
                            <>


                                <div className='row'>

                                    <div className='col-md-3'>
                                        <div className="form-group ">
                                            <label htmlFor='search'>Buscar: </label>
                                            <Field type="text" id="search" name="search" onChange={handleSearch} onKeyPress={(e) => { e.key === 'Enter' && e.preventDefault(); }} className="form-control border border-success" placeholder="Buscar" />

                                        </div>
                                    </div>

                                    <ListaBotones getRoutes={getRoutes} from={from} parentCallback={handleCallback} id="botonesNavegacion" name="botonesNavegacion" />

                                </div>



                                {
                                    (hasAccess('CR_ClasificacionRadicado', 'Consultar')) ? (

                                        <DataTable className="table table-bordered table-striped table-vcenter js-dataTable-full"
                                            columns={columns}
                                            data={clasificacionRadicadoLista.data}
                                            perPage={perPage}
                                            page={pageActual}
                                            pagination
                                            noDataComponent="Sin datos"
                                            paginationServer
                                            paginationTotalRows={clasificacionRadicadoLista.meta ? clasificacionRadicadoLista.meta.total : 1}
                                            onChangePage={handlePageChange}
                                            onChangeRowsPerPage={handlePerRowsChange}
                                            defaultSortFieldId="Nombre"
                                            striped
                                            icon
                                            paginationComponentOptions={{ rowsPerPageText: 'Filas por página' }}
                                            defaultSortAsc={false}
                                        />
                                    ) : null
                                }
                            </>
                        </div>
                    </div>
                </Form>
            </Formik>
        </>
    );


}
export default ClasificacionRadicadoLista;
