export default global.Constants = {
    ESTADOS: {
        ACTIVO: '1',
        INACTIVO: '0'
    },

    //MIS PENDIENTES
    RANGOS: {
        TODOS: '-1'
    },

    BOTONES_RAMAS: {
        PRIMARY: 'btn btn-sm btn-secundary w2d_btn-large mr-1 mb-3 text-left',
        SUCCESS: 'btn btn-sm btn-success w2d_btn-large mr-1 mb-3 text-left'
    },

    //PARA LOS TEXT_AREA
    TEXT_AREA: {
        CARACTERES_ESPECIALES: `\`!@#$%^&*()_+\-=\[\]{};':"\\|<>\/?~`,
        CANTIDAD_MINIMA_DESCRIPCION: 100 //Cantidad mínima que se muestra en las descripciones
    },

    //CARACTERES ESECIALES
    CARACTERES_ESPECIALES: {
        formatOnlyLetters: /^[A-Za-z\s]+$/,
        formatOnlyLettersOrnUmbers: /^[0-9a-zA-Z\s]+$/,
        formatWithOutSpecials: /^[A-Za-z0-9#-\s]+$/g
    },

    //ETAPAS
    ETAPAS: {
        CAPTURA_REPARTO: '1',
        EVALUACION: '2',
        EVALUACION_PD: '3'
    },

    //FASES
    FASES: {
        ANTECEDENTES: 1,
        INTERESADO: 2,
        CLASIFICACION: 3,
        ENTIDAD_INVESTIGADO: 4,
        SOPORTE_RADICADO: 5


    },

    TIPO_INGRESSO: {
        SIRIUS: 1,
        DESGLOSE: 2,
        SINRPOC: 3
    },

    //TABLAS
    DATA_TABLE: {
        SIN_DATOS_TABLE: 'Sin datos',
        FILTRO_POR_PAGINA: 'Filas por página'
    },

    CLASIFICACION_RADICADO: {
        TIPO_QUEJA_EXTERNA: 1,
        TIPO_QUEJA_INTERNA: 2
    },

    TIPOS_EXPEDIENTES: {
        DERECHO_PETICION: '1',
        PODER_REFERENTE: '2',
        QUEJA: '3',
        TUTELA: '4'
    },

    DERECHOS_PETICION: {
        COPIAS: '1',
        GENERAL: '2',
        ALERTA: '3'
    },

    TERMINOS_RESPUESTA: {
        DIAS: '1',
        HORAS: '2'
    },

    TIPO_DOCUMENTO: {
        NO_INFORMA: '4'
    },

    ACCESO_DEPENDENCIA: {
        REMITIR_PROCESO: '1',
        CREACION_PROCESO: '2',
        REMISION_QUEJA_INCORPORACION: '3',
        CREAR_INTERESADO: '4',
        CREAR_USUARIO: '5',
        MODIFICAR_USUARIO: '6'

    },

    SUJETO_PROCESAL: {
        INTERESADO: '1',
        INTERESADO_ACOSO_LABORAL: '2',
        DISCIPLINADO: '3',
        APODERADO: '4',
        DEFENSOR_OFICIO: '5',
        MINISTERIO_PUBLICO: '6',
        VICTIMAS_GRAVES: '7'
    },

    ENTIDAD: {
        PUBLICA: '1',
        PRIVADA: '2'
    },

