import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import AntecedentesApi from '../Api/Services/AntecedentesApi';
import { useParams } from "react-router";
import ClasificacionRadicadoApi from '../Api/Services/ClasificacionRadicadoApi';
import DatosInteresadoApi from '../Api/Services/DatosInteresadoApi';
function RamasProceso() {

    const [respuestaAntecedente, setRespuestaAntecedente] = useState(false);
    const [antecedente, setAntecedente] = useState({ data:{} });
    const [errorApi, setErrorApi] = useState('');
    const [cambiaColorAntecedentes, setCambiaColorAntecedentes] = useState(false);
    const [cambiaColorDatosInteresado, setCambiaColorDatosInteresado] = useState(false);
    const [cambiaColorClasificacionRadicado, setCambiaColorClasificacionRadicado] = useState(false);
    const [cambiaColorEntidadInvestigado, setCambiaColorEntidadInvestigado] = useState(false);

    let { radicado, procesoDisciplinarioId } = useParams();

    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);

            const data = {
                "data": {
                    "type": "antecedentes",
                    "attributes": {
                        "antecedentes": 1,
                        "id_dependencia": 1
                    }
                }
            }

            const dataRadicado = {
                "data": {
                    "type": "clasificacion_radicado",
                    "attributes": {
                        "id_proceso_disciplinario": procesoDisciplinarioId,
                        "id_etapa": "1",
                        "id_tipo_expediente": "1"
                    }
                }
            }

            const dataDatosInteresado = {
                "data": {
                    "type": "clasificacion_radicado",
                    "attributes": {
                        "tipo_documento" : "0",
                        "numero_documento" : "0",
                        "primer_nombre" : "0",
                        "segundo_nombre" : "0",
                        "primer_apellido" : "0",
                        "segundo_apellido" : "0",
                        "id_tipo_interesao": "0",
                        "id_tipo_sujeto_procesal": "0",
                        "id_departamento": "0",
                        "id_ciudad": "0",
                        "direccion": "0",
                        "id_localidad": "0",
                        "email": "0",
                        "telefono_celular": "0",
                        "telefono_fijo": "0",
                        "id_sexo": "0",
                        "id_genero": "0",
                        "id_orientacion_sexual": "0",
                        "cargo": "0",
                         "tarjeta_profesional": "0",
                         "id_dependencia": "0",
                         "id_tipo_entidad": "0",
                         "nombre_entidad":"0"
                    }
                }
            }


            AntecedentesApi.getAllAntecedente(data, procesoDisciplinarioId).then(
                datos => {
                    if(!datos.error){
                        setAntecedente(datos)
                        if(datos != null){
                            setCambiaColorAntecedentes(true);
                        }
                        setRespuestaAntecedente(true)
                    }
                    else{
                        setErrorApi(datos.error.toString())
                        window.showModal()
                    }
                    window.showSpinner(false);
                }
            )


            ClasificacionRadicadoApi.getAllClasificacionRadicadoByIdProDisciplinario(dataRadicado, procesoDisciplinarioId).then(
                datos => {
                    if(!datos.error){
                        if(datos["id_tipo_expediente"] != null){
                            console.log(datos);
                            setCambiaColorClasificacionRadicado(true);
                        }
                        
                    }
                    else{
                        setErrorApi(datos.error.toString())
                        window.showModal(1)
                    }
                    window.showSpinner(false);
                }
            )


            DatosInteresadoApi.getAllDatosInteresadoByIdProDisciplinario(dataDatosInteresado, procesoDisciplinarioId).then(
                datos => {
                    if(!datos.error){
                        if(datos["ID_TIPO_INTERESAO"] != null){
                            console.log(datos);
                            setCambiaColorDatosInteresado(true);
                        }
                        
                    }
                    else{
                        setErrorApi(datos.error.toString())
                        window.showModal(1)
                    }
                    window.showSpinner(false);
                }
            )
        }
        fetchData();
    }, []);

    const componentAntecedente = () => {

        var fecha_creado = new Date(antecedente.data.attributes.fecha_creado);
        fecha_creado = fecha_creado.getFullYear()+"-"+(fecha_creado.getMonth()+1)+"-"+fecha_creado.getDate()

        return(
            <>

                <div className="col-lg-12 push">
                    <div className="alert alert-info" role="alert">
                        <h3 className="text-center">(RADICADO) - (PENDIENTE NOMBRE): {radicado}</h3>
                        <span><strong>ANTECEDENTE: </strong></span><br/>
                        <span>{antecedente.data.attributes.descripcion}</span><br/>

                        <br/>
                        <div className="row">
                            <div className="col-lg-6"><span><strong>REGISTRADO POR: </strong>PENDIENTE</span></div>
                            <div className="col-lg-6"><span><strong>DEPENDENCIA REGISTRO: </strong> {antecedente.data.attributes.dependencia_origen}</span></div>
                        </div>

                        <div className="row">
                            <div className="col-lg-4"><span><strong>VIGENCIA: </strong>{antecedente.data.attributes.vigencia}</span></div>
                            <div className="col-lg-4"><span><strong>FECHA REGISTRO: </strong> {antecedente.data.attributes.fecha_registro}</span></div>
                            <div className="col-lg-4"><span><strong>FECHA DE INGRESO: </strong> {fecha_creado}</span></div>
                        </div>

                    </div>
                </div>
            </>
        )
    }

    return (

            <div className="row">


                <div className="col-md-12">
                    <div className="block block-rounded block-bordered">
                        <div className="block-content">
                            <nav aria-label="breadcrumb">
                                <ol className="breadcrumb breadcrumb-alt push">

                                    <li className="breadcrumb-item"><Link underline="hover" className="text-dark"
                                                                          to={`/MisPendientes`}>
                                        <small>Mis Pendientes</small>
                                    </Link></li>
                                    <li className="breadcrumb-item"><small>Ramas del proceso</small></li>
                                </ol>
                            </nav>
                        </div>
                    </div>
                </div>

                {  respuestaAntecedente ? componentAntecedente() : null }

                <div className="col-lg-12 push">
                    <div id="accordion2" role="tablist" aria-multiselectable="true">
                        
                        <div className="block block-rounded mb-1">
                            <div className="block-header block-header-default" role="tab" id="accordion2_h1">
                                <h3><a className="font-w600" data-toggle="collapse" data-parent="#accordion2" 
                                href="#accordion2_q1" aria-expanded="true" aria-controls="accordion2_q1">Captura y reparto</a>
                                <div className="font-size-sm text-muted">Días desde su registro: 0</div>
                                </h3>
                            </div>
                            <div id="accordion2_q1" className="collapse show" role="tabpanel" aria-labelledby="accordion2_h1">
                                
                                <div className="block-content">
                                    <ul className="nav-items push">

                                        <li>

                                            {cambiaColorAntecedentes && (
                                                <div className="media-body">
                                                    <Link to={`/AntecedentesLista/${radicado}/${procesoDisciplinarioId}`}>
                                                        <button type="button" className="btn btn-sm btn-danger mr-1 mb-3">
                                                            <i className="fa fa-fw fa-archive mr-1"></i> Antecedentes
                                                        </button>
                                                    </Link>

                                                    <i class="fas fa-folder-open"></i>
                                                </div>
                                            )}

                                            {!cambiaColorAntecedentes && (
                                                <div className="media-body">
                                                    <Link to={`/AntecedentesLista/${radicado}/${procesoDisciplinarioId}`}>
                                                        <button type="button" className="btn btn-sm btn-primary mr-1 mb-3">
                                                            <i className="fa fa-fw fa-archive mr-1"></i> Antecedentes
                                                        </button>
                                                    </Link>
                                                </div>
                                            )}

                                        </li>

                                        <li>
                                           {cambiaColorDatosInteresado && (
                                                <div className="media-body">
                                                    <Link to={`/DatosInteresadoLista/${radicado}/${procesoDisciplinarioId}`}>
                                                        <button type="button" className="btn btn-sm btn-danger mr-1 mb-3" data-toggle="modal" data-target="#modal-datos-interesado">
                                                            <i className="fa fa-fw fa-archive mr-1"></i> Datos interesado
                                                        </button>
                                                    </Link>
                                                </div>
                                            )}

                                            {!cambiaColorDatosInteresado && (
                                                <div className="media-body">
                                                    <Link to={`/DatosInteresadoLista/${radicado}/${procesoDisciplinarioId}`}>
                                                        <button type="button" className="btn btn-sm btn-primary mr-1 mb-3" data-toggle="modal" data-target="#modal-datos-interesado">
                                                            <i className="fa fa-fw fa-archive mr-1"></i> Datos interesado
                                                        </button>
                                                    </Link>
                                                </div>
                                            )}


                                        </li>

                                        <li>

                                            {cambiaColorClasificacionRadicado && (
                                                <div className="media-body">
                                                    <Link to={`/ClasificacionRadicadoLista/${radicado}/${procesoDisciplinarioId}`}>
                                                        <button type="button" className="btn btn-sm btn-danger mr-1 mb-3">
                                                            <i className="fa fa-fw fa-archive mr-1"></i> Clasificación del radicado
                                                        </button>
                                                    </Link>
                                                </div>
                                            )}

                                            {!cambiaColorClasificacionRadicado && (
                                                <div className="media-body">
                                                    <Link to={`/ClasificacionRadicadoForm/${radicado}/${procesoDisciplinarioId}`}>
                                                        <button type="button" className="btn btn-sm btn-primary mr-1 mb-3">
                                                            <i className="fa fa-fw fa-archive mr-1"></i> Clasificación del radicado
                                                        </button>
                                                    </Link>
                                                </div>
                                            )}
                                        </li>

                                        <li>

                                            {cambiaColorEntidadInvestigado && (
                                                <div className="media-body">
                                                    <Link to={`/EntidadInvestigadoLista/${radicado}/${procesoDisciplinarioId}`}>
                                                        <button type="button" className="btn btn-sm btn-danger mr-1 mb-3">
                                                            <i className="fa fa-fw fa-archive mr-1"></i> Entidad del investigado
                                                        </button>
                                                    </Link>
                                                </div>
                                            )}

                                            {!cambiaColorEntidadInvestigado && (
                                                <div className="media-body">
                                                    <Link to={`/EntidadInvestigadoLista/${radicado}/${procesoDisciplinarioId}`}>
                                                        <button type="button" className="btn btn-sm btn-primary mr-1 mb-3">
                                                            <i className="fa fa-fw fa-archive mr-1"></i> Entidad del investigado
                                                        </button>
                                                    </Link>
                                                </div>
                                            )}



                                        </li>


                                        <li>

                                            {cambiaColorEntidadInvestigado && (
                                                <div className="media-body">
                                                    <Link to={`/SoporteRadicadoForm/${radicado}/${procesoDisciplinarioId}`}>
                                                        <button type="button" className="btn btn-sm btn-danger mr-1 mb-3">
                                                            <i className="fa fa-fw fa-archive mr-1"></i> Soporte Radicado EN CONSTRUCCION
                                                        </button>
                                                    </Link>
                                                </div>
                                            )}

                                            {!cambiaColorEntidadInvestigado && (
                                                <div className="media-body">
                                                    <Link to={`/SoporteRadicadoForm/${radicado}/${procesoDisciplinarioId}`}>
                                                        <button type="button" className="btn btn-sm btn-primary mr-1 mb-3">
                                                            <i className="fa fa-fw fa-archive mr-1"></i> Soporte Radicado EN CONSTRUCCION
                                                        </button>
                                                    </Link>
                                                </div>
                                            )}



                                        </li>
                                        
                                    </ul>
                                </div>

                            </div>
                        </div>

                        <div className="block block-rounded mb-1">
                            <div className="block-header block-header-default" role="tab" id="accordion2_h2">
                                <h3><a className="font-w600" data-toggle="collapse" data-parent="#accordion2" href="#accordion2_q2" aria-expanded="true" aria-controls="accordion2_q2">Evaluación</a></h3>
                            </div>
                            <div id="accordion2_q2" className="collapse" role="tabpanel" aria-labelledby="accordion2_h2">
                                <div className="block-content">
                                    <p>Dolor posuere proin blandit accumsan senectus netus nullam curae, ornare laoreet adipiscing luctus mauris adipiscing pretium eget fermentum, tristique lobortis est ut metus lobortis tortor tincidunt himenaeos habitant quis dictumst proin odio sagittis purus mi, nec taciti vestibulum quis in sit varius lorem sit metus mi.</p>
                                </div>
                            </div>
                        </div>

                        <div className="block block-rounded mb-1">
                            <div className="block-header block-header-default" role="tab" id="accordion2_h3">
                                <h3><a className="font-w600" data-toggle="collapse" data-parent="#accordion2" href="#accordion2_q3" aria-expanded="true" aria-controls="accordion2_q3">Evaluación en PD</a></h3>
                            </div>
                            <div id="accordion2_q3" className="collapse" role="tabpanel" aria-labelledby="accordion2_h3">
                                <div className="block-content">
                                    <p>Dolor posuere proin blandit accumsan senectus netus nullam curae, ornare laoreet adipiscing luctus mauris adipiscing pretium eget fermentum, tristique lobortis est ut metus lobortis tortor tincidunt himenaeos habitant quis dictumst proin odio sagittis purus mi, nec taciti vestibulum quis in sit varius lorem sit metus mi.</p>
                                </div>
                            </div>
                        </div>

                    </div>
                </div>

            </div>    
            
            
    );
}


export default RamasProceso;
