import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import { Field, Form, Formik } from 'formik';
import AntecedentesApi from '../Api/Services/AntecedentesApi';
import { useParams } from "react-router";
import ClasificacionRadicadoApi from '../Api/Services/ClasificacionRadicadoApi';
import DatosInteresadoApi from '../Api/Services/DatosInteresadoApi';
import Spinner from '../Utils/Spinner';
import EntidadInvestigadoApi from '../Api/Services/EntidadInvestigadoApi';
import CierreEtapaApi from '../Api/Services/CierreEtapaApi';
function RamasProceso() {

    const [respuestaAntecedente, setRespuestaAntecedente] = useState(false);
    const [antecedente, setAntecedente] = useState({ data:{} });
    const [errorApi, setErrorApi] = useState('');
    const [cambiaColorAntecedentes, setCambiaColorAntecedentes] = useState(false);
    const [cambiaColorDatosInteresado, setCambiaColorDatosInteresado] = useState(false);
    const [cambiaColorClasificacionRadicado, setCambiaColorClasificacionRadicado] = useState(false);
    const [cambiaColorEntidadInvestigado, setCambiaColorEntidadInvestigado] = useState(false);
    const [cambiaColorSoporteRadicado, setCambiaColorSoporteRadicado] = useState(false);


    let { radicado, procesoDisciplinarioId } = useParams();

    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);

            const data = {
                "data": {
                    "type": "antecedentes",
                    "attributes": {
                        "antecedentes": 1,
                        "id_dependencia": 1,
                        "fecha_registro": "NO_APLICA"
                    }
                }
            }

            const dataRadicado = {
                "data": {
                    "type": "clasificacion_radicado",
                    "attributes": {
                        "id_proceso_disciplinario": procesoDisciplinarioId,
                        "id_etapa": "1",
                        "id_tipo_expediente": "1"
                    }
                }
            }

            const dataDatosInteresado = {
                "data": {
                    "type": "clasificacion_radicado",
                    "attributes": {
                        "tipo_documento" : "0",
                        "numero_documento" : "0",
                        "primer_nombre" : "0",
                        "segundo_nombre" : "0",
                        "primer_apellido" : "0",
                        "segundo_apellido" : "0",
                        "id_tipo_interesao": "0",
                        "id_tipo_sujeto_procesal": "0",
                        "id_departamento": "0",
                        "id_ciudad": "0",
                        "direccion": "0",
                        "id_localidad": "0",
                        "email": "0",
                        "telefono_celular": "0",
                        "telefono_fijo": "0",
                        "id_sexo": "0",
                        "id_genero": "0",
                        "id_orientacion_sexual": "0",
                        "cargo": "0",
                         "tarjeta_profesional": "0",
                         "id_dependencia": "0",
                         "id_tipo_entidad": "0",
                         "nombre_entidad":"0",
                         "id_proceso_disciplinario":procesoDisciplinarioId
                    }
                }
            }

            const dataEntidadInvestigado = {
                "data": {
                    "type": "entidad_investigado",
                    "attributes": {
                        "id_proceso_disciplinario": procesoDisciplinarioId,
                        "id_etapa": 1,
                        "nombre_entidad": "-1",
                        "nombre_investigado": "-1",
                        "cargo": "-1",
                        "codigo": "",
                        "observaciones": "",
                        "estado": true,
                        "requiere_registro":true

                    }
                }
            }
            //console.log(JSON.stringify(data));

            AntecedentesApi.getAllAntecedente(data, procesoDisciplinarioId).then(
                datos => {
                    if(!datos.error){
                        setAntecedente(datos)
                        if(datos != null){
                            setCambiaColorAntecedentes(true);
                        }
                        setRespuestaAntecedente(true)
                    }
                    else{
                        setErrorApi(datos.error.toString())
                        window.showModal()
                    }
                    
                }
            )

            
            ClasificacionRadicadoApi.getAllClasificacionRadicadoByIdProDisciplinario(dataRadicado, procesoDisciplinarioId).then(
                datos => {
                    if(!datos.error){
                        //console.log(JSON.stringify(datos));
                        //console.log(datos["data"].length);
                        if(datos["data"].length  > 0){
                         
                            setCambiaColorClasificacionRadicado(true);
                        }
                        
                    }
                    else{
                        
                        setErrorApi(datos.error.toString())
                        window.showModal(1)
                    }
                    
                }
            )


            DatosInteresadoApi.getAllDatosInteresadoByIdProDisciplinario(dataDatosInteresado, procesoDisciplinarioId).then(
                datos => {
                    if(!datos.error){
                        if(datos["data"].length  > 0){
                        
                            setCambiaColorDatosInteresado(true);
                        }
                        
                    }
                    else{
                        setErrorApi(datos.error.toString())
                        window.showModal(1)
                    }
                    
                }
            )

            EntidadInvestigadoApi.getAllEntidadInvestigadoByIdProDisciplinario(dataEntidadInvestigado, procesoDisciplinarioId).then(
                datos => {
             
                    if(!datos.error){
                        if(datos["data"].length  > 0){
                        
                            setCambiaColorEntidadInvestigado(true);
                        }
                        
                    }
                    else{
                        setErrorApi(datos.error.toString())
                        window.showModal(1)
                    }
                   
                }
            )

            const timer = setTimeout(() => {
                window.showSpinner(false);
              }, 3000);
              return () => clearTimeout(timer);
        }
        fetchData();
    }, []);

    const cerrarEtapa = (datos) => {

        let data

        data = {
            "data": {
                "type": "cerrar_etapa",
                "attributes": {
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "id_etapa": datos.inputEtapa,
                    "id_delegada": 1,
                    "id_funcionario": 1
                }
            }
        }

        console.log(data);
        
        CierreEtapaApi.addCierreEtapa(data).then(
            datos => {
                if(!datos.error){
                    window.showModal(2)
                }
                else{
                    setErrorApi(datos.error.toString())
                    window.showModal(1)
                }
            }
        )
    }

  


    const componentAntecedente = () => {

        var fecha_creado = new Date(antecedente.data.attributes.fecha_creado);
        fecha_creado = fecha_creado.getFullYear()+"-"+(fecha_creado.getMonth()+1)+"-"+fecha_creado.getDate()

        return(
            <>

                <div className="col-lg-12 push">
                    <div className="alert alert-info" role="alert">
                        <h3 className="text-center">(RADICADO) - (PENDIENTE NOMBRE): {radicado}</h3>
                        <span><strong>ANTECEDENTE: </strong></span><br/>
                        <span>{antecedente.data.attributes.descripcion}</span><br/>

                        <br/>
                        <div className="row">
                            <div className="col-lg-6"><span><strong>REGISTRADO POR: </strong>PENDIENTE</span></div>
                            <div className="col-lg-6"><span><strong>DEPENDENCIA REGISTRO: </strong> {antecedente.data.attributes.dependencia_origen}</span></div>
                        </div>

                        <div className="row">
                            <div className="col-lg-4"><span><strong>VIGENCIA: </strong>{antecedente.data.attributes.vigencia}</span></div>
                            <div className="col-lg-4"><span><strong>FECHA REGISTRO: </strong> {antecedente.data.attributes.fecha_registro}</span></div>
                            <div className="col-lg-4"><span><strong>FECHA DE INGRESO: </strong> {fecha_creado}</span></div>
                        </div>

                    </div>
                </div>
            </>
        )
    }

    return (
<>
<Spinner />
            <div className="row">


                <div className="col-md-12">
                    <div className="block block-rounded block-bordered">
                        <div className="block-content">
                            <nav aria-label="breadcrumb">
                                <ol className="breadcrumb breadcrumb-alt push">

                                    <li className="breadcrumb-item"><Link underline="hover" className="text-dark"
                                                                          to={`/MisPendientes`}>
                                        <small>Mis Pendientes</small>
                                    </Link></li>
                                    <li className="breadcrumb-item"><small>Ramas del proceso</small></li>
                                </ol>
                            </nav>
                        </div>
                    </div>
                </div>

                {  respuestaAntecedente ? componentAntecedente() : null }

                    {/*CAPTURA Y REPARTO*/}
                    <div class="col-lg-4">
                        <div class="block block-rounded block-bordered">
                            <div class="block-header block-header-default">
                                <h3 class="block-title">CAPTURA Y REPARTO</h3>
                                <p>Días desde su registro: 0</p>
                            </div>
                            <div class="block-content">
                            <ul className="nav-items push">

                                {/*ANTECEDENTES*/}
                                <li>
                                    {cambiaColorAntecedentes && (
                                        <div className="media-body">
                                            <Link to={`/AntecedentesLista/${radicado}/${procesoDisciplinarioId}`}>
                                                <button type="button" className="btn btn-sm btn-danger mr-1 mb-3">
                                                    <i className="fas fa-clipboard-list"></i> Antecedentes
                                                </button>
                                            </Link>

                                            <button type="button" class="btn btn-sm btn-danger mr-1 mb-3" data-toggle="modal" 
                                            data-target="#modal-archivos"><i class="fas fa-folder-open"></i></button>

                                        </div>
                                    )}

                                    {!cambiaColorAntecedentes && (
                                        <div className="media-body">
                                            <Link to={`/AntecedentesLista/${radicado}/${procesoDisciplinarioId}`}>
                                                <button type="button" className="btn btn-sm btn-primary mr-1 mb-3">
                                                    <i className="fa fa-fw fa-archive mr-1"></i> Antecedentes
                                                </button>
                                            </Link>

                                            <button type="button" class="btn btn-sm btn-primary mr-1 mb-3" data-toggle="modal" 
                                            data-target="#modal-archivos"><i class="fas fa-folder-open"></i></button>
                                        </div>
                                    )}

                                </li>

                                {/*DATOS DEL INTERESADO*/}
                                <li>
                                {cambiaColorDatosInteresado && (
                                        <div className="media-body">
                                            <Link to={`/DatosInteresadoLista/${radicado}/${procesoDisciplinarioId}`}>
                                                <button  type="button" className="btn btn-sm btn-danger mr-1 mb-3" data-toggle="modal" data-target="#modal-datos-interesado">
                                                    <i className="fa fa-fw fa-archive mr-1"></i> Datos interesado
                                                </button>
                                            </Link>

                                            <button type="button" class="btn btn-sm btn-danger mr-1 mb-3" data-toggle="modal" 
                                            data-target="#modal-archivos"><i class="fas fa-folder-open"></i></button>
                                        </div>
                                    )}

                                    {!cambiaColorDatosInteresado && (
                                        <div className="media-body">
                                            <Link to={`/DatosInteresadoLista/${radicado}/${procesoDisciplinarioId}`}>
                                                <button type="button" className="btn btn-sm btn-primary mr-1 mb-3" data-toggle="modal" data-target="#modal-datos-interesado">
                                                    <i className="fa fa-fw fa-archive mr-1"></i> Datos interesado
                                                </button>
                                            </Link>

                                            <button type="button" class="btn btn-sm btn-primary mr-1 mb-3" data-toggle="modal" 
                                            data-target="#modal-archivos"><i class="fas fa-folder-open"></i></button>
                                        </div>
                                    )}


                                </li>

                                {/*CLASIFICACION DEL RADICADO*/}
                                <li>
                                    {cambiaColorClasificacionRadicado && (
                                        <div className="media-body">
                                            <Link to={`/ClasificacionRadicadoForm/${radicado}/${procesoDisciplinarioId}`}>
                                                <button  type="button" className="btn btn-sm btn-danger mr-1 mb-3">
                                                    <i className="fa fa-fw fa-archive mr-1"></i> Clasificación del radicado
                                                </button>
                                            </Link>
                                            <button  type="button" class="btn btn-sm btn-danger mr-1 mb-3" data-toggle="modal" 
                                            data-target="#modal-archivos"><i class="fas fa-folder-open"></i></button>
                                        </div>
                                        
                                    )}

                                    {!cambiaColorClasificacionRadicado && (
                                        <div className="media-body">
                                            <Link to={`/ClasificacionRadicadoForm/${radicado}/${procesoDisciplinarioId}`}>
                                                <button  type="button" className="btn btn-sm btn-primary mr-1 mb-3">
                                                    <i className="fa fa-fw fa-archive mr-1"></i> Clasificación del radicado
                                                </button>
                                            </Link>
                                            <button  type="button" class="btn btn-sm btn-primary mr-1 mb-3" data-toggle="modal" 
                                            data-target="#modal-archivos"><i class="fas fa-folder-open"></i></button>
                                        </div>
                                    )}
                                </li>

                                {/*ENTIDAD DEL INVESTIGADO*/}
                                <li>
                                    {cambiaColorEntidadInvestigado && (
                                        <div className="media-body">
                                            <Link to={`/EntidadInvestigadoLista/${radicado}/${procesoDisciplinarioId}`}>
                                                <button  type="button" className="btn btn-sm btn-danger mr-1 mb-3">
                                                    <i className="fa fa-fw fa-archive mr-1"></i> Entidad del investigado
                                                </button>
                                            </Link>
                                            <button  type="button" class="btn btn-sm btn-danger mr-1 mb-3" data-toggle="modal" 
                                            data-target="#modal-archivos"><i class="fas fa-folder-open"></i></button>
                                        </div>
                                    )}

                                    {!cambiaColorEntidadInvestigado && (
                                        <div className="media-body">
                                            <Link to={`/EntidadInvestigadoLista/${radicado}/${procesoDisciplinarioId}`}>
                                                <button  type="button" className="btn btn-sm btn-primary mr-1 mb-3">
                                                    <i className="fa fa-fw fa-archive mr-1"></i> Entidad del investigado
                                                </button>
                                            </Link>
                                            <button  type="button" class="btn btn-sm btn-primary mr-1 mb-3" data-toggle="modal" 
                                            data-target="#modal-archivos"><i class="fas fa-folder-open"></i></button>
                                        </div>
                                    )}
                                </li>

                                {/*SOPORTE RADICADO*/}
                                <li>
                                    {cambiaColorSoporteRadicado && (
                                        <div className="media-body">
                                            <Link to={`/SoporteRadicadoForm/${radicado}/${procesoDisciplinarioId}`}>
                                                <button  type="button" className="btn btn-sm btn-danger mr-1 mb-3">
                                                    <i className="fa fa-fw fa-archive mr-1"></i> Soporte Radicado EN CONSTRUCCION
                                                </button>
                                            </Link>
                                            <button  type="button" class="btn btn-sm btn-danger mr-1 mb-3" data-toggle="modal" 
                                            data-target="#modal-archivos"><i class="fas fa-folder-open"></i></button>
                                        </div>
                                    )}

                                    {!cambiaColorSoporteRadicado && (
                                        <div className="media-body">
                                            <Link to={`/SoporteRadicadoForm/${radicado}/${procesoDisciplinarioId}`}>
                                                <button  type="button" className="btn btn-sm btn-primary mr-1 mb-3">
                                                    <i className="fa fa-fw fa-archive mr-1"></i> Soporte Radicado
                                                </button>
                                            </Link>

                                            <button  type="button" class="btn btn-sm btn-primary mr-1 mb-3" data-toggle="modal" 
                                            data-target="#modal-archivos"><i class="fas fa-folder-open"></i></button>
                                        </div>
                                    )}

                                </li>

                                {/*CERRAR ETAPA*/}
                                <li>

                                <div className="form-group">
                                {cambiaColorAntecedentes && cambiaColorDatosInteresado && cambiaColorClasificacionRadicado && cambiaColorEntidadInvestigado && cambiaColorSoporteRadicado && (

                                    <Formik

                                        initialValues={{
                                            inputCierreEtapa: '',
                                            inputEtapa: 1,
                                        }}
                                        enableReinitialize
                                            validate={(valores) => {

                                                let errores = {}
                                                if(!valores.inputCierreEtapa){
                                                    errores.inputCierreEtapa = 'Debe seleccionar la opción cierre de etapa'
                                                }
                                                return errores
                                            }}

                                            onSubmit={(valores, {resetForm}) => {
                                                cerrarEtapa(valores);
                                            }}

                                    >

                                        <Form>
                                            <div className="col-md-12">
                                                <Field type="checkbox" className="form-check-input" id="inputCierreEtapa" name="inputCierreEtapa"/>
                                                <label className="form-check-label" for="example-checkbox-inline1">¿Con la información actual se puede dar cierre a esta etapa?</label>
                                            </div>

                                            <div className="col-md-12">
                                                <div className="block-content block-content-full text-left">
                                                    <button type="submit" className="btn btn-danger"> Cerrar Etapa</button>
                                                </div>
                                            </div>
                                        </Form>
                                    </Formik>
                                )}
                                    
                                </div>

                                </li>

                                </ul>
                            </div>
                        </div>
                    </div>


                    {/*EVALUACIÓN*/}
                    <div className="col-lg-4">
                        <div className="block block-rounded block-bordered">
                            <div className="block-header block-header-default">
                                <h3 className="block-title">EVALUACIÓN</h3>
                            </div>
                            <div className="block-content">
                                
                            </div>
                        </div>
                    </div>



                    {/*EVALUACIÓN EN PD*/}
                    <div className="col-lg-4">
                        <div className="block block-rounded block-bordered">
                            <div className="block-header block-header-default">
                                <h3 className="block-title">EVALUACIÓN EN PD</h3>
                            </div>
                            <div className="block-content">
                                
                            </div>
                        </div>
                    </div>


                     {/*EVALUACIÓN EN PRELIMINAR*/}
                     <div className="col-lg-4">
                        <div className="block block-rounded block-bordered">
                            <div className="block-header block-header-default">
                                <h3 className="block-title">INVESTIGACIÓN PRELIMINAR</h3>
                            </div>
                            <div className="block-content">
                                
                            </div>
                        </div>
                    </div>


                    {/*EVALUACIÓN EN DISCIPLINARIA*/}
                    <div className="col-lg-4">
                        <div className="block block-rounded block-bordered">
                            <div className="block-header block-header-default">
                                <h3 className="block-title">INVESTIGACIÓN DISCIPLINARIA</h3>
                            </div>
                            <div className="block-content">
                                
                            </div>
                        </div>
                    </div>

                    {/*CAUSA / JUZGAMIENTO*/}
                    <div className="col-lg-4">
                        <div className="block block-rounded block-bordered">
                            <div className="block-header block-header-default">
                                <h3 className="block-title">CAUSA / JUZGAMIENTO</h3>
                            </div>
                            <div className="block-content">
                                
                            </div>
                        </div>
                    </div>

                     {/*CAUSA / JUZGAMIENTO*/}
                     <div className="col-lg-4">
                        <div className="block block-rounded block-bordered">
                            <div className="block-header block-header-default">
                                <h3 className="block-title">PROCESO VERBAL</h3>
                            </div>
                            <div className="block-content">
                                
                            </div>
                        </div>
                    </div>

                     {/*SEGUNDA INSTANCIA*/}
                     <div className="col-lg-4">
                        <div className="block block-rounded block-bordered">
                            <div className="block-header block-header-default">
                                <h3 className="block-title">SEGUNDA INSTANCIA</h3>
                            </div>
                            <div className="block-content">
                                
                            </div>
                        </div>
                    </div>
                    

                <div class="modal" id="modal-archivos" tabindex="-1" role="dialog" aria-labelledby="modal-block-normal" aria-hidden="true">
                    <div class="modal-dialog modal-lg" role="document">
                        <div class="modal-content">
                            <div class="block block-themed block-transparent mb-0">
                                <div class="block-header bg-primary-dark">
                                    <h3 class="block-title">Adicionar Documento a (TIPO DE RADICADO): {radicado}</h3>
                                    <div class="block-options">
                                        <button type="button" class="btn-block-option" data-dismiss="modal" aria-label="Close">
                                            <i class="fa fa-fw fa-times"></i>
                                        </button>
                                    </div>
                                </div>
                                <div class="block-content">


                                <Formik>
                   
                                    <Form>
                                        <div className="row">
                                            <div className="col-md-12">
                                                <div className="alert alert-warning alert-dismissable" role="alert">
                                                    <h3 className="alert-heading font-size-h4 my-2">Alerta</h3>
                                                    <p className="mb-0"> ETAPA:   CAPTURA Y REPARTO - FASE:   (PARAMETRIZAR) </p>
                                                    <p className="mb-0"> Para cualquier archivo que requiera adjuntar valide previamente lo siguiente: </p>
                                                    <p className="mb-0">
                                                        NO puede superar QUINCE (15) Mb de peso/tamaño.
                                                        Tipo/Formato permitido: [.doc], [.docx], [.pdf], [.xls], [.xlsx], [.zip], [.rar], [.png], [.jpg], [.mp4], [.avi], [.mpeg], [.mpg], [.mov], [.mp3], [.wav], [.wma].
                                                    </p>
                                                    <p className="mb-0">
                                                        El nombre del archivo debe ser de máximo 40 caracteres.
                                                    </p>
                                                </div>
                                            </div>
                                        </div>
                    
                                        
                                        <div className="row">

                                            <div className="col-md-4">
                                                <Field as="input" className="form-control" type="text" id="numFolios" name="numFolios" placeholder="No. de folios" />
                                            </div>

                                            <div className="col-md-6">
                                                <div class="custom-file">
                                                    <input type="file" class="custom-file-input" data-toggle="custom-file-input" id="example-file-input-custom" name="example-file-input-custom"/>
                                                    <label class="custom-file-label" for="example-file-input-custom">Selecciona el archivo</label>
                                                </div>
                                            </div>

                                            <div className="col-md-2">
                                                <button type="submit" className="btn btn-primary"><i class="fas fa-minus-circle"></i></button>
                                            </div>

                                        </div>


                                        <div className="row" style={{paddingTop: "10px"}}>
                                            
                                            <div className="col-md-4">
                                                <Field as="input" className="form-control" type="text" id="numFolios" name="numFolios" placeholder="No. de folios" />
                                            </div>

                                            <div className="col-md-6">
                                                <div class="custom-file">
                                                    <input type="file" class="custom-file-input" data-toggle="custom-file-input" id="example-file-input-custom" name="example-file-input-custom"/>
                                                    <label class="custom-file-label" for="example-file-input-custom">Selecciona el archivo</label>
                                                </div>
                                            </div>

                                            <div className="col-md-2">
                                                <button type="button" className="btn btn-primary"><i class="fas fa-minus-circle"></i></button>
                                            </div>

                                        </div>


                                        <div className="row" style={{paddingTop: "10px"}}>

                                            <div className="col-md-4">
                                                <Field as="input" className="form-control" type="text" id="numFolios" name="numFolios" placeholder="No. de folios" />
                                            </div>

                                            <div className="col-md-6">
                                                <div class="custom-file">
                                                    <input type="file" class="custom-file-input" data-toggle="custom-file-input" id="example-file-input-custom" name="example-file-input-custom"/>
                                                    <label class="custom-file-label" for="example-file-input-custom">Selecciona el archivo</label>
                                                </div>
                                            </div>

                                            <div className="col-md-2">
                                                <button type="button" className="btn btn-primary"><i class="fas fa-minus-circle"></i></button>
                                            </div>

                                        </div>

                                        <div className="col-md-12">
                                            <div className="block-content block-content-full text-right">
                                                <button type="submit" className="btn btn-primary"><i className="fas fa-save"></i> Registrar</button>
                                            </div>
                                        </div>

                             
                                                        
                                    </Form>
                                
                                </Formik> 


                            </div>
                        </div>
                    </div>
                </div>
            
            
            
            </div>


        </div>    
            
        </>   
    );
}


export default RamasProceso;
