import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import { useParams } from "react-router";
import ClasificacionRadicadoApi from '../../Api/Services/ClasificacionRadicadoApi';

function ClasificacionRadicadoLista() {

    const [respuestaAntecedentes, setRespuestaAntecedentes] = useState(false);
    const [antecedentesLista, setAntecedentesLista] = useState({ data: [], links: [], meta: [] });
    const [errorApi, setErrorApi] = useState('');

    let { radicado, procesoDisciplinarioId } = useParams();


    useEffect(() => {
        async function fetchData() {

            const data = {
                "data": {
                    "type": "antecedentes",
                    "attributes": {
                        "id_proceso_disciplinario": procesoDisciplinarioId,
                        "id_etapa": "1",
                        "id_tipo_expediente": "1"
                    }
                }
            }

            ClasificacionRadicadoApi.getAllClasificacionRadicadoByIdProDisciplinario(data, procesoDisciplinarioId).then(
                datos => {
                    if(!datos.error){
                        setAntecedentesLista(datos)
                        setRespuestaAntecedentes(true)
                    }
                    else{
                        setErrorApi(datos.error.toString())
                        window.showModal()
                    }
                    window.showSpinner(false);
                }
            )
        }
        fetchData();
    }, []);


    return(
        <>
            <div className="block-header block-header-default">
                <h3 className="block-title">Historial clasificaciones del rádicado (RADICADO) - (PENDIENTE NOMBRE): {radicado}</h3>
            </div> 

            <div className="block block-rounded block-bordered">
                        
                <div className="block-content block-content-full">
                    <div className="row">
                        <div className="col-sm-12">
                            <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                <thead>
                                    <tr>
                                        <th className="text-center">No.</th>
                                        <th>Tipo de expediente</th>
                                        <th>Registrado En</th>
                                        <th>Registrado por</th>
                                        <th>Fecha de registro</th>
                                        <th>Estado actual</th>
                                        <th>Termino</th>
                                        <th>Acciones</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>
            </div> 
        </>
    );

  
}
export default ClasificacionRadicadoLista;
