import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState, useRef } from 'react';
import { Link } from "react-router-dom";
import { getUser } from '../../Utils/Common';
import { useLocation } from 'react-router-dom';
import '../../Utils/Constants';
import ModalGen from '../../Utils/Modals/ModalGeneric';
import GenericApi from '../../Api/Services/GenericApi';
import { useParams } from "react-router";
import Spinner from '../../Utils/Spinner';
import Center from '../../Utils/Styles/Center.js'
import moment from 'moment';

function ProcesoDisciplinarioMigracionForm() {

    const location = useLocation()
    const { from } = location.state

    const [getModalState, setModalState] = useState({ title: "", message: "", show: false });
    const [getProcesoDisciplinario, setProcesoDisciplinario] = useState({ data: [], links: [], meta: [] });
    const [radicado, setRadicado] = useState('');
    const [vigencia, setVigencia] = useState('');
    const [etapa, setEtapa] = useState('');
    const [ubicacionExpediente, setUbicacionExpediente] = useState('');
    const [dependenciaOrigen, setDependenciaOrigen] = useState('');
    const [fecha, setFecha] = useState('');

    const [idAntecedente, setIdAntecedente] = useState(1);
    const [idProceso, setIdProceso] = useState(1);
    //let { idAntecedente } = useParams();

    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);
            listaProcesoDisciplinarioMigracion();
        }
        fetchData();
    }, []);


    const listaProcesoDisciplinarioMigracion = () => {
        try {

            //buscamos el parametro
            GenericApi.getGeneric("migracion-proceso-disciplinario/51238/1/1",).then(
                datos => {

                    if (!datos.error) {
                        setProcesoDisciplinario(datos);                       
                    } else {
                        setModalState({ title: "Antecedentes", message: datos.error.toString(), show: true, redirect: '/AntecedentesForm', from: {from}, alert: global.Constants.TIPO_ALERTA.ERROR });
                    }
                    window.showSpinner(false);
                }
            )
        } catch (error) {
            // console.log(error);
        }
    }

                     
    const handleClick = (event, i) => {

        console.log("valor de i "+i);

        if(i===1){
            setRadicado(getProcesoDisciplinario.data[0].attributes.radicado);
        }

        else if(i===2){
            setVigencia(getProcesoDisciplinario.data[0].attributes.vigencia);
        }

        else if(i===3){
            setEtapa(getProcesoDisciplinario.data[0].attributes.etapa);
        }

        else if(i===4){
            console.log("DEPENDENCIA ORIGEN1: ");
            setDependenciaOrigen(getProcesoDisciplinario.data[0].dependencia_origen);
            console.log("DEPENDENCIA ORIGEN2: ");
        }
/*
        else if(i===5){
            setUbicacionExpediente(getProcesoDisciplinario.data[0].ubicacion_expediente);
        }*/

        return true;

    };


    const handleInputChange = (event, i) => {
     
        if(i===1){
            setRadicado(event.currentTarget.value);
        }

        else if(i===2){
            setVigencia(event.currentTarget.value)
        }

        else if(i===3){
            setEtapa(event.currentTarget.value)
        }

        else if(i===4){
            setDependenciaOrigen(event.currentTarget.value)
        }

        else if(i===5){
            setUbicacionExpediente(event.currentTarget.value)
        }

        return true;

    }



    const infoAntecedente = () => {

        if (getProcesoDisciplinario.data != null && typeof (getProcesoDisciplinario.data) != 'undefined') {
            return (

                getProcesoDisciplinario.data.map((item, i) => {
                    return (
                        <>

                        {/* ETAPA ACTUAL */}
                        <div className="col-md-5">
                            <div className="form-group">
                                <h4>Origen</h4>
                                <hr/>
                            </div>
                        </div>    

                        <div className="col-md-2">
                            <div className="form-group">
                                <Center>
                                    <button type="button" className="btn btn-success" onClick={event => handleClick(event, 0)}><i class="fas fa-angle-double-right"></i></button>
                                </Center>
                            </div>
                        </div>  
                            
                        <div className="col-md-5">
                            <h4>Destino</h4>
                            <hr/>
                        </div>

                        {/* RADICADO */}
                        <div className="col-md-5">
                            <div className="form-group">
                                <label htmlFor="radicado_origen">Radicado</label>
                                <Field value={item.attributes.radicado} as="input" type="text" autoComplete="off" className="form-control" 
                                id="radicado_origen" name="radicado_origen"></Field>
                            </div>
                        </div>
                        
                        <div className="col-md-2">
                            <div className="form-group">
                                <Center>
                                    <button type="button" className="btn btn-success" onClick={event => handleClick(event, 1)}><i class="fas fa-angle-double-right"></i></button>
                                </Center>
                            </div>
                        </div>    
                            
                        <div className="col-md-5">
                            <div className="form-group">
                                <label htmlFor="radicado_destino">Radicado <span className="text-danger">*</span></label>
                                <Field value={radicado} as="input" type="text" autoComplete="off" className="form-control" 
                                id="radicado_destino" name="radicado_destino" onChange={event => handleInputChange(event, 1)}></Field>
                            </div>
                        </div>

                        {/* VIGENCIA */}
                        <div className="col-md-5">
                            <div className="form-group">
                                <label htmlFor="vigencia_origen">Vigencia</label>
                                <Field value={item.attributes.vigencia} as="input" type="text" autoComplete="off" className="form-control" 
                                    id="vigencia_origen" name="vigencia_origen"></Field>
                            </div>
                        </div>    

                        <div className="col-md-2">
                            <div className="form-group">
                                <Center>
                                    <button type="button" className="btn btn-success" onClick={event => handleClick(event, 2)}><i class="fas fa-angle-double-right"></i></button>
                                </Center>
                            </div>
                        </div>  
                            
                        <div className="col-md-5">
                            <div className="form-group">
                                <label htmlFor="vigencia_destino">Vigencia <span className="text-danger">*</span></label>
                                <Field value={vigencia} as="input" type="text" autoComplete="off" className="form-control" 
                                    id="vigencia_destino" name="vigencia_destino" onChange={event => handleInputChange(event, 2)}></Field>
                            </div>
                        </div> 

                        {/* ETAPA ACTUAL */}
                        <div className="col-md-5">
                            <div className="form-group">
                                <label htmlFor="etapa_origen">Etapa</label>
                                <Field value={item.attributes.etapa} as="input" type="text" autoComplete="off" className="form-control"
                                     id="etapa_origen" name="etapa_origen"></Field>
                            </div>
                        </div>    

                        <div className="col-md-2">
                            <div className="form-group">
                                <Center>
                                    <button type="button" className="btn btn-success" onClick={event => handleClick(event, 3)}><i class="fas fa-angle-double-right"></i></button>
                                </Center>
                            </div>
                        </div>  
                            
                        <div className="col-md-5">
                            <div className="form-group">
                                <label htmlFor="etapa_destino">Etapa <span className="text-danger">*</span></label>
                                <Field value={etapa} as="input" type="text" autoComplete="off" className="form-control" 
                                id="etapa_destino" name="etapa_destino" onChange={event => handleInputChange(event, 3)}></Field>
                            </div>
                        </div>

                        {/* DEPENDENCIA ORIGEN */}
                        <div className="col-md-5">
                            <div className="form-group">
                                <label htmlFor="dependencia_origen">Dependencia origen</label>
                                <Field value={item.attributes.dependencia_origen} as="input" type="text" autoComplete="off" className="form-control" 
                                id="dependencia_origen" name="dependencia_origen"></Field>
                            </div>
                        </div>    

                        <div className="col-md-2">
                            <div className="form-group">
                                <Center>
                                    <button type="button" className="btn btn-success" onClick={event => handleClick(event, 4)}><i class="fas fa-angle-double-right"></i></button>
                                </Center>
                            </div>
                        </div>  
                            
                        <div className="col-md-5">
                            <div className="form-group">
                                <label htmlFor="depedencia_destino">Dependencia origen <span className="text-danger">*</span></label>
                                <Field value={dependenciaOrigen} as="input" type="text" autoComplete="off" className="form-control" 
                                id="depedencia_destino" name="depedencia_destino" onChange={event => handleInputChange(event, 4)}></Field>
                            </div>
                        </div>

                        {/* DEPENDENCIA DUEA DEL PROCESO */}
                        <div className="col-md-5">
                            <div className="form-group">
                                <label htmlFor="dependencia_duena_origen">Dependencia duea del proceso</label>
                                <Field value={item.attributes.ubicacion_expediente} as="input" type="text" autoComplete="off" className="form-control" id="dependencia_duena_origen" name="dependencia_duena_origen"></Field>
                            </div>
                        </div>    

                        <div className="col-md-2">
                            <div className="form-group">
                                <Center>
                                    <button type="button" className="btn btn-success" onClick={event => handleClick(event, 5)}><i class="fas fa-angle-double-right"></i></button>
                                </Center>
                            </div>
                        </div>  
                            
                        <div className="col-md-5">
                            <div className="form-group">
                                <label htmlFor="dependencia_duena_destino">Dependencia duea del proceso <span className="text-danger">*</span></label>
                                <Field value={ubicacionExpediente} as="input" type="text" autoComplete="off" className="form-control" 
                                id="dependencia_duena_destino" name="dependencia_duena_destino" onChange={event => handleInputChange(event, 5)}></Field>
                            </div>
                        </div>
                          
                        </>
                    )
                })
            )
        }
    }

    const enviarDatos = (datos) => {
        window.showSpinner(true);
        let data;

        /*data = {
            "data": {
                "type": 'antecedente',
                "attributes": {
                    "antecedentes": 3,
                    "descripcion": datos.descripcion,
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "fecha_registro": Date.now(),
                    "id_dependencia": dependencia ? dependencia : "",
                }
            }
        }

        GenericApi.addGeneric("antecedentes",data).then(
            datos => {
                if (!datos.error) {
                    setModalState({ title: "SINPROC No "+radicado+" :: Crear Antecedente", message: 'Registro Exitoso', show: true, redirect: '/AntecedentesLista', from: {from}, alert: global.Constants.TIPO_ALERTA.EXITO});
                }
                else {
                    setModalState({ title: "SINPROC No "+radicado+" :: Crear Antecedente", message: datos.error.toString(), show: true, redirect: '/AntecedentesLista', from: {from}, alert: global.Constants.TIPO_ALERTA.ERROR });
                }
                window.showSpinner(false);

            }
        )*/
    }

    return (
        <>
            {<Spinner />}   
            {<ModalGen data={getModalState} />}
          
            <Formik
                initialValues={{
                    radicado_destino: '',
                    vigencia_destino: '',
                    etapa_destino: '',
                    depedencia_destino: '',
                    dependencia_duena_destino: ''
                }}
                enableReinitialize
                validate={(valores) => {
                    let errores = {}

                    return errores
                }}
                onSubmit={(valores, { resetForm }) => {
                    enviarDatos(valores);
                }}
            >

                {({ errors }) => (
                    <Form>

                        <div className="w2d_block">
                            <nav aria-label="breadcrumb">
                                <ol className="breadcrumb breadcrumb-alt push">
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/Buscador`}><small>Buscador</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/ListarMigracionAntecedentes`}><small>Lista de antecedentes</small></Link></li>
                                    <li class="breadcrumb-item"> <small>Proceso Disciplinario</small></li>
                                </ol>
                            </nav>
                        </div>
                      
                        <div className="block block-themed">
                            <div className="block-header">
                                <h3 className="block-title">SINPROC No  :: <strong>Migrar proceso {idAntecedente}</strong></h3>
                            </div>

                            <div className="block-content">

                                <div className='text-right'>

                                    <Link to={`/AntecedentesLista/`} title='Regresar a lista de Antecedentes' state={{ from: from }}>
                                        <button type="button" className="btn btn-success"><i className="fas fa-backward"></i> </button>
                                    </Link>
                                </div>

                                <div className="row">

                                    {infoAntecedente()}
                                    
                                </div>

                            </div>
                            <div className="block-content block-content-full text-right bg-light">
                                <button type="submit" className="btn btn-rounded btn-primary">Registrar</button>

                                <Link to={`/AntecedentesLista/`} state={{ from: from }}>
                                    <button type="button" className="btn btn-rounded btn-outline-primary">Cancelar</button>
                                </Link>
                            </div>
                        </div>
                    </Form>
                )}
            </Formik>
        </>
    );
}


export default ProcesoDisciplinarioMigracionForm;
