import { createSchema, addTranslations, setLanguageByName, tr } from 'react-hook-form-auto'

export default function CiudadModel(ddlData) {
    const handleChange = (value, { setValue }) => {
        if (global.Constants.CARACTERES_ESPECIALES.formatOnlyLettersWhitAccent.test(value)) {
            setValue('nombre', value)
        }
        else{
            setValue('nombre', value.substring(0, value.length -1))
        }
        
    }

    const model = createSchema('CiudadModel', {
        nombre: {
            type: 'string',
            required: true,
            maxLength: 255,
            onChange: (handleChange)
        },
        id_departamento: {
            type: 'select',
            required: true,
            options: ddlData['departamento'],
        },
        codigo_dane: {
            type: 'number',
            required: true,
            maxLength: 255,
            helperText: 'C贸digo DANE del departamento'
        },
        estado: {
            type: 'select',
            required: true,
            options: [
                { value: '1', label: 'Activo' },
                { value: '0', label: 'Inactivo' }
            ],
        }
    });

    return model;
}


setLanguageByName('es')
addTranslations({
    models: {
        CiudadModel: {
            nombre: 'Nombre',
            codigo_dane: 'C贸digo Dane',
            id_departamento: {
                _field: 'Departamento',
                _default: "Seleccione",
            },
            estado: {
                _field: 'Estado',
                _default: "Seleccione",
            },
        },
    },
})
