import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import { Field, Form, Formik } from 'formik';
import AntecedentesApi from '../Api/Services/AntecedentesApi';
import { useParams } from "react-router";
import ClasificacionRadicadoApi from '../Api/Services/ClasificacionRadicadoApi';
import DatosInteresadoApi from '../Api/Services/DatosInteresadoApi';
import Spinner from '../Utils/Spinner';
import EntidadInvestigadoApi from '../Api/Services/EntidadInvestigadoApi';
import CierreEtapaApi from '../Api/Services/CierreEtapaApi';
import DocumentoSiriusApi from '../Api/Services/DocumentoSiriusApi';
import { Navigate } from "react-router-dom";
import moment from 'moment';
import { useLocation } from 'react-router-dom'
import { ParametroModel } from '../Models/ParametroModel';
import ProcesoDiciplinarioApi from '../Api/Services/ProcesoDiciplinarioApi';
import { hasAccess } from '../../components/Utils/Common';

function RamasProceso() {
    const location = useLocation()
    const { from } = location.state

    const [getRespuestaAntecedente, setRespuestaAntecedente] = useState(false);
    const [getAntecedentesLista, setAntecedentesLista] = useState({ data: [], links: [], meta: [] });

    const [errorApi, setErrorApi] = useState('');

    // Color Fases
    const [getColorAntecedente, setColorAntecedente] = useState("btn btn-sm btn-primary w2d_btn-large mr-1 mb-3 text-left");
    const [getColorDatosInteresado, setColorDatosInteresado] = useState("btn btn-sm btn-primary w2d_btn-large mr-1 mb-3 text-left");
    const [getColorClasificacionRadicado, setColorClasificacionRadicado] = useState("btn btn-sm btn-primary w2d_btn-large mr-1 mb-3 text-left");
    const [getColorEntidadInvestigado, setColorEntidadInvestigado] = useState("btn btn-sm btn-primary w2d_btn-large mr-1 mb-3 text-left");
    const [getColorSoporteRadicado, setColorSoporteRadicado] = useState("btn btn-sm btn-primary w2d_btn-large mr-1 mb-3 text-left");
    const [getColorValidarClasificacion, setColorValidarClasificacion] = useState("btn btn-sm btn-primary w2d_btn-large mr-1 mb-3 text-left");
    const [getColorComunicacionInteresado, setColorComunicacionInteresado] = useState("btn btn-sm btn-primary w2d_btn-large mr-1 mb-3 text-left");


    const [getHabilitadaComunicacionInteresado, setHabilitadaComunicacionInteresado] = useState(false);


    // Color Etapas
    const [getColorCapturaReparto, setColorCapturaReparto] = useState("#328DBE");
    const [getEvaluacionReparto, setEvaluacionReparto] = useState("#328DBE");

    const [getBotonColorSuccess, setBotonColorSuccess] = useState("btn btn-sm btn-success w2d_btn-large mr-1 mb-3 text-left");

    const [getEtapaEvaluacionRepartoCompleta, setEtapaEvaluacionRepartoCompleta] = useState(false);
    const [getEtapaEvaluacionCompleta, setEtapaEvaluacionCompleta] = useState(false);

    const [getRespuestaProcesoDisciplinario, setRespuestaProcesoDisciplinario] = useState(false);
    const [getProcesoDisciplinario, setProcesoDisciplinario] = useState({ attributes: null });

    let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;
    let vigencia = from.vigencia;
    let debeConsultar = from.debeConsultar;
    let dependencia = from.dependencia;

    from.estadoActividad = true;


    const [isRedirect, setIsRedirect] = useState(false);


    /*Redirects*/
    const redirectToRoutes = () => {
        return <Navigate to={`/RamasProceso/`} />;
    }

    useEffect(() => {
        async function fetchData() {
            console.log('debeConsultar = ' + debeConsultar);
            if (debeConsultar) {
                window.showSpinner(true);
                const proceso_disciplinario = {
                    "data": {
                        "type": "antecedentes",
                        "attributes": {
                            "id_proceso_disciplinario": procesoDisciplinarioId,
                        }
                    }
                }

                ProcesoDiciplinarioApi.getProcesoDisciplinarioById(procesoDisciplinarioId).then(
                    datos => {
                        if (!datos.error) {
                            setProcesoDisciplinario(datos.data.attributes);
                            setRespuestaProcesoDisciplinario(true);
                        }
                    }
                );

                ProcesoDiciplinarioApi.getConsultarFasesProcesoDisciplinario(proceso_disciplinario, procesoDisciplinarioId).then(
                    datos => {
                        console.log(datos);
                        if (!datos.error) {

                            consultaAntecedentes();

                            if (datos["data"]["attributes"]["etapa"] === '1') {
                                setColorCapturaReparto("#82b54b");

                                // Etapa: Captura y reparto Fase: Antecendentes
                                if (datos["data"]["attributes"]["antecedentes"] === true) {
                                    setColorAntecedente(getBotonColorSuccess);
                                    setEtapaEvaluacionRepartoCompleta(true);
                                    from.cambiaColorAntecedentes = getBotonColorSuccess;
                                } else {
                                    setEtapaEvaluacionRepartoCompleta(false);
                                }

                                // Etapa: Captura y reparto Fase: Datos del interesado
                                if (datos["data"]["attributes"]["datos_interesado"] === true) {
                                    setColorDatosInteresado(getBotonColorSuccess);
                                    setEtapaEvaluacionRepartoCompleta(true);
                                    from.cambiaColorDatosInteresado = getBotonColorSuccess;
                                } else {
                                    setEtapaEvaluacionRepartoCompleta(false);
                                }

                                // Etapa: Captura y reparto Fase: Clasificación del radicado
                                if (datos["data"]["attributes"]["clasificacion_radicado"] === true) {
                                    setColorClasificacionRadicado(getBotonColorSuccess);
                                    setEtapaEvaluacionRepartoCompleta(true);
                                    from.cambiaColorClasificacionRadicado = getBotonColorSuccess;
                                } else {
                                    setEtapaEvaluacionRepartoCompleta(false);
                                }

                                // Etapa: Captura y reparto Fase: Entidad del investigado

                                if (datos["data"]["attributes"]["entidad_investigado"] === true) {

                                    setColorEntidadInvestigado(getBotonColorSuccess);
                                    setEtapaEvaluacionRepartoCompleta(true);
                                    from.cambiaColorEntidadInvestigado = getBotonColorSuccess;
                                } else {
                                    setEtapaEvaluacionRepartoCompleta(false);
                                }

                                // Etapa: Captura y reparto Fase: Soporte radicado
                                if (datos["data"]["attributes"]["soporte_radicado"] === true) {
                                    setColorSoporteRadicado(getBotonColorSuccess);
                                    setEtapaEvaluacionRepartoCompleta(true);
                                    from.cambiaColorSoporteRadicado = getBotonColorSuccess;
                                } else {
                                    setEtapaEvaluacionRepartoCompleta(false);
                                }

                            }

                            if(datos["data"]["attributes"]["antecedentes"] && datos["data"]["attributes"]["datos_interesado"]  && 
                            datos["data"]["attributes"]["clasificacion_radicado"] && datos["data"]["attributes"]["entidad_investigado"]){


                            }

                            if (datos["data"]["attributes"]["etapa"] == 2) {
                                // Etapa: Evaluación Fase: validar clasificación
                                if (datos["data"]["attributes"]["validar_clasificacion"] === true) {
                                    setColorValidarClasificacion(getBotonColorSuccess);
                                    setEtapaEvaluacionCompleta(true);
                                } else {
                                    setEtapaEvaluacionCompleta(false);
                                }

                                // Etapa: Evaluación Fase: comunicacion interesado
                                if (datos["data"]["attributes"]["muestra_comunicacion_interesado"] === true) {
                                    setHabilitadaComunicacionInteresado(true);
                                    from.habilitaBotonComunicacionInteresado = getBotonColorSuccess;

                                    //si lo muestra revisamos si debe cambiar el color dependiendo si ya hay registros
                                    if (datos["data"]["attributes"]["comunicacion_interesado"] === true) {
                                        setColorComunicacionInteresado(getBotonColorSuccess);
                                        from.cambiaColorComunicacionInteresado = getBotonColorSuccess;
                                        setEtapaEvaluacionCompleta(true);
                                    }
                                    else {
                                        setEtapaEvaluacionCompleta(false);
                                    }

                                }

                            }

                            setRespuestaAntecedente(true);
                            from.debeConsultar = false;
                            from.radicado = radicado;
                            from.registradoPor = "PENDIENTE";
                            window.showSpinner(false);



                        }
                        else {
                            window.showModal()
                        }

                    }

                )
            }
            else {
                setColorAntecedente(from.cambiaColorAntecedentes);
                setColorDatosInteresado(from.cambiaColorDatosInteresado);
                setColorClasificacionRadicado(from.cambiaColorClasificacionRadicado);
                setColorEntidadInvestigado(from.cambiaColorEntidadInvestigado);
                setColorSoporteRadicado(from.cambiaColorSoporteRadicado);
                setHabilitadaComunicacionInteresado(from.habilitaBotonComunicacionInteresado);
                setColorComunicacionInteresado(from.cambiaColorComunicacionInteresado);

                setRespuestaAntecedente(true);

                if (from.cambiaColorAntecedentes.includes("succes")
                    && from.cambiaColorDatosInteresado.includes("succes")
                    && from.cambiaColorClasificacionRadicado.includes("succes")
                    && from.cambiaColorEntidadInvestigado.includes("succes")
                    && from.cambiaColorSoporteRadicado.includes("succes")) {
                    setEtapaEvaluacionRepartoCompleta(true);
                }
                //window.showSpinner(false);
            }






        }
        fetchData();
    }, []);



    const consultaAntecedentes = () => {

        const dataAntecedente = {
            "data": {
                "type": "antecedente",
                "attributes": {
                    "descripcion": "descripcion",
                    "fecha_registro": "fecha_registro",
                    "id_dependencia": "id_dependencia",
                    "estado": "estado",
                    "id_proceso_disciplinario": "id_proceso_disciplinario",
                    "id_etapa": "id_etapa",
                    "dependencia_origen": "dependencia_origen",
                    "vigencia": "vigencia",
                    "fecha_creado": "fecha_creado",
                    "etapa": "etapa"
                }
            }
        }

        AntecedentesApi.getPrimerYUltimoAntecedente(dataAntecedente, procesoDisciplinarioId).then(
            datos => {

                if (!datos.error) {

                    console.log("DATOS: " + JSON.stringify(datos));
                    setAntecedentesLista(datos)
                    setRespuestaAntecedente(true)

                }
                else {
                    setErrorApi(datos.error.toString())
                    window.showModal()
                }
            }
        )

    }

    //Funciones
    const handleClicSoporteRadicado = (id_etapa, id_fase) => {
        from.id_etapa = id_etapa;
        from.id_fase = id_fase;
    };

    const cerrarEtapa = (datos) => {

        let data

        data = {
            "data": {
                "type": "cerrar_etapa",
                "attributes": {
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "id_etapa": datos.inputEtapa,
                    "id_delegada": 1,
                    "id_funcionario": 1
                }
            }
        }

        CierreEtapaApi.addCierreEtapa(data).then(
            datos => {
                if (!datos.error) {
                    window.showModal(2)
                    window.location.reload(false);
                }
                else {
                    setErrorApi(datos.error.toString())
                    window.showModal(1)
                }
            }
        )
    }


    const componentAntecedente = () => {


        var descripcion_antecedente = "";
        var fecha_registro = "";
        var fecha_ingreso = "";
        var dependencia = "";
        var id_dependencia = "";

        if (getAntecedentesLista.length > 0) {
            if (getAntecedentesLista.length > 1) {
                descripcion_antecedente = getAntecedentesLista[1].descripcion;
                fecha_registro = getAntecedentesLista[1].created_at;
                fecha_ingreso = getAntecedentesLista[0].created_at;
                dependencia = getAntecedentesLista[0].nombre_dependencia;
                id_dependencia = getAntecedentesLista[0].id_dependencia;
            }

