            from.dependendencia = dependencia;
            from.fechaRegistro = fecha_registro;
            from.fechaIngreso = fecha_ingreso;
            from.dependencia = id_dependencia;
        }
        else {
            descripcion_antecedente = from.antecedente;
            dependencia = from.dependendencia;
            fecha_registro = from.fechaRegistro;
            fecha_ingreso = from.fechaIngreso;
