                                            {/*ENTIDAD DEL INVESTIGADO*/}
                                            <li>
                                                <div className="media-body">
                                                    <Link to={`/EntidadInvestigadoLista/`} state={{ from: from }}>
                                                        <button type="button" className={getColorEntidadInvestigado}><i className="fas fa-hotel"></i> Entidad del investigado</button>
                                                    </Link>
                                                    <Link to={`/SoporteradicadoForm/`} state={{ from: from }}>
                                                        <button type="button" className="btn btn-sm btn-warning mr-1 mb-3" onClick={() => handleClicSoporteRadicado(1, 4)}><i className="fas fa-folder-open"></i></button>
                                                    </Link>                                                    
                                                </div>

                                            </li>

                                            {/*SOPORTE RADICADO*/}
                                            <li>
                                                <div className="media-body">
                                                    <Link to={`/SoporteRadicadoLista/`} state={{ from: from }}>
                                                        <button type="button" className={getColorSoporteRadicado} onClick={() => handleClicSoporteRadicado(1, 5)}><i className="fas fa-archive"></i> Soporte Radicado</button>
                                                    </Link>
                                                </div>
                                            </li>

                                            {/*CERRAR ETAPA*/}
                                            <li>
                                                {getEtapaEvaluacionRepartoCompleta && (

