                            <h3 className="block-title">Investigación Preliminar</h3>
                        </div>
                        <>
                            {
                                (hasAccess('InvestigacionPreliminar', 'Consultar')) ? (
                                    <div className="block-content">

                                    </div>
                                ) : null
                            }
                        </>
                    </div>
                </div>

                {/*EVALUACIÓN EN DISCIPLINARIA*/}
                <div className="col-lg-4 col-md-6 col-sm-6">
                    <div className="block block-themed">
                        <div className="block-header block-header-default">
                            <h3 className="block-title">Investigación Disciplinaria</h3>
                        </div>
                        <>
                            {
                                (hasAccess('InvestigacionDisciplinaria', 'Consultar')) ? (
                                    <div className="block-content">

                                    </div>
                                ) : null
                            }
                        </>
                    </div>
                </div>

                {/*CAUSA / JUZGAMIENTO*/}
                <div className="col-lg-4 col-md-6 col-sm-6">
                    <div className="block block-themed">
                        <div className="block-header block-header-default">
                            <h3 className="block-title">Causa / Juzgamiento</h3>
                        </div>
                        <>
                            {
                                (hasAccess('CausaJuzgamiento', 'Consultar')) ? (
                                    <div className="block-content">

                                    </div>
                                ) : null
                            }
                        </>
                    </div>
                </div>

                {/*CAUSA / JUZGAMIENTO*/}
                <div className="col-lg-4 col-md-6 col-sm-6">
                    <div className="block block-themed">
                        <div className="block-header block-header-default">
                            <h3 className="block-title">Proceso Verbal</h3>
                        </div>
                        <>
                            {
                                (hasAccess('ProcesoVerbal', 'Consultar')) ? (
                                    <div className="block-content">

                                    </div>
                                ) : null
                            }
                        </>
                    </div>
                </div>

                {/*SEGUNDA INSTANCIA*/}
                <div className="col-lg-4 col-md-6 col-sm-6">
                    <div className="block block-themed">
                        <div className="block-header block-header-default">
                            <h3 className="block-title">Segunda Instancia</h3>
                        </div>
                        <>
                            {
                                (hasAccess('SegundaInstancia', 'Consultar')) ? (
                                    <div className="block-content">

                                    </div>
                                ) : null
                            }
                        </>
                    </div>
                </div>

                <div className="modal" id="modal-archivos" tabIndex="-1" role="dialog" aria-labelledby="modal-block-normal" aria-hidden="true">
                    <div className="modal-dialog modal-lg" role="document">
                        <div className="modal-content">
                            <div className="block block-themed block-transparent mb-0">
                                <div className="block-header bg-primary-dark">
                                    <h3 className="block-title">Adicionar Documento a (TIPO DE RADICADO): {radicado}</h3>
                                    <div className="block-options">
                                        <button type="button" className="btn-block-option" data-dismiss="modal" aria-label="Close">
                                            <i className="fa fa-fw fa-times"></i>
                                        </button>
                                    </div>
                                </div>
                                <div className="block-content">


                                    <Formik>

                                        <Form>
                                            <div className="row">
                                                <div className="col-md-12">
                                                    <div className="alert alert-warning alert-dismissable" role="alert">
                                                        <h3 className="alert-heading font-size-h4 my-2">Alerta</h3>
                                                        <p className="mb-0"> ETAPA:   CAPTURA Y REPARTO - FASE:   (PARAMETRIZAR) </p>
                                                        <p className="mb-0"> Para cualquier archivo que requiera adjuntar valide previamente lo siguiente: </p>
                                                        <p className="mb-0">
                                                            NO puede superar QUINCE (15) Mb de peso/tamaño.
                                                            Tipo/Formato permitido: [.doc], [.docx], [.pdf], [.xls], [.xlsx], [.zip], [.rar], [.png], [.jpg], [.mp4], [.avi], [.mpeg], [.mpg], [.mov], [.mp3], [.wav], [.wma].
                                                        </p>
                                                        <p className="mb-0">
                                                            El nombre del archivo debe ser de máximo 40 caracteres.
                                                        </p>
                                                    </div>
                                                </div>
                                            </div>


                                            <div className="row">

                                                <div className="col-md-4">
                                                    <Field as="input" className="form-control" type="text" id="numFolios" name="numFolios" placeholder="No. de folios" />
                                                </div>

                                                <div className="col-md-6">
                                                    <div className="custom-file">
                                                        <input type="file" className="custom-file-input" data-toggle="custom-file-input" id="example-file-input-custom" name="example-file-input-custom" />
                                                        <label className="custom-file-label" htmlFor="example-file-input-custom">Selecciona el archivo</label>
                                                    </div>
                                                </div>

                                                <div className="col-md-2">
                                                    <button type="submit" className="btn btn-primary"><i className="fas fa-minus-circle"></i></button>
                                                </div>

                                            </div>


