import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import { useParams } from "react-router";
import ClasificacionRadicadoApi from '../../Api/Services/ClasificacionRadicadoApi';

function ClasificacionRadicadoLista() {

    const [respuestaClasificacionRadicado, setClasificacionRadicado] = useState(false);
    const [calsificacionRadciadoLista, setClasificacionRadicadoLista] = useState({ data: [], links: [], meta: [] });
    const [errorApi, setErrorApi] = useState('');

    let { radicado, procesoDisciplinarioId } = useParams();


    useEffect(() => {
        async function fetchData() {

            const data = {
                "data": {
                    "type": "clasificacion_radicado",
                    "attributes": {
                        "id_proceso_disciplinario": procesoDisciplinarioId,
                        "id_etapa": "1",
                        "id_tipo_expediente": "1"
                    }
                }
            }

            ClasificacionRadicadoApi.getAllClasificacionRadicadoByIdProDisciplinario(data, procesoDisciplinarioId).then(
                datos => {
                    if(!datos.error){
                        setAntecedentesLista(datos)
                        setRespuestaAntecedentes(true)
                    }
                    else{
                        setErrorApi(datos.error.toString())
                        window.showModal()
                    }
                    window.showSpinner(false);
                }
            )
        }
        fetchData();
    }, []);


    const listaClasificacionRadicado = () => {

        return(
            antecedentesLista.data.map((antecedente, i) => {
                return (
                    <tr key={antecedente.id}>
                        <td className="text-center">{i+1}</td>
                        <td>{ antecedente.attributes.descripcion }</td>
                        <td>{ antecedente.attributes.dependencia_origen }</td>
                        <td>{ antecedente.attributes.estado ? 'Activo' : 'Inactivo' }</td>
                        <td>PENDIENTE</td>
                        <td>{ antecedente.attributes.etapa }</td>
                        <td>{ antecedente.attributes.fecha_registro}</td>
                        <td>PENDIENTE</td>
                    </tr>
                )
            })
        )
    }


    return(
        <>
            <div className="block-header block-header-default">
                <h3 className="block-title">Historial clasificaciones del rádicado (RADICADO) - (PENDIENTE NOMBRE): {radicado}</h3>
            </div> 

            <div className="block block-rounded block-bordered">
                        
                <div className="block-content block-content-full">
                    <div className="row">
                        <div className="col-sm-12">
                            <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                <thead>
                                    <tr>
                                        <th className="text-center">No.</th>
                                        <th>Tipo de expediente</th>
                                        <th>Registrado En</th>
                                        <th>Registrado por</th>
                                        <th>Fecha de registro</th>
                                        <th>Estado actual</th>
                                        <th>Termino</th>
                                        <th>Acciones</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>
            </div> 
        </>
    );

  
}
export default ClasificacionRadicadoLista;
