import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import AntecedentesApi from '../../Api/Services/AntecedentesApi';

function AntecedentesForm() {

    const enviarDatos = (datos) => {
        const data = {
            "data" : {
                "type": 'antecedente',
                "attributes": {
                    "descripcion": datos.descripcion
                }
            }
        }

        console.log(data);

        AntecedentesApi.addAntecedente(data).then(
            datos => {
                if(!datos.error){
                    console.log(datos);
                    window.showModal(2)
                }
                else{
                    window.showModal(1)
                }
            }
        )
    }


        
        return(
            <>
                <Formik
                    initialValues={{
                        descripcion: ''
                    }}
                    enableReinitialize
                    validate={(valores) => {
                        let errores = {}
                        if(!valores.descripcion){
                            errores.descripcion = 'Debe ingresar un antecedente'
                        }
                        return errores
                    }}
                    onSubmit={(valores, {resetForm}) => {
                        console.log(valores.descripcion);
                        enviarDatos(valores);
                        resetForm();
                    }}
                >
                </Formik>

                <div className="block block-rounded block-bordered">
                    <div className="block-header block-header-default">
                        <h3 className="block-title">SINPROC No 1614971 :: Antecedentes</h3>
                    </div>
                    <div className="block-content">
                        <div className="row justify-content-center py-sm-3 py-md-5">
                            <div className="col-sm-10 col-md-8">
                                <div className="form-group">
                                    <label>Descripci√≥n</label>
                                    <Field as="textarea" class="form-control" id="descripcion" name="descripcion" rows="4" 
                                            placeholder="Escriba en este espacio los antencedentes"></Field>
                                    <ErrorMessage name="descripcion" component={() => (<span className="text-danger">{errors.descripcion}</span>)} />
                                </div>
                            
                            </div>
                        </div>
                    </div>
                    <div className="block-content block-content-full text-right bg-light">
                        <button type="button" className="btn btn-sm btn-light" data-dismiss="modal">Cerrar</button>
                        <button type="submit" className="btn btn-sm btn-primary">Guardar</button>              
                    </div>
                    
                </div>  
        </>                            
    );
}


export default AntecedentesForm;
