 ",getUsuarioSleccionado,/*#__PURE__*/_jsx("span",{className:"text-danger",children:"*"})]}),/*#__PURE__*/_jsxs(Field,{value:getUsuarioSleccionado,onChange:handleInputChange,as:"select",className:"form-control",id:"usuario",name:"usuario",children:[/*#__PURE__*/_jsx("option",{value:"",children:"Por favor seleccione"}),respuestaUsuariosGrupoTrabajo?selectUsuarioGrupoTrabajo():null]}),/*#__PURE__*/_jsx(ErrorMessage,{name:"usuario",component:function component(){return/*#__PURE__*/_jsx("span",{className:"text-danger",children:errors.usuario});}})]})}),/*#__PURE__*/_jsx("div",{className:"col-md-12",children:/*#__PURE__*/_jsxs("div",{className:"form-group",children:[/*#__PURE__*/_jsxs("label",{htmlFor:"informacion",children:["Observaciones para su remisi\xF3n ",/*#__PURE__*/_jsx("span",{className:"text-danger",children:"*"})]}),/*#__PURE__*/_jsx(Field,{as:"textarea",className:"form-control",id:"informacion",name:"informacion",rows:"6",placeholder:"Informaci\xF3n para su remisi\xF3n....",maxLength:getMaximoTextArea,minLength:getMinimoTextArea}),/*#__PURE__*/_jsx("div",{className:"text-right",children:/*#__PURE__*/_jsxs("span",{className:"text-primary",children:[countTextArea," / ",getMaximoTextArea]})}),/*#__PURE__*/_jsx(ErrorMessage,{name:"informacion",component:function component(){return/*#__PURE__*/_jsx("span",{className:"text-danger",children:errors.informacion});}})]})})]}),/*#__PURE__*/_jsxs("div",{className:"block-content block-content-full text-right",children:[/*#__PURE__*/_jsx("button",{type:"submit",className:"btn btn-rounded btn-primary",children:"Remitir"}),/*#__PURE__*/_jsx(Link,{to:'/Transacciones',state:{from:from},className:"font-size-h5 font-w600",children:/*#__PURE__*/_jsx("button",{type:"button",className:"btn btn-rounded btn-outline-primary",children:"Cancelar"})})]})]})]})]});}})]});}export default EnviaraAlguienDeSecretariaComunDirigido;

