import React, { useEffect, useState } from "react";
import Spinner from '../Utils/Spinner';
import { Link } from "react-router-dom";
import { getUser } from '../../components/Utils/Common';
import GenericApi from '../Api/Services/GenericApi';

function Perfil() {

    // Constantes del sistema
    const [getDatosUsuario, setDatosUsuario] = useState([]);
    const [getExistenDatosUsuario, setExistenDatosUsuario] = useState(false);

    useEffect(() => {
        async function fetchData() {

            // Se usa el cargando
            window.showSpinner(true);

            // Se llama el metodo
            obtenerDatosUsuario();
        }
        fetchData();
    }, []);

    // Metodo encargado de obtener los datos del usuario
    const obtenerDatosUsuario = () => {

        // Se consume la API
        GenericApi.getGeneric("usuario/get-usuario-por-name/" + getUser().nombre).then(

            // Se inicializa la variable de respuesta
            datos => {


                // Se valida que no haya error
                if (!datos.error) {

                    // Se valida que haya informacion del usuario
                    if (datos.data[0]) {

                        // Se redeclara en true
                        setExistenDatosUsuario(true);

                        // Se redeclara los datos del usuario
                        setDatosUsuario(datos.data[0]);
                    } else {

                        // Se redeclara en false
                        setExistenDatosUsuario(false);
                    }
                }

                // Se quita el cargando
                window.showSpinner(false);
            }
        )
    }


    return (
        <>
            {<Spinner />}
            <div className="row">
                <div className="col-md-12">
                    <div className="block block-themed">
                        <div className="block-header">
                            <h3 className="block-title">Perfil Usuario</h3>
                        </div>
                        <div className="block-content">
                            <div className="row">
                                <div className="col-md-6 mb-2">
                                    <span className="txt-blue-primary"><strong >Nombre: </strong></span>
                                    {getExistenDatosUsuario == true ?
                                        getDatosUsuario.attributes ? getDatosUsuario.attributes.nombre : null
                                        : null}
                                </div>
                                <div className="col-md-6 mb-2">
                                    <span className="txt-blue-primary"><strong >Apellido: </strong></span>
                                    {getExistenDatosUsuario == true ?
                                        getDatosUsuario.attributes ? getDatosUsuario.attributes.apellido : null
                                        : null}
                                </div>
                                <div className="col-md-6 mb-2">
                                    <span className="txt-blue-primary"><strong >Usuario: </strong></span>
                                    {getExistenDatosUsuario == true ?
                                        getDatosUsuario.attributes ? getDatosUsuario.attributes.name : null
                                        : null}
                                </div>
                                <div className="col-md-6 mb-4">
                                    <span className="txt-blue-primary"><strong >Correo: </strong></span>
                                    {getExistenDatosUsuario == true ?
                                        getDatosUsuario.attributes ? getDatosUsuario.attributes.email : null
                                        : null}
                                </div>

                                <div className="col-md-6 my-2">
                                    <Link to={`/DocumentosFirmadosOPendientesDeFirma/`}>
                                        <button type="button" className="btn btn-rounded btn-primary">
                                            Ver documentos firmados o pendientes de firma
                                        </button>
                                    </Link>

                                </div>

                                <div className="col-md-6 my-2">
                                    <Link to={`/AgregarFirma/`}>
                                        <button type="button" className="btn btn-rounded btn-primary">
                                            {getExistenDatosUsuario == true ?
                                                getDatosUsuario.attributes ? getDatosUsuario.attributes.firma_mecanica ? "Cambiar" : "Agregar" : null
                                                : null
                                            } firma mecánica
                                        </button>
                                    </Link>
                                </div>


                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    )

}

export default Perfil;
