import moment from 'moment';
import GenericApi from '../../Api/Services/GenericApi';
import Spinner from '../../Utils/Spinner';

function ModalCalendar(props) {

    const crearDia = (fecha) => {
        // console.log('crear')
        window.showSpinner(true);
        const data = {

            "data": {
                "type": "mas_dias_no_laborales",
                "attributes": {

                    "fecha": moment(props.getFecha, 'YYYY/MM/DD').format("YYYY-MM-DD"),
                    "estado": 1,

                }
            }
        }
        // console.log(JSON.stringify(data))
        GenericApi.addGeneric('dias-no-laborales', data).then(
            datos => !datos.error ? (window.showSpinner(false), props.parentCallback(fecha)) : (window.showModal(1))
        )
    }



    const modificarEstado = (fecha, estado, id) => {
        // console.log('actualizar')
        window.showSpinner(true);
        const data = {

            "data": {
                "type": "mas_dias_no_laborales",
                "attributes": {

                    "fecha": fecha,
                    "estado": estado,

                }
            }
        }
        // console.log(JSON.stringify(data))
        let myCurrentDate = new Date();
        let myFutureDate = new Date(fecha);
        myFutureDate.setDate(myFutureDate.getDate() + 1);

        GenericApi.updateGeneric('dias-no-laborales', id, data).then(
            datos => !datos.error ? (window.showSpinner(false), props.parentCallback(myFutureDate)) : (window.showModal(1))
        )
    }

    return (
        <>
            {<Spinner />}
            <div className="modal fade" id='modal-editar-fecha' tabindex="-1" role="dialog" aria-labelledby="modal-block-normal" aria-hidden="true">
                <div className="modal-dialog modal-sm" role="document" >
                    <div class="modal-content">
                        <div class="block block-themed block-transparent mb-0">
                            <div class="block-header bg-primary-dark">
                                <h3 class="block-title">Cambio de estado</h3>
                                <div class="block-options">
                                    <button type="button" class="btn-block-option" data-dismiss="modal" aria-label="Close">
                                        <i class="fa fa-fw fa-times"></i>
                                    </button>
                                </div>
                            </div>
                            <div className="block-content">
                                <strong >Fecha seleccionada: </strong>{moment(props.getFecha, 'YYYY/MM/DD').format("DD/MM/YYYY")}

                            </div>
                            <div className="block-content">
                                <div className="row">
                                    <div className="col-md-12">
                                        <div className="form-group text-center">
                                            {
                                                (props.getEstado == '1') ? (
                                                    <button type="button" class="btn btn-rounded btn-outline-primary" data-dismiss="modal" onClick={() => modificarEstado(props.getFecha, 0, props.getId)}>Inactivar</button>

                                                ) : (
                                                    <button type="button" class="btn btn-rounded btn-primary" data-dismiss="modal" onClick={() => crearDia(props.getFecha)}>Activar</button>
                                                )
                                            }



                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}

export default ModalCalendar;
