import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { Link, } from "react-router-dom";
import InfoErrorApi from '../../Utils/InfoErrorApi';
import InfoExitoApi from '../../Utils/InfoExitoApi';
import GenericApi from '../../Api/Services/GenericApi';
import Spinner from '../../Utils/Spinner';
import { Navigate } from "react-router-dom";
import DataTable from 'react-data-table-component';
import ListaBotones from '../../Utils/ListaBotones';
import '../../Utils/Constants';
import { useLocation } from 'react-router-dom';
import { quitarAcentos, hasAccess, getUser } from '../../Utils/Common';

function ActuacionesLista() {

    const [errorApi, setErrorApi] = useState('');
    const [isRedirect, setIsRedirect] = useState(false);
    const [getActuacionesListaSearch, setActuacionesListaSearch] = useState({ data: [], links: [], meta: [] });
    const [getUsuarioComisionado, setUsuarioComisionado] = useState({ data: [], links: [], meta: [] });
    
    const [getSeach, setSeach] = useState('');
    const paganationPerPages = global.Constants.DATA_TABLE.PAGINATION_PER_PAGE;
    const [perPage, setPerPage] = useState(global.Constants.DATA_TABLE.PAGINATION_PER_PAGE);
    const [pageActual, setPageActual] = useState(1);
    const [getEstadoLista, setEstadoLista] = useState('');
    const [getNombreEtapaSeleccionada, setNombreEtapaSeleccionada] = useState('');
    const [muestrameBotones, setmuestrameBotones] = useState('');
    const [getLaDependenciaDelUsuarioTieneUnComisorioAprobado, setLaDependenciaDelUsuarioTieneUnComisorioAprobado] = useState(false);
    const [getNombreProceso, setNombreProceso] = useState('');
    const Documento = [];

    const [getMostrarBotonAgregar, setMostrarBotonAgregar] = useState(true);
    const [getRutaParametrizada, setRutaParametrizada] = useState('Actuación');


    const [getPermisoActuaciones, setPermisoActuaciones] = useState(false);
    

    const location = useLocation()
    const { from, selected_id_etapa, disable } = location.state

    let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;
    //let idEtapa = from.idEtapa >= 3 ? from.idEtapa : 3;
    let estado = 1;

    const [getRoutes, setRoutes] = useState({
        crear_registro: "/ActuacionesForm/",
        consultar_registros: "/ActuacionesLista/",
        ver_detalle: "/ActuacionesVer/" + procesoDisciplinarioId + "/" + selected_id_etapa + "/" + estado,
        muestra_atras: true,
        id_etapa: from.idEtapa,
        selected_id_etapa: selected_id_etapa,
        muestra_inactivos: true,
        modulo: "Actuaciones",
        funcionalidad_crear: "Crear",
        funcionalidad_consultar: "Consultar",
    });

    const handleCallback = (childData) => {

        try {
            // console.log(childData);
            setEstadoLista(childData == global.Constants.ESTADOS.INACTIVO ? "Inactivos" : "Activos");
            cargarActuaciones(1, paganationPerPages, childData);
        } catch (error) {

        }
    }

    const columns = [
        {

            name: 'ACTUACIÓN',
            cell: actuaciones => actuaciones.attributes.actuacion.attributes.nombre_actuacion,
            selector: actuaciones => actuaciones.attributes.nombre_actuacion,
            sortable: true,
            width: "20%"
        },
        {

            name: 'INFORMACIÓN DE REGISTRO',
            cell: actuaciones =>
                <div className="mt-2 mb-2 d-none d-sm-table-cell">
                    <div className='row'>
                        <div className="col-10">
                            <small><strong>USUARIO:</strong> {actuaciones.attributes.actuacion.attributes.usuario.nombre + " " + actuaciones.attributes.actuacion.attributes.usuario.apellido + " (" + actuaciones.attributes.actuacion.attributes.id_dependencia.nombre + ")"}</small><br />
                            <small><strong>FECHA DE REGISTRO:</strong> {actuaciones.attributes.actuacion.attributes.created_at}</small><br />
                        </div>
                    </div>
                </div>,
            selector: actuaciones => actuaciones.attributes.actuacion.attributes.created_user,
            sortable: true,
            width: "23%"
        },
        {
            name: 'INFORMACIÓN APROBACIÓN/RECHAZO',
            cell: actuaciones =>
                actuaciones.attributes.UsuarioAprobacion != null ? (
                    < div className="mt-2 mb-2 d-none d-sm-table-cell" >
                        <div className='row'>
                            <div className="col-10">
                                <small><strong>USUARIO:</strong> {actuaciones.attributes.UsuarioAprobacion.attributes.usuario.nombre + " " + actuaciones.attributes.UsuarioAprobacion.attributes.usuario.apellido}</small><br />
                                <small><strong>FECHA DE REGISTRO:</strong> {actuaciones.attributes.UsuarioAprobacion.attributes.created_at}</small><br />
                                <small><strong>N° DE AUTO:</strong> {actuaciones.attributes.actuacion.attributes.auto ? actuaciones.attributes.actuacion.attributes.auto : ""}</small><br />
                            </div>
                        </div>
                    </div >
                ) : null,
            selector: actuaciones => actuaciones.attributes.actuacion.attributes.usuario_accion,
            sortable: true,
            width: "24%"
        },
        {
            name: 'ESTADO',
            cell: actuaciones => actuaciones.attributes.actuacion.attributes.nombre_estado_actuacion,
            selector: actuaciones => actuaciones.attributes.actuacion.attributes.nombre_estado_actuacion,
            sortable: true,
            width: "13%"
        },
        {
            name: 'N° AUTO',
            cell: actuaciones =>
                actuaciones.attributes.ArchivoFinalPdf != null ? (
                    <div className='row'>
                        <div className='col-4'>
                            <div className='row'>
                                <div className='col-4'>
                                    <button type='button' title={actuaciones.attributes.actuacion.attributes.auto} onClick={() => handleClicArchivo(actuaciones.attributes.ArchivoFinalPdf.id, actuaciones.attributes.ArchivoFinalPdf.attributes.extension, actuaciones.attributes.ArchivoFinalPdf.attributes.nombre_archivo)} className='btn btn-sm btn-primary' data-toggle="modal"><i className="fas fa-download"></i></button>
                                </div>
                            </div >
                        </div>
                    </div>
                ) : null
            ,
            sortable: true,
            width: "10%"
        },
        {
            name: 'DETALLE',
            cell: actuaciones =>
                <div className='row'>
                    <div className='col-4'>
                        <Link to={getRoutes.ver_detalle} state={{ from: from, selected_id_etapa: selected_id_etapa, id: actuaciones.attributes.actuacion.id, nombre: actuaciones.attributes.actuacion.attributes.nombre_actuacion, estadoActualActuacion: actuaciones.attributes.actuacion.attributes.nombre_estado_actuacion, tipoActuacion: actuaciones.attributes.actuacion.attributes.tipo_actuacion, disable: disable }} >
                            <button type='button' title='Ver actuación' className='btn btn-sm btn-primary' data-toggle="modal" ><i className="fas fa-search"></i></button>
                        </Link>
                    </div>
                </div >,
            width: "10%"
        }
    ];


    const handleClicArchivo = (id_documento, extension, nombre_documento) => {

        try {

            // Se genera la peticion para descargar el archivo
            GenericApi.getGeneric("archivo-actuaciones/get-documento/" + id_documento + "/" + extension).then(
                datos => {
                    if (!datos.error) {
                        downloadBase64File(datos.content_type, datos.base_64, nombre_documento);
                    } else {
                        setErrorApi(datos.error.toString())
                        window.showModal(1)
                    }
                }
            )
        } catch (error) {
            console.error(error);
        }
    };

    function downloadBase64File(contentType, base64Data, fileName) {
        const linkSource = `data:${contentType};base64,${base64Data}`;
        const downloadLink = document.createElement("a");
        downloadLink.href = linkSource;
        downloadLink.download = fileName;
        downloadLink.click();
    }

    const redirectToRoutes = () => {
        return <Navigate to={`/ActuacionesLista/`} state={{ from: from, selected_id_etapa: selected_id_etapa }} />;
    }

    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);
            setEstadoLista("Activos");
            nombreProceso();
            cargarUsuarioComisionado();
        }
        fetchData();
    }, []);


    const nombreProceso = () => {

        GenericApi.getByIdGeneric("nombre-proceso", from.procesoDisciplinarioId).then(
            datos => {
                if (!datos.error) {
                    setNombreProceso(datos.data.attributes.nombre);
                    validarPermisosCrearActuaciones();
                    cargarActuaciones(1, paganationPerPages, global.Constants.ESTADOS.ACTIVO);
                    obtenerDatosDeLaEtapaSeleccionada();
                }
            }
        )
    }

    const cargarUsuarioComisionado = (page, perPage, estado) => {
        GenericApi.getGeneric('actuaciones/get-usuario-comisionado/' + procesoDisciplinarioId).then(
            datos => {
                if (!datos.error) {
                    console.log("USuario COmisionado");
                    console.log(datos);
                    setUsuarioComisionado(datos);
                } else {
                }
            }
        )
    }

    

    const cargarActuaciones = (page, perPage, estado) => {
        GenericApi.getGeneric('actuaciones/get-actuaciones-discipl-etapa-documento-final/' + procesoDisciplinarioId + '/' + selected_id_etapa + '/' + estado).then(
            datos => {
                if (!datos.error) {
                    setActuacionesListaSearch(datos);

                    if (datos["data"] != "") {
                        obtenerLaUltimaActuacion(datos.data[0].attributes.actuacion.attributes.id_estado_actuacion, datos.data[0].attributes.actuacion.attributes.id_actuacion);

                    } else {
                        validacionDeBotonAgregar(0, 0, 0, getLaDependenciaDelUsuarioTieneUnComisorioAprobado);
                    }
                } else {
                    setErrorApi(datos.error.toString())
                    window.showModal(1);
                }
            }
        )
    }

    const obtenerLaUltimaActuacion = (estado, id) => {
        GenericApi.getByIdGeneric('mas_actuaciones', id).then(
            datos => {
                if (!datos.error) {
                    if (datos["data"] != "") {
                        // console.log(datos);
                        validacionDeBotonAgregar(datos.data.attributes.tipo_actuacion, estado, datos.data.attributes.excluyente, getLaDependenciaDelUsuarioTieneUnComisorioAprobado);
                    }
                } else {
                    window.showModal(1);
                }
            }
        )
    }

    const obtenerDatosDeLaEtapaSeleccionada = () => {
        GenericApi.getGeneric('mas-etapa/' + selected_id_etapa).then(
            datos => {
                if (!datos.error) {
                    setNombreEtapaSeleccionada(datos.data.attributes.nombre);
                } else {
                    // console.log("Error en la consulta de obtener los datos de la etapa seleccionada")
                }

            }
        )
    }

    const handlePageChange = page => {
        setPageActual(page);
        cargarActuaciones(page, perPage);
    }

    const handlePerRowsChange = async (newPerPage, page) => {
        setPerPage(newPerPage);
        setPageActual(page);
        cargarActuaciones(page, newPerPage);

    }

    const validarPermisosCrearActuaciones = () => {
        
        GenericApi.getGeneric('validar-permiso-crear-actuaciones').then(

            datos => {
                if (!datos.error) {
                    setPermisoActuaciones(datos.data.attributes.crear_actuacion);
                    console.log("CREAR ACTUACION: "+datos.data.attributes.crear_actuacion)
               
                }
                //window.showSpinner(false);
            }
        )
    }

    const validacionDeBotonAgregar = (tipo_actuacion, estado, excluyente) => {
        GenericApi.getGeneric('actuaciones/dependencia-tiene-comisorio-aprobado/' + getUser().id_dependencia +'/'+ from.procesoDisciplinarioId).then(
            datos => {
                console.log(datos);
                console.log(tipo_actuacion);
                if (tipo_actuacion == 0) {
                    if (from.idEtapa == selected_id_etapa && (excluyente != 1 || estado != 3)) {
                        setMostrarBotonAgregar(true);
                    } else {
                        setMostrarBotonAgregar(false);
                    }
                } else if (tipo_actuacion == 1) {

                    if (datos == "Si tiene" || (hasAccess('Actuaciones', 'SeleccionarDependenciaDuenaDelProceso'))) {
                        setMostrarBotonAgregar(true);
                    } else {
                        setMostrarBotonAgregar(false);
                    }

                    // Se valida cuando el tipo es comisorio
                } else if (tipo_actuacion == 2) {
                    console.log("entrando al dos");
                    console.log(from.idEtapa+" / "+selected_id_etapa+" / "+excluyente+" / "+estado);
                    // Se valida que la etapa actual sea igual a la etapa seleccionada y que no sea excluyente y que sea diferente de pendiente de aprobacion
                    if (from.idEtapa == selected_id_etapa && excluyente != 1 && estado != 3) {
                        console.log("entrando al si del if");
                        // Se habilita el boton de agregado
                        setMostrarBotonAgregar(true);

                        // Se valida que el estado rechazado
                        if (estado == 2) {
                            console.log("entrando al si del if del if");
                            // Se setea la ruta en actuacion 
                            setRutaParametrizada("Comisorio");
                        } else {
                            console.log("entrando al NO del if del if");
                            // Se setea la ruta en comisorio
                            setRutaParametrizada("Actuación");
                        }
                    } else {
                        console.log("entrando al NO del if");
                        // Se deshabilita el boton de agregado
                        setMostrarBotonAgregar(false);
                    }

                } 
               
                else {

                    // Se habilita el boton de agregado
                    setMostrarBotonAgregar(true);
                }

                console.log("VALOR DE PERMISO: "+getPermisoActuaciones);
                if(getPermisoActuaciones === true || getPermisoActuaciones === false){
                    setMostrarBotonAgregar(true);
                } 
               

                // Se setean los botones generales
                setmuestrameBotones(true);

                // Se deshabilita el cargando
                window.showSpinner(false);
            }
        )
    }

    


    return (
        <>
            {isRedirect ? redirectToRoutes() : null}
            {<Spinner />}
            {<InfoErrorApi error={errorApi} />}
            {<InfoExitoApi />}
            <Formik>
                <Form>
                    <div className="w2d_block">
                        <nav aria-label="breadcrumb">
                            <ol className="breadcrumb breadcrumb-alt push">
                                <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`} state={{ from: from, disable: disable }}><small>Ramas del proceso</small></Link></li>
                                <li class="breadcrumb-item"> <small>Actuaciones</small></li>
                            </ol>
                        </nav>
                    </div>
                    <div className="block block-themed">
                        <div className={(disable ? ("block-header bg-dark") : "block-header")}>
                            <h3 className="block-title">{getNombreProceso.toUpperCase()} :: <strong>CONSULTAR LISTA DE ACTUACIONES {getEstadoLista.toUpperCase()} DE {getNombreEtapaSeleccionada.toUpperCase()}</strong></h3>
                        </div>
                        <div className="block-content">
                            <div className='row'>
                                <div className='col-md-3'>
                                    <div className="form-group ">
                                        <Field type="text" id="search" name="search" value={getSeach} onChange={e => setSeach(e.target.value)} onKeyPress={(e) => { e.key === 'Enter' && e.preventDefault(); }} className="form-control border border-success" placeholder="Buscar" />
                                    </div>
                                </div>
                                <div className='col-md-3 mb-2'>
                                    <Link to="/Transacciones" state={{ from: from, selected_id_etapa: selected_id_etapa }}>
                                        <button type="button" className="btn btn-rounded btn-primary w-100" ><i class="fa fa-arrow-right"></i> TRANSACCIONES</button>
                                    </Link>
                                </div>
                                {console.log(getUsuarioComisionado?.data?.attributes?.email +" / "+getUser().email)}
                                {console.log(getMostrarBotonAgregar)}
                                {/*muestrameBotones != '' && getUsuarioComisionado != null? 
                                    <ListaBotones getRoutes={getRoutes} from={from} disable={disable} mostrarBotonAgregar={muestrameBotones && getMostrarBotonAgregar && (getUsuarioComisionado?.data?.attributes?.email == getUser().email)? true : false} rutaParametrizada={getRutaParametrizada} parentCallback={handleCallback} id="botonesNavegacion" name="botonesNavegacion" />
                                : null*/}

                                <ListaBotones getRoutes={getRoutes} from={from} disable={disable} mostrarBotonAgregar={true} parentCallback={handleCallback} id="botonesNavegacion" name="botonesNavegacion" />
                             
                                
                            </div>
                            <div className="row">
                                <div className="col-sm-12">
                                    <DataTable className="table table-bordered table-striped table-vcenter js-dataTable-full"
                                        columns={columns}
                                        data={getActuacionesListaSearch.data.filter((suggestion) => {
                                            if (getSeach === "") {
                                                return suggestion;
                                            } else if (
                                                ((quitarAcentos(suggestion.attributes.nombre_actuacion)
                                                    + quitarAcentos(suggestion.attributes.created_user) + quitarAcentos(suggestion.attributes.usuario_accion) +
                                                    quitarAcentos(suggestion.attributes.nombre_estado_actuacion) + quitarAcentos(suggestion.attributes.auto)
                                                ).toLowerCase().includes(getSeach.toLowerCase()))
                                            ) {
                                                return suggestion;
                                            }
                                        })}
                                        perPage={perPage}
                                        page={pageActual}
                                        pagination
                                        noDataComponent={global.Constants.DATA_TABLE.SIN_DATOS_TABLE}
                                        paginationTotalRows={getActuacionesListaSearch.length}
                                        onChangePage={handlePageChange}
                                        onChangeRowsPerPage={handlePerRowsChange}
                                        defaultSortFieldId="Nombre"
                                        striped
                                        paginationComponentOptions={{ rowsPerPageText: global.Constants.DATA_TABLE.FILTRO_POR_PAGINA }}
                                        defaultSortAsc={false}
                                    />
                                </div>
                            </div>
                        </div>
                    </div>
                </Form>
            </Formik>
        </>
    );

}

export default ActuacionesLista;
