import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import { getUser } from '../../Utils/Common';
import { useLocation } from 'react-router-dom';
import '../../Utils/Constants';
import ModalGen from '../../Utils/Modals/ModalGeneric';
import GenericApi from '../../Api/Services/GenericApi';

function ProcesoDisciplinarioMigracion() {


    const [getModalState, setModalState] = useState({ title: "", message: "", show: false });
    const [getListaFases, setListaFases] = useState({ data: [], links: [], meta: [] });
    const [getRtaListaFases, setRtaListaFases] = useState(false);

    const location = useLocation()
    const { from } = location.state
    let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;
    let dependencia = from.dependencia;

    useEffect(() => {
        async function fetchData() {
            listaFases();
           
        }
        fetchData();
    }, []);


    const listaFases = () => {
        try {

            //buscamos el parametro
            GenericApi.getGeneric("get-fases-orden",).then(
                datos => {

                    if (!datos.error) {
                        setListaFases(datos);
                        //setRtaListaFases(true);
                    } else {
                        setModalState({ title: "Antecedentes", message: datos.error.toString(), show: true, redirect: '/AntecedentesForm', from: {from}, alert: global.Constants.TIPO_ALERTA.ERROR });
                    }
                }
            )
        } catch (error) {
            // console.log(error);
        }
    }

    
    const tablaFases = () => {

        if (getListaFases.data != null && typeof (getListaFases.data) != 'undefined') {
            return (

                clasificacionRadicadoLista.data.map((clasificacion_radicado, i) => {
                    return (
                        cargarTabla(clasificacion_radicado, i)
                    )
                })
            )
        }
        else {
            return (

                clasificacionRadicadoLista.map((clasificacion_radicado, i) => {
                    return (
                        cargarTabla(clasificacion_radicado, i)
                    )
                })
            )
        }

    }

    return (
        <>
            {<ModalGen data={getModalState} />}
          
            <Formik
                initialValues={{
                    descripcion: ''
                }}
                enableReinitialize
                validate={(valores) => {
                    let errores = {}
                    return errores
                }}
                onSubmit={(valores, { resetForm }) => {
                    enviarDatos(valores);
                }}
            >

                {({ errors }) => (
                    <Form>

                        <div className="w2d_block">
                            <nav aria-label="breadcrumb">
                                <ol className="breadcrumb breadcrumb-alt push">
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`} state={{ from: from }}><small>Ramas del proceso</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/AntecedentesLista/`} state={{ from: from }}><small>Antecedentes</small></Link></li>
                                    <li class="breadcrumb-item"> <small>Agregar Antecedente</small></li>
                                </ol>
                            </nav>
                        </div>

                        <div className="block block-themed">
                            <div className="block-header">
                                <h3 className="block-title">Proceso de migración</h3>
                            </div>

                            <div className="block-content">

                                <div className="col-lg-12 push">
                                    <div className="alert alert-info" role="alert">
                                        <span className="txt-blue-primary"><strong>Radicado: </strong> <strong>Vigencia: </strong> <strong>Año: </strong></span><br /><br />
                                        <span className="txt-blue-primary"><strong>Tipo de proceso: </strong></span><br />
                                        <span className="txt-blue-primary"><strong>Etapa: </strong></span><br />
                                        <span className="txt-blue-primary"><strong>Dependencia: </strong></span><br />
                                        <span className="txt-blue-primary"><strong>Antecedente: </strong></span><br />
                                        
                                        <div className=''>
                                            <span className='txt-black-primary' title={(from.antecedente) ? from.antecedente : ""}>{from.antecedente ? from.antecedente.substring(0, 300) : ""}...</span>
                                        </div>

                                        <br />
                                    </div>
                                </div>


                                
                                <div className="col-md-12">

                                    <table class="table">
                                        <thead>
                                            <tr>
                                                <th scope="col">#</th>
                                                <th scope="col">Fase</th>
                                                <th scope="col">Acciones</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            {setListaFases!=null?
                                                <tr></tr>:null
                                            }
                                                                  
                                        </tbody>
                                    </table>
                                    
                                </div>
                            </div>
                            
                        </div>

                    </Form>
                )}
            </Formik>
        </>
    );
}


export default ProcesoDisciplinarioMigracion;
