                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`} state={{ from: from, disable: disable }}><small>Ramas del proceso</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/ActuacionesLista/`} state={{ from: from, selected_id_etapa: selected_id_etapa, disable: disable }}><small>Actuaciones</small></Link></li>
                                    <li class="breadcrumb-item"> <small>Ver detalle {tipoActuacion == "0" ? "Actuación" : (tipoActuacion == "1" ? 'Impedimento' : 'Comisorio')}</small></li>
                                </ol>
                            </nav>
                        </div>

                        <div className="block block-themed">
                            <div className={(disable ? ("block-header bg-dark") : "block-header")}>
                                <h3 className="block-title">SINPROC No {radicado} :: <strong>{tipoActuacion == "0" ? "Actuación" : (tipoActuacion == "1" ? 'Impedimento' : 'Comisorio')} detalle</strong></h3>
                            </div>
                            <div className="block-content">


                                <div className='row'>
                                    <div className='col-md-5 mb-2'>
                                        {disable != true && getActuacionEstaAprobadaSioNo && getMuestraBotonFechaParaSemaforo ? (
                                            <Link to={"/SeleccionDeFechaParaSemaforo/"} state={{ from: from, selected_id_etapa: selected_id_etapa, id: id, nombre: nombre, estadoActualActuacion: estadoActualActuacion, tipoActuacion: tipoActuacion }} className="font-size-h5 font-w600" >
                                                <button type="button" className="btn btn-rounded btn-primary" >Selección de fecha para semáforo</button>
                                            </Link>
                                        ) : null}
                                        
                                        {disable != true && tipoActuacion == "1" && (hasAccess('Actuaciones', 'SeleccionarDependenciaDuenaDelProceso')) ? (
                                            <Link to={"/SeleccionarDuenoProceso/"} state={{ from: from, selected_id_etapa: selected_id_etapa, id: id, nombre: nombre, estadoActualActuacion: estadoActualActuacion, tipoActuacion: tipoActuacion }} className="font-size-h5 font-w600" >
                                                <button type="button" className="btn btn-rounded btn-primary" >Asignar la nueva dependencia dueña del proceso</button>
                                            </Link>
                                        ) : null}
                                    </div>


                                    <div className='col-md-3 mb-2'>
                                        {disable != true ? (
                                            <Link to="/Transacciones" state={{ from: from, selected_id_etapa: selected_id_etapa, id_actuacion: getidMasActuacion }}>
                                                <button type="button" className="btn btn-rounded btn-primary" > Transacciones</button>
                                            </Link>
                                        ) : null}
                                    </div>

                                    <div className='col-md-3 mb-2'>
                                        {tipoActuacion != 2 &&
                                            disable == true &&
                                            (getUltimoEstado == "Solicitud anulación" || getUltimoEstado == "Solicitud anulación aceptada") ?
                                            null
                                            : (
                                                <Link to={`/ActuacionesSolicitudDeAnulacion/`} state={{ from: from, selected_id_etapa: selected_id_etapa, id: id, nombre: nombre, estadoActualActuacion: estadoActualActuacion }}>
                                                    <button type="button" className="btn btn-rounded btn-primary"> Solicitar Inactivación</button>
                                                </Link>
                                            )}
                                    </div>
                                    <div className='col-md-1 mb-2'>
                                        <Link to={`/ActuacionesLista/`} title='Regresar a lista de Actuaciones' state={{ from: from, selected_id_etapa: selected_id_etapa, disable: disable }}>
                                            <button type="button" className="btn btn-rounded btn-primary"><i class="fas fa-backward"></i></button>
                                        </Link>

                                    </div>
                                </div>

                                <div className='col-md-12'>
                                    <div className='mb-3'>
                                        <b style={{ fontSize: '18px' }}>{tipoActuacion == "0" ? "Actuación" : (tipoActuacion == "1" ? 'Impedimento' : 'Comisorio')}: </b>{nombre}
                                    </div>
                                    <div>
                                        <button type='button' title='Descargar documento' className='btn btn-rounded btn-primary' onClick={() => handleClicArchivoManual()}>
                                            <i className='fas fa-download mr-2'></i>Descargar Manual
                                        </button>
                                    </div>
                                </div>

                                {disable != true ? (
                                    (estadoActualActuacion != "Aprobada" ||
                                        (getArchivosListaSearch.data.length == 2 || getArchivosListaSearch.data.length > 2)) ? null :
                                        <div className='col-md-12'>
                                            <table className="table table-striped table-vcenter js-dataTable-full">
                                                <thead>
                                                    <tr>
                                                        {inputListArchivos.map((x, i) => {
                                                            return (
                                                                <div className="col-md-12" key={i}>
                                                                    <div className="form-group" style={{
                                                                        display: 'flex',
                                                                        justifyContent: 'flex-end'
                                                                    }}>
                                                                        <div className='row'>
                                                                            <div className='col-md-12'>
                                                                                <div className='col-md-12' style={{
                                                                                    textAlign: 'center',
                                                                                    marginLeft: '14%'
                                                                                }}>
                                                                                    <label>Cargar archivo definitivo<span className="text-danger">* </span></label>
                                                                                </div>
                                                                            </div>
                                                                            <div className='col-md-12' style={{
                                                                                display: 'flex',
                                                                                justifyContent: 'flex-end'
                                                                            }}>
                                                                                <div className='col-md-6'>
                                                                                    <label className="custom-file-label" htmlFor="example-file-input-custom" name="input-file" data-toggle="custom-file-input">{x.archivo.name}</label>
                                                                                    <input className="custom-file-input" data-toggle="custom-file-input" type="file" name="archivo" onChange={e => handleInputChangeArchivos(e, i, 0, 0, 0, "Documento definitivo", 2)} required />
                                                                                    <label >Peso del archivo: {formatBytes(x.archivo.size)}</label>
                                                                                </div>
                                                                            </div>
                                                                        </div>
                                                                    </div>
                                                                </div>
                                                            );
                                                        })}
                                                    </tr>
                                                </thead>
                                            </table>
                                        </div>
                                ) : null
                                }

                                <div className='mb-3 mt-4'>
                                    <b style={{ fontSize: '18px' }}>Archivos {tipoActuacion == "0" ? "Actuación:" : (tipoActuacion == "1" ? 'Impedimento:' : 'Comisorio:')}</b>
                                </div>
                                <table className="table table-bordered table-striped table-vcenter js-dataTable-full table-responsive-md">
                                    <thead>
                                        <tr>
                                            <th>Tipo </th>
                                            <th>Documento</th>
                                            <th>Descarga </th>
                                            <th>Actualizar</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {getRtaInfoArchivoActuaciones ? infoArchivos() : null}
                                    </tbody>
                                </table>

                                <strong>
                                    <span className='txt-blue-primary'>Estado de aprobación: </span>
                                    {getActuacionEstaAprobadaSioNo && !getActuacionPendienteDeFirmas ? (" Aprobado y con firmas") : null}
                                    {!getActuacionEstaAprobadaSioNo && getActuacionPendienteDeFirmas ? (" Pendiente de aprobación y pendiente de firmas") : null}
                                    {!getActuacionEstaAprobadaSioNo && !getActuacionPendienteDeFirmas ? (" Pendiente de aprobación, con firmas") : null}

                                </strong>

                                {
                                    (hasAccess('Actuaciones', 'SolicitudDeAprobación')) &&
                                        getActuacionEstaAprobadaSioNo == false &&
                                        getRtaJefe &&
                                        disable != true &&
                                        tipoActuacion != "1" &&
                                        !getActuacionPendienteDeFirmas &&
                                        !getValidarUsuariosPendienteFirma ?
                                        <div className='col-md-12'>
                                            <table>
                                                <thead>
                                                    <tr>
                                                        <th style={{ width: '30%' }}>Aprobar y/o rechazar {tipoActuacion == "0" ? "actuacion" : 'comisorio'}: </th>
                                                        <th style={{ paddingLeft: '50px' }}>
                                                            <div className="block-content block-content-full text-left">
                                                                <Link to={`/ActuacionesAprobacionRechazar/`} state={{ getActuacionConFirmas: getListaFirmasMecanicas.data.length, from: from, selected_id_etapa: selected_id_etapa, id: id, nombre: nombre, estadoActualActuacion: estadoActualActuacion, titulo: "Aprobar actuación", valor: 1, solicitante: getUsuarioSolicitante, tipoActuacion: tipoActuacion }}>
                                                                    <button type="button" className="btn btn-rounded btn-primary">Aprobar</button>
                                                                </Link>
                                                                <Link to={`/ActuacionesAprobacionRechazar/`} state={{ getActuacionConFirmas: getListaFirmasMecanicas.data.length, from: from, selected_id_etapa: selected_id_etapa, id: id, nombre: nombre, estadoActualActuacion: estadoActualActuacion, titulo: "Rechazar actuación", valor: 2, solicitante: getUsuarioSolicitante, tipoActuacion: tipoActuacion }}>
                                                                    <button type="button" className="btn btn-rounded btn-outline-primary">Rechazar</button>
                                                                </Link>
                                                            </div>
                                                        </th>
                                                    </tr>
                                                </thead>
                                            </table>
                                        </div>
                                        : null
                                }

                                {
                                    (hasAccess('Actuaciones', 'AprobarYRechazarUnImpedimento')) &&
                                        getActuacionEstaAprobadaSioNo == false &&
                                        getRtaJefe &&
                                        disable != true &&
                                        tipoActuacion == "1" &&
                                        !getActuacionPendienteDeFirmas &&
                                        !getValidarUsuariosPendienteFirma ?
                                        <div className='col-md-12'>
                                            <table>
                                                <thead>
                                                    <tr>
                                                        <th style={{ width: '30%' }}>Aprobarr y/o rechazar Impedimento: </th>
                                                        <th style={{ paddingLeft: '50px' }}>
                                                            <div className="block-content block-content-full text-left">
                                                                <Link to={`/ActuacionesAprobacionRechazar/`} state={{ getActuacionConFirmas: getListaFirmasMecanicas.data.length, from: from, selected_id_etapa: selected_id_etapa, id: id, nombre: nombre, estadoActualActuacion: estadoActualActuacion, titulo: "Aprobar actuación", valor: 1, solicitante: getUsuarioSolicitante, tipoActuacion: tipoActuacion }}>
                                                                    <button type="button" className="btn btn-rounded btn-primary mb-2">Aprobar</button>
                                                                </Link>
                                                                <Link to={`/ActuacionesAprobacionRechazar/`} state={{ getActuacionConFirmas: getListaFirmasMecanicas.data.length, from: from, selected_id_etapa: selected_id_etapa, id: id, nombre: nombre, estadoActualActuacion: estadoActualActuacion, titulo: "Rechazar actuación", valor: 2, solicitante: getUsuarioSolicitante, tipoActuacion: tipoActuacion }}>
                                                                    <button type="button" className="btn btn-rounded btn-outline-primary mb-2">Rechazar</button>
                                                                </Link>
                                                            </div>
                                                        </th>
                                                    </tr>
                                                </thead>
                                            </table>
                                        </div>
                                        : null
                                }

                                {
                                    (hasAccess('Actuaciones', 'SolicitudDeInactivación')) && getUltimoEstado == "Solicitud inactivación" && disable != true ? (
                                        <div className='col-md-12'>
                                            <label><b>Estado de aprobación de inactivación: </b></label>

                                            <Link to={`/ActuacionesAceptaRechazaSolicitudDeAnulacion/`} state={{ from: from, selected_id_etapa: selected_id_etapa, id: id, nombre: nombre, estadoActualActuacion: estadoActualActuacion, boton: "Acepta", solicitante: getUsuarioSolicitante, tipoActuacion: tipoActuacion }}>
                                                <button type="button" className="btn btn-rounded btn-primary">Aprobar</button>
                                            </Link>
                                            <Link to={`/ActuacionesAceptaRechazaSolicitudDeAnulacion/`} state={{ from: from, selected_id_etapa: selected_id_etapa, id: id, nombre: nombre, estadoActualActuacion: estadoActualActuacion, boton: "Rechaza", solicitante: getUsuarioSolicitante, tipoActuacion: tipoActuacion }}>
                                                <button type="button" className="btn btn-rounded btn-outline-primary">Rechazar</button>
                                            </Link>
                                        </div>) : null
                                }

                            </div >
                        </div >

                        <div className="block block-themed">
                            <div className={(disable ? ("block-header bg-dark") : "block-header")}>
                                <h3 className="block-title"><strong>Usuarios para firma mecánica</strong></h3>
                            </div>
                            <div className="block-content">
                                {
                                    getUltimoEstado != "Remitida" &&
                                        getUltimoEstado != "Aprobada" &&
                                        getUltimoEstado != "Aprobada y pdf definitivo" &&
                                        getRtaJefe &&
                                        !getActuacionEstaAprobadaSioNo ? (
                                        (hasAccess('Actuaciones', 'AgregarUsuarioParaFirmaMecanica')) ? (
                                            <Link to="/AgregarUsuarioParaFirmaMecanica" state={{ from: from, selected_id_etapa: selected_id_etapa, id: id, nombre: nombre, estadoActualActuacion: estadoActualActuacion, titulo: "Agregar usuario para firma mecánica", valor: 1, solicitante: getUsuarioSolicitante, tipoActuacion: tipoActuacion }}>
                                                <button type="button" className="btn btn-rounded btn-primary mb-4" >Agregar usuario para firma mecánica</button>
                                            </Link>
                                        ) : null
                                    ) : null}
                                <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                    <thead>
                                        <tr>
                                            <th>Usuario</th>
                                            <th>Tipo firma</th>
                                            <th>Estado </th>
                                            <th>Acciones</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {getFirmasMecanicas ? ListaFirmasMecanicas() : null}
                                    </tbody>
                                </table>
                            </div>
                        </div>

                        <div className="block block-themed">
                            <div className={(disable ? ("block-header bg-dark") : "block-header")}>
                                <h3 className="block-title"><strong>Historial de aprobaciones {tipoActuacion == "0" ? "de la Actuación" : (tipoActuacion == "1" ? 'del Impedimento' : ' del Comisorio')}</strong></h3>
                            </div>
                            <div className="block-content">
                                <table className="table table-bordered table-striped table-vcenter js-dataTable-full table-responsive-md">
                                    <thead>
                                        <tr>
                                            <th style={{ width: '23%' }}>Fecha</th>
                                            <th>Usuario</th>
                                            <th>Usuario aprueba/rechaza</th>
                                            <th>Estado</th>
                                            <th>Observaciones</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {getRtaInfoTrazabilidadActuaciones ? infoTrazabilidad() : null}
                                    </tbody>
                                </table>
                            </div>
                        </div>
                    </Form >
                )
                }
            </Formik >


        </>
    );

}

export default ActuacionesVer;
