import React, { useEffect, useState, } from 'react';
import { ErrorMessage, Form, Field, Formik } from 'formik';
import { Link } from 'react-router-dom';
import Spinner from '../../Utils/Spinner';
import GenericApi from './../../Api/Services/GenericApi';
import '../../Utils/Constants';
import { getUser } from '../../Utils/Common';
import InfoErrorApi from '../../Utils/InfoErrorApi';

function TrasladoProcesos() {

    const [errorApi, setErrorApi] = useState('');
    const [formIntialValuesTraslado, setFormIntialValuesTraslado] = useState();
    const [getListaUsuarios, setListaUsuarios] = useState({ data: {} });
    const [getRespuestaUsuarios, setRespuestaUsuarios] = useState(false);

    const columns = [

    ];

    useEffect(() => {
        async function fetchData() {

        }
        fetchData();
    }, []);

    const enviarDatos = (datos) => {
        window.showSpinner(true);
        let data;
        if (formIntialValuesTraslado) {

            data = {

                "data": {
                    "type": "user",
                    "attributes": {
                        "nombre": formIntialValuesTraslado ? (formIntialValuesTraslado.nombre ? formIntialValuesTraslado.nombre : '') : '',
                        "apellido": formIntialValuesTraslado ? (formIntialValuesTraslado.apellido ? formIntialValuesTraslado.apellido : '') : '',
                        "id_dependencia": getUser().id_dependencia
                    }
                }
            }
        }


        console.log(JSON.stringify(data));

        GenericApi.getByDataGeneric('usuario/usuario-filter', data).then(
            datos => {

                console.log(JSON.stringify(datos));

                if (!datos.error) {
                    setListaUsuarios(datos);
                    setRespuestaUsuarios(true);
                }
                else {
                    setErrorApi(datos.error.toString())
                    // console.log(datos.error);
                    window.showModal(1)
                }
                window.showSpinner(false);
            }
        )
    }


    const listarCoincidencias = () => {


        if (getListaUsuarios != null && typeof (getListaUsuarios) != 'undefined') {
            return (

                getListaUsuarios.data.map((usuario, i) => {
                    return (

                        <tr key={usuario.id}>
                            <td>{usuario.attributes.nombre}</td>
                            <td>{usuario.attributes.apellido}</td>
                            <td>{usuario.attributes.email}</td>
                            <td>{usuario.attributes.dependencia.nombre}</td>

                            <td>  <Link to={`/CasosActivos/${usuario.attributes.name}`} state={{ data: usuario }}>
                                <button type='button' data-dismiss="modal" className='btn btn-rounded btn-primary' >VER CASOS ACTIVOS</button>
                            </Link></td>
                        </tr >
                    )
                })
            )
        }


    }

    return (
        <>
            <Spinner />
            <InfoErrorApi error={errorApi} />
            <Formik
                initialValues={{
                    nombre: '',
                    apellido: '',
                }}
                enableReinitialize
                validate={(valores) => {
                    setFormIntialValuesTraslado(valores);
                    let errores = {}

                    if (!valores.nombre && !valores.apellido) {
                        errores.nombre = 'Debe ingresar nombre o apellido'
                    }

                    return errores
                }}
                onSubmit={(valores, { resetForm }) => {

                    enviarDatos(valores);
                }}
            >

                {({ errors }) => (
                    <Form>

                        <div className="w2d_block">
                            <nav aria-label="breadcrumb">
                                <ol className="breadcrumb breadcrumb-alt push">
                                    <li className="breadcrumb-item"> <small>Administración</small></li>
                                    <li className="breadcrumb-item"> <small>Traslado de casos</small></li>
                                </ol>
                            </nav>
                        </div>


                        <div className="block block-themed">

                            <div className="block-header">
                                <h3 className="block-title">ADMINISTRACIÓN :: TRASLADO DE PROCESOS</h3>
                            </div>

                            <div className="block-content block-content-full">
                                <div className="row">
                                    <div className="col-md-12 text-center mb-3">
                                        <label>PASO 1. COLOQUE EL NOMBRE DEL USUARIO QUE DESEA CONSULTAR:</label>
                                    </div>
                                </div>
                                <div className="row">
                                    <div className="col-md-12">
                                        <div className="form-group">
                                            <ErrorMessage name="nombre" component={() => (<span className="text-danger">{errors.nombre}</span>)} />
                                        </div>
                                    </div>
                                </div>
                                <div className="row">

                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label htmlFor='nombre'>NOMBRES</label>
                                            <Field type="text" id="nombre" name="nombre" className="form-control" placeholder="Nombre" maxlength="255" />

                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label htmlFor='apellido'>APELLIDOS </label>
                                            <Field type="text" id="apellido" name="apellido" className="form-control" placeholder="Apellido" maxlength="255" />
                                        </div>
                                    </div>


                                </div>
                                <div className="block-content block-content-full text-right">
                                    <button type="submit" className="btn btn-rounded btn-primary">{global.Constants.BOTON_NOMBRE.BUSCAR}</button>
                                </div>

                                <div className="block-content block-content-full text-center">
                                    <div className="row">
                                        <div className="col-md-12">
                                            {
                                                (getListaUsuarios.data.length > 0) ? (
                                                    <div>
                                                        <hr />
                                                        <span>SE HAN ENCONTRADO LAS SIGUIENTES COINCIDENCIAS, SELECCIONE EL USUARIO PARA VER LOS CASOS QUE TIENE ACTIVOS.</span>

                                                        <div className="row mt-2" >

                                                            <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                                                <thead>
                                                                    <tr>
                                                                        <th>NOMBRE</th>
                                                                        <th>APELLIDO</th>
                                                                        <th>CORREO</th>
                                                                        <th>DEPENDENCIA</th>
                                                                        <th>ACCIONES</th>
                                                                    </tr>
                                                                </thead>
                                                                <tbody >
                                                                    {getRespuestaUsuarios ? listarCoincidencias() : null}
                                                                </tbody>
                                                            </table>

                                                        </div>


                                                    </div>
                                                ) : null
                                            }
                                        </div>
                                    </div>
                                </div>
                            </div>




                        </div>
                    </Form>
                )}
            </Formik>
        </>
    )

}

export default TrasladoProcesos;
