sourcesContent:
import { ErrorMessage, Field, Form, Formik } from 'formik';
import { useEffect, useState } from 'react';
import RemisionQuejaApi from '../../Api/Services/RemisionQuejaApi';
import InfoErrorApi from '../../Utils/InfoErrorApi';
import { Navigate, useParams } from "react-router";
import { Link, useLocation } from 'react-router-dom';
import ParametrosMasApi from "./../../Api/Services/ParametrosMasApi";
import InfoExitoApiCustom from '../../Utils/InfoExitoApiCustom';
import InfoExitoApiTextsCustom from '../../Utils/InfoExitoApiTextsCustom';
import Spinner from '../../Utils/Spinner';
import { getUser } from '../../../components/Utils/Common';
import DocumentoSiriusApi from '../../Api/Services/DocumentoSiriusApi';
import GestorRespuestaApi from '../../Api/Services/GestorRespuestaApi';

function GestorRespuestaForm() {
    
    const location = useLocation();
    const { from } = location.state;

    let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;
    let vigencia = from.vigencia;
    
    const [isRedirect, setIsRedirect] = useState(false);
    const [getNombreUsuario, setNombreUsuario] = useState("");
    const [errorApi, setErrorApi] = useState('');
    const [exitoApiTextsCustom, setExitoApiTextsCustom] = useState({titulo: null, cuerpo: null, boton: null});
    const [inputListArchivos, setInputListArchivos] = useState([{ folios: "", archivo: {}, filebase64: "", size: 0, id_mas_formato: null }]);
    
    const [getMaximoTextArea, setMaximoTextArea] = useState(0);
    const [getMinimoTextArea, setMinimoTextArea] = useState(0);
    const [countTextArea, setCountTextArea] = useState(0);
    const [getFormatosApi, setFormatosApi] = useState();

    const [getGestorRespuestaApi, setGestorRespuestaApi] = useState();
    
    useEffect(() => {
        async function fetchData() {

            //window.showSpinner(true);

            setNombreUsuario(getUser().nombre);

            GestorRespuestaApi.getGestorRespuestaByProcesoDisciplinario(procesoDisciplinarioId).then(
                datos => {
                    if (!datos.error) {
                        console.log("Daticos", datos);
                        setGestorRespuestaApi(datos);
                    }
                    else {
                        setErrorApi(datos.error.toString())
                        window.showModal(1)
                    }
                    window.showSpinner(false);
                }
            );

            ParametrosMasApi.getFormatos().then(
                datos => {
                    if (!datos.error) {
                        setFormatosApi(datos.data);
                    }
                    else {
                        setErrorApi(datos.error.toString())
                        window.showModal(1)
                    }
                    window.showSpinner(false);
                }
            );
            obtenerParametros();
        }
        fetchData();
    }, []);


    function containsSpecialChars(str) {
        const specialChars = `\`!@#$%^&*()_+\-=\[\]{};':"\\|<>\/?~`;
      
        const result = specialChars.split('').some(specialChar => {
          if (str.includes(specialChar)) {
            return true;
          }
      
          return false;
        });
      
        return result;
      }

    /*Llamadas API*/
    const obtenerParametros = () => {
        try {
            const data = {
                "data": {
                    "type": 'mas_parametro',
                    "attributes": {
                        "nombre": "minimo_caracteres_textarea|maximo_caracteres_textarea"
                    }
                }
            }

            //buscamos el parametro
            ParametrosMasApi.getParametroPorNombre(data).then(
                datos => {

                    if (!datos.error) {

                        if (datos["data"].length > 0) {

                            datos["data"].filter(data => data["attributes"]["nombre"].includes('minimo_caracteres_textarea')).map(filteredName => (
                                setMinimoTextArea(filteredName["attributes"]["valor"])
                            ))
                            datos["data"].filter(data => data["attributes"]["nombre"].includes('maximo_caracteres_textarea')).map(filteredName => (
                                setMaximoTextArea(filteredName["attributes"]["valor"])
                            ))

                        }
                    } else {
                        window.showModal(1);
                    }
                }
            )
        } catch (error) {
            console.log(error);
        }
    }

    /*Funciones*/
    const handleInputChangeFolios = (e, index) => {
        const { name, value } = e.target;
        const list = [...inputListArchivos];
        list[index][name] = value;
        setInputListArchivos(list);
    };

    const handleInputChangeArchivos = (e, index) => {

        const { name, files } = e.target;
        if(files.length > 0){
            const extension = getFileExtension(files[0].name);
            const resultado = getFormatosApi.filter(datos => datos.attributes.nombre == extension);
            
            if(resultado.length > 0){
                const list = [...inputListArchivos];
                list[index][name] = files[0];
                if (files[0]) {
                    list[index][name] = files[0];
                }
                else {
                    list[index][name] = '';
                    list[index].filebase64 = '';
                    list[index].size = 0;
                    list[index].id_mas_formato = null;
                    setInputListArchivos(list);
                }
                //Conversion a Base64
                Array.from(e.target.files).forEach(archivo => {
                    var reader = new FileReader();
                    reader.readAsDataURL(archivo);
                    reader.onload = function () {
                        var arrayAuxiliar = [];
                        var base64 = reader.result;
                        arrayAuxiliar = base64.split(',');
                        list[index].filebase64 = arrayAuxiliar[1];
                        list[index].id_mas_formato = resultado[0].id;
                        setInputListArchivos(list);
                    }
                })
            }
            else{
                const list = [...inputListArchivos];
                list[index][name] = files[0];
                list[index].filebase64 = '';
                list[index].size = 0;
                list[index].id_mas_formato = null;
                setInputListArchivos(list);
                setErrorApi("El archivo seleccionado no tiene un formato permitido")
                window.showModal(1)
            }
        }
        else{
            const list = [...inputListArchivos];
            list[index][name] = '';
            list[index].filebase64 = '';
            list[index].size = 0;
            list[index].id_mas_formato = null;
            setInputListArchivos(list);
        }

    }

    // handle click event of the Remove button
    const handleRemoveClick = index => {
        const list = [...inputListArchivos];
        list.splice(index, 1);
        setInputListArchivos(list);
    };

    // handle click event of the Add button
    const handleAddClick = () => {
        setInputListArchivos([...inputListArchivos, { folios: "", archivo: {}, filebase64: "", size: 0, id_mas_formato: null }]);
    };

    function getFileExtension(filename) {
        return filename.slice((filename.lastIndexOf(".") - 1 >>> 0) + 2);
    }

    const handleOnSubmitDocument = (valores) => {

        let band = false;

        // VALIDA QUE NO EXISTAN ARCHIVOS CON EL MISMO NOMBRE
        for (let i = 0; i < inputListArchivos.length - 1; i++) {
            for (let j = i + 1; j < inputListArchivos.length; j++) {
                if (inputListArchivos[i].archivo.name === inputListArchivos[j].archivo.name) {
                    setErrorApi("No puede registrar archivos con el mismo nombre.")
                    window.showModal(1)
                    band = true;
                }
            }
        }

        // VALIDA QUE NO SE PUEDAN SUBIR FORMATOS NO PERMITIDOS
        for (let i = 0; i < inputListArchivos.length; i++) {
            const extension = getFileExtension(inputListArchivos[i].archivo.name);
            const resultado = getFormatosApi.filter(datos => datos.attributes.nombre == extension);

            if (resultado.length <= 0) {
                setErrorApi('Uno de los documentos adjuntados no tiene un formato correspondiente')
                window.showModal(1)
                band = true;
            }
        }

        if (band === false) {

            // PASO 1. VALIDAR CON SIRIUS
            // PASO 1.1 LOGIN Y RADICACION

            const attributes = [];

            for (let i = 0; i < inputListArchivos.length; i++) {
                // PASO 2. GUARDAR EN BASE DE DATOS
                if (inputListArchivos[i].folios !== null || inputListArchivos[i].folios !== '') {

                    attributes.push({
                        "id_proceso_disciplinario": procesoDisciplinarioId,
                        "id_etapa": from.id_etapa,
                        "id_fase": from.id_fase,
                        "url_archivo": "www.prueba.com",
                        "nombre_archivo": inputListArchivos[i].archivo.name,
                        "file64": inputListArchivos[i].filebase64,
                        "estado": "1",
                        "num_folios": inputListArchivos[i].folios,
                        "num_radicado": radicado,
                        "vigencia": vigencia,
                        "extension": getFileExtension(inputListArchivos[i].archivo.name),
                        "peso": inputListArchivos[i].archivo.size,
                        "created_user": getNombreUsuario,
                        "descripcion": valores.descripcion,
                        "id_mas_formato": inputListArchivos[i].id_mas_formato,
                        "es_compulsa": false,
                        "es_soporte": false
                    });

                }
            }

            const data = {
                "data": {
                    "type": "documento_sirius",
                    "attributes": attributes
                }
            }

            window.showSpinner(true);

            DocumentoSiriusApi.addDocumentoSirius(data).then(
                datos => {
                    if (!datos.error) {
                        setExitoApiTextsCustom(
                            {
                                titulo: 'Archivo(s) adjuntados', 
                                cuerpo: 'Los archivos fueron adjuntados al radicado: ' + radicado, 
                                boton: 'Aceptar'
                            }
                        );
                        from.debeConsultar = true;
                        window.showModal(6);
                        setIsRedirect(true);
                    }
                    else {
                        setErrorApi(datos.error.toString())
                        window.showModal(1)
                    }
                    window.showSpinner(false);
                }
            )

        }

    }

    const handleOnSubmit = (valores) => {

        const data = {
            "data": {
                "type": "documento_sirius",
                "attributes": {
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "aprobado": (valores.aprobacion*1),
                    "descripcion": valores.descripcion,
                    "version": getGestorRespuestaApi.data[0].attributes.version,
                    "nuevo_documento": false
                }
            }
        }

        window.showSpinner(true);

        GestorRespuestaApi.addGestorRespuesta(data).then(
            datos => {
                if (!datos.error) {
                    setExitoApiTextsCustom(
                        {
                            titulo: 'Proceso Exitoso', 
                            cuerpo: 'El expediente ha sido reasignado', 
                            boton: 'Aceptar'
                        }
                    );
                    from.debeConsultar = true;
                    window.showModal(6);
                    setIsRedirect(true);
                }
                else {
                    setErrorApi(datos.error.toString())
                    window.showModal(1)
                }
                window.showSpinner(false);
            }
        )

    }

    const handleClicArchivo = (documento_sirius) => {
        try {
            let nombre_documento = documento_sirius.attributes.nombre_archivo;
            let extension = documento_sirius.attributes.extension;
            let es_compulsa = false;

            if(documento_sirius.attributes.compulsa){
                es_compulsa = true;
            }

            const data = {
                "data": {
                    "type": "documeto_sirius",
                    "attributes": {
                        "radicado":radicado,
                        "vigencia": vigencia,
                        "id_documento_sirius": nombre_documento,
                        "extension": extension,
                        "es_compulsa": es_compulsa
                    }
                }    
            }

            DocumentoSiriusApi.getDocumento(data).then(
                datos => {
                    if (!datos.error) {
                        //console.log(datos.content_type);
                        downloadBase64File(datos.content_type, datos.base_64, nombre_documento, extension);
                    }
                    else {
                        setErrorApi(datos.error.toString())
                        window.showModal(1)
                    }
                }
            )
        } catch (error) {
            console.error(error);
        }
    };

    function downloadBase64File(contentType, base64Data, fileName, extension) {
        const linkSource = `data:${contentType};base64,${base64Data}`;
        const downloadLink = document.createElement("a");
        downloadLink.href = linkSource;
        downloadLink.download = fileName+"."+extension;
        downloadLink.click();
    }

    /*Redirects*/
    const redirectToRoutes = () => {
        return <Navigate to={`/RamasProceso`} state={{ from: from }} />;
    }

    const componentRegistro = () => {
        return (
            <>
                <div className="col-md-12">
                    <div className="alert alert-warning alert-dismissable" role="alert">
                        <h3 className="alert-heading font-size-h4 my-2">Alerta</h3>
                        <p className="mb-0"> ETAPA: PENDIENTE - FASE:   PENDIENTE </p>
                        <p className="mb-0"> Para cualquier archivo que requiera adjuntar valide previamente lo siguiente: </p>
                        <p className="mb-0">
                            NO puede superar QUINCE (15) Mb de peso/tamaño.
                            Tipo/Formato permitido: [.doc], [.docx], [.pdf], [.xls], [.xlsx], [.zip], [.rar], [.png], [.jpg], [.mp4], [.avi], [.mpeg], [.mpg], [.mov], [.mp3], [.wav], [.wma].
                        </p>
                        <p className="mb-0">
                            El nombre del archivo debe ser de máximo 40 caracteres.
                        </p>
                    </div>
                </div>
                <div className="col-md-12">
                    { componentForm() }
                </div>
            </>
        )
    }

    /*Componentes */
    const componentForm = () => {
        return (
            <Formik
                initialValues={{
                    descripcion: '',
                }}
                enableReinitialize
                validate={(valores) => {

                    let errores = {}
                    setCountTextArea(valores.descripcion.length);

                    if (valores.descripcion && valores.descripcion.length < getMinimoTextArea) {
                        errores.descripcion = 'Debe ingresar mínimo ' + getMinimoTextArea + ' caracteres'
                    }


                    if (valores.descripcion) {
                        if(containsSpecialChars(valores.descripcion))
                        errores.descripcion = 'Tiene caracteres inválidos'
                    }

                    return errores;
                }}
                onSubmit={(valores, {resetForm}) => {
                    handleOnSubmitDocument(valores);
                }}>
                {({ errors, values }) => (
                    <Form>
                        <div className='row'>
                            <div className='col-md-12'>
                                {
                                    inputListArchivos.map((x, i) => {
                                        return (
                                            <div key={i}>
                                                <div className='row'>
                                                    <div className='col-md-12'>
                                                        <div className='row'>
                                                            <div className='col-md-6'>
                                                                <input className='form-control' name="folios" placeholder="No. de folios" value={x.folios} onChange={e => handleInputChangeFolios(e, i)} required />
                                                            </div>

                                                            <div className='col-md-6'>
                                                                <label className="custom-file-label" htmlFor="example-file-input-custom" data-toggle="custom-file-input">{x.archivo.name}</label>
                                                                <input className="custom-file-input" data-toggle="custom-file-input" type="file" name="archivo" onChange={e => handleInputChangeArchivos(e, i)} required />
                                                            </div>
                                                            {
                                                                inputListArchivos.length !== 1
                                                                ?
                                                                (
                                                                    <div className="col-md-2">
                                                                        <button className="btn btn-danger" onClick={() => handleRemoveClick(i)}><i className="fas fa-trash-alt col"></i></button>
                                                                    </div>
                                                                )
                                                                :
                                                                null
                                                            }
                                                        </div>
                                                    </div>
                                                </div>
                                                <br></br>
                                                <div className='row'>
                                                    <div className='col-md-12'>
                                                        <div className="form-group">
                                                            <Field as="textarea" className="form-control" id="descripcion" name="descripcion" rows="4" placeholder="Descripción de los soportes" required
                                                                maxLength={getMaximoTextArea} minLength={getMinimoTextArea}></Field>
                                                            <div className="text-right">
                                                                <span className="text-primary">{countTextArea} / {getMaximoTextArea}</span>
                                                            </div>
                                                            <ErrorMessage name='descripcion' component={() => (<span className='text-danger'>{errors.descripcion}</span>)} />
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        );
                                    })
                                }
                            </div>
                        </div>

                        <div className='row'>
                            <div className="col-md-12 text-right">
                                <button type="submit" className="btn btn-primary"> <i className="fas fa-save"></i> Registrar</button>
                                <Link to={`/RamasProceso/`} state={{ from: from }}>
                                    <button type="button" className="btn btn-danger"><i className="fas fa-window-close"></i> Cancelar</button>
                                </Link>
                            </div>
                        </div>
                        <br></br>
                    </Form>
                )}
            </Formik>
        )
    }

    const componentAprobacion = () => {
        return (
            <>
                <div className='row'>
                    <div className="col-md-12">
                        <div className='row'>
                            <div className="col-md-4">
                                <label>Versión: </label> { getGestorRespuestaApi.data[0].attributes.version }
                            </div>
                            <div className="col-md-4">
                                <label>Fecha: </label> { getGestorRespuestaApi.data[0].attributes.fecha }
                            </div>
                            <div className="col-md-4">
                                <label>Concepto: </label> PENDIENTE
                            </div>
                        </div>
                        <div className='row'>
                            <div className="col-md-12">
                                <label>Por: </label> PENDIENTE
                            </div>
                        </div>
                        <div className='row'>
                            <div className="col-md-10">
                                <label>Observación: </label> PENDIENTE
                            </div>
                            <div className="col-md-2">
                                <button type='button' title='Descargar documento' className='btn btn-sm btn-primary' onClick={() => handleClicArchivo(null)}><i className="fas fa-download"></i></button>
                            </div>
                        </div>
                        <div className='row'>
                            <div className="col-md-6">
                                <label>Evaluación Inicial: </label> PENDIENTE
                            </div>
                            <div className="col-md-6">
                                <label>Evaluación Final: </label> PENDIENTE
                            </div>
                        </div>
                    </div>
                </div>
                <div className="content-heading"></div>
                <div className='row'>
                    <div className="col-md-6">
                        <p>Tipo de Expediente: <b>PENDIENTE</b></p>
                    </div>
                    <div className="col-md-6">
                        <p>Evaluación de la Queja: <b>PENDIENTE</b></p>
                    </div>
                </div>
                <div className="content-heading"></div>
                {
                    getGestorRespuestaApi?.data[0]?.attributes?.proceso_finalizado == false
                    ?
                    (
                        <>             
                            <div className="content-heading"></div>
                            <div className='row'>
                                <div className="col-md-12">
                                    <div className="alert alert-warning alert-dismissable" role="alert">
                                        <div className='row'>
                                            <div className="col-md-4">
                                                Mi rol: <b>PENDIENTE</b>
                                            </div>
                                            <div className="col-md-8">
                                                Siguiente rol O Anterior rol: <b>PENDIENTE</b><br></br>
                                                Por: <b>PENDIENTE</b>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <div>
                                <div>
                                    <Formik
                                        initialValues={{
                                            aprobacion: '',
                                            descripcion: ''
                                        }}
                                        enableReinitialize
                                        validate={(valores) => {

                                            let errores = {}
                                            setCountTextArea(valores.descripcion.length);

                                            if (!valores.aprobacion) {
                                                errores.aprobacion = 'Debe seleccionar un concepto de documento'
                                            }

                                            if (valores.descripcion) {
                                                if (valores.descripcion.length < getMinimoTextArea) {
                                                    errores.descripcion = 'Debe ingresar mínimo ' + getMinimoTextArea + ' caracteres'
                                                }
                                            }

                                            if (valores.descripcion) {
                                                if(containsSpecialChars(valores.descripcion))
                                                errores.descripcion = 'Tiene caracteres inválidos'
                                            }

                                            return errores;
                                        }}
                                        onSubmit={(valores, {resetForm}) => {
                                            handleOnSubmit(valores);
                                        }}>
                                        {({ errors, values }) => (
                                            <Form>
                                                
                                                <div className='row'>
                                                    <div className="col-md-12">
                                                        <div className="form-group">
                                                            <label htmlFor='aprobacion'>Concepto del documento <span className='text-danger'>*</span></label>
                                                            <Field as="select" className='form-control' id='aprobacion' name='aprobacion'>
                                                                <option value="">Por favor seleccione</option>
                                                                <option value="1">APROBAR</option>
                                                                <option value="0">RECHAZAR</option>
                                                            </Field>
                                                            <ErrorMessage name='aprobacion' component={() => (<span className='text-danger'>{errors.aprobacion}</span>)} />
                                                        </div>
                                                    </div>
                                                </div>
                                                <div className='row'>
                                                    <div className='col-md-12'>
                                                        <div className="form-group">
                                                            <Field as="textarea" className="form-control" id="descripcion" name="descripcion" rows="4" placeholder="Detalle del concepto del documento" required
                                                                maxLength={getMaximoTextArea} minLength={getMinimoTextArea}></Field>
                                                            <div className="text-right">
                                                                <span className="text-primary">{countTextArea} / {getMaximoTextArea}</span>
                                                            </div>
                                                            <ErrorMessage name='descripcion' component={() => (<span className='text-danger'>{errors.descripcion}</span>)} />
                                                        </div>
                                                    </div>
                                                </div>

                                                <div className='row'>
                                                    <div className="col-md-12 text-right">
                                                        <button type="submit" className="btn btn-primary"> <i className="fas fa-save"></i> Registrar</button>
                                                        <Link to={`/RamasProceso/`} state={{ from: from }}>
                                                            <button type="button" className="btn btn-danger"><i className="fas fa-window-close"></i> Cancelar</button>
                                                        </Link>
                                                    </div>
                                                </div>
                                                <br></br>
                                            </Form>
                                        )}
                                    </Formik>
                                </div>
                            </div>
                        </>
                    )
                    :
                    null
                }
            </>
        );
    }

    return (
        <>
            { <Spinner /> }
            { isRedirect ? redirectToRoutes() : null}
            { <InfoErrorApi error={errorApi} />}
            { <InfoExitoApiTextsCustom success={exitoApiTextsCustom} />}

            <div className="block block-rounded block-bordered">
                <div className="block-content">
                    <nav aria-label="breadcrumb">
                        <ol className="breadcrumb breadcrumb-alt push">
                            <li className="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                            <li className="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`} state={{ from: from }}><small>Ramas del proceso</small></Link></li>
                            <li className="breadcrumb-item"> <small>Documento Cierre</small></li>
                        </ol>
                    </nav>
                </div>
            </div>

            <div className='content'>
                <div className='row'>
                    <div className='col-md-12'>
                        <div className='block block-themed'>
                            <div className='block-header'>
                                <h3 className='block-title'>SINPROC NO {radicado} ::: GESTOR RESPUESTA</h3>
                            </div>
                            <div className='block-content'>
                                { /*getGestorRespuestaApi?.data.length > 0 && getGestorRespuestaApi?.data[0]?.attributes.nuevo_documento == false  ? componentAprobacion() : componentRegistro()*/ }
                                {
                                    getGestorRespuestaApi?.data.length > 0 
                                    ?
                                    (
                                        getGestorRespuestaApi?.data[0]?.attributes.aprobado == true
                                        ?
                                        componentAprobacion()
                                        :
                                        componentRegistro()
                                    )
                                    :
                                    componentRegistro()
                                }
                            </div>
                        </div>
                    </div>
                </div>
            </div>

        </>
    );

}

export default GestorRespuestaForm;
