import React, { Component, Fragment } from "react";
import { Field } from 'formik';


class Autocomplete extends Component {


    constructor(props) {
        super(props);
        this.state = {
            activeSuggestion: 0,
            filteredSuggestions: [],
            showSuggestions: false,
            userInput: "",
            idEntidad: "",
            nombreEntidad: "",
            direccion: "",
            paginaweb: "",
            correo: "",
            telefono: "",
            codigopostal: "",
        };
    }
    onTrigger = (event) => {
        //console.log(event.currentTarget.innerText);
        this.props.parentCallback(event.currentTarget.innerText.split(" | ")[0]);
        event.preventDefault();
    }
    onChange = e => {
        try {


            const { suggestions } = this.props;

            const userInput = e.currentTarget.value;




            const filteredSuggestions = suggestions.data.filter(
                suggestion =>
                    (suggestion["id"] + " | " + suggestion["nombre"]).toLowerCase().indexOf(userInput.toLowerCase()) > -1
            );

            this.setState({
                activeSuggestion: 0,
                filteredSuggestions,
                showSuggestions: true,
                userInput: e.currentTarget.value,


                idEntidad: "",
                nombreEntidad: "",
                direccion: "",
                paginaweb: "",
                correo: "",
                telefono: "",
                codigopostal: "",
            });

        } catch (error) {
            console.error("Error " + error);
        }
    };

    onClick = e => {

        this.setState({
            activeSuggestion: 0,
            filteredSuggestions: [],
            showSuggestions: false,
            userInput: e.currentTarget.innerText.split(" | ")[0] + " | " + e.currentTarget.innerText.split(" | ")[1],
            idEntidad: e.currentTarget.innerText.split(" | ")[0],
            nombreEntidad: e.currentTarget.innerText.split(" | ")[1],
            direccion: e.currentTarget.innerText.split(" | ")[2],
            paginaweb: e.currentTarget.innerText.split(" | ")[3],
            correo: e.currentTarget.innerText.split(" | ")[4],
            telefono: e.currentTarget.innerText.split(" | ")[5],
            codigopostal: e.currentTarget.innerText.split(" | ")[6],
            nombre_sector: e.currentTarget.innerText.split(" | ")[7],
            nombre_secretaria: e.currentTarget.innerText.split(" | ")[8]
        });

        this.onTrigger(e);
    };

    onKeyDown = e => {

        try {
            const { activeSuggestion, filteredSuggestions } = this.state;
            if (filteredSuggestions[activeSuggestion]) {


                if (e.keyCode === 13) {
                    this.setState({
                        activeSuggestion: 0,
                        showSuggestions: false,
                        userInput: filteredSuggestions[activeSuggestion]["id"] + " | " + filteredSuggestions[activeSuggestion]["nombre"],
                        idEntidad: filteredSuggestions[activeSuggestion]["id"],
                        nombreEntidad: filteredSuggestions[activeSuggestion]["nombre"],
                        direccion: filteredSuggestions[activeSuggestion]["direccion"],
                        paginaweb: filteredSuggestions[activeSuggestion]["paginaweb"],
                        correo: filteredSuggestions[activeSuggestion]["correo"],
                        telefono: filteredSuggestions[activeSuggestion]["telefono"],
                        codigopostal: filteredSuggestions[activeSuggestion]["codigopostal"],
                        nombre_sector: filteredSuggestions[activeSuggestion]["nombre_sector"],
                        nombre_secretaria: filteredSuggestions[activeSuggestion]["nombre_secretaria"]
                    });
                } else if (e.keyCode === 38) {
                    if (activeSuggestion === 0) {
                        return;
                    }
                    this.setState({ activeSuggestion: activeSuggestion - 1 });
                }
                // User pressed the down arrow, increment the index
                else if (e.keyCode === 40) {
                    if (activeSuggestion - 1 === filteredSuggestions.length) {
                        return;
                    }
                    this.setState({ activeSuggestion: activeSuggestion + 1 });
                }
            }


        } catch (error) {
            console.error(error);
        }
    };

    render() {
        const {
            onChange,
            onClick,
            onKeyDown,
            state: {
                activeSuggestion,
                filteredSuggestions,
                showSuggestions,
                userInput
            }
        } = this;

        let suggestionsListComponent;

        if (showSuggestions && userInput) {
            if (filteredSuggestions.length) {
                suggestionsListComponent = (
                    <ul class="suggestions">
                        {filteredSuggestions.map((suggestion, index) => {
                            let className;

                            // Flag the active suggestion with a class
                            if (index === activeSuggestion) {
                                className = "suggestion-active";
                            }
                            return (
                                <li className={className} key={suggestion["id"]} onClick={onClick}>
                                    <strong>{suggestion["id"]} | {suggestion["nombre"]}</strong> | {suggestion["direccion"]} | {suggestion["paginaweb"]} | {suggestion["correo"]} | {suggestion["telefono"]} | {suggestion["codigopostal"]} | {suggestion["nombre_sector"]} | {suggestion["nombre_secretaria"]}
                                </li>
                            );
                        })}
                    </ul>
                );
            } else {
                suggestionsListComponent = (
                    <div class="no-suggestions">
                        <em>Sin resultados.</em>
                    </div>
                );
            }
        }

        return (
            <Fragment>
                <input
                    type="text"
                    id="idEntidad" name="idEntidad"
                    className="form-control"
                    onChange={onChange}
                    onKeyDown={onKeyDown}
                    value={userInput}

                />
                {suggestionsListComponent}
                <>
                    {
                        (this.state.nombreEntidad !== '') ?
                            (
                                <div>
                                    <div className="row">
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label htmlFor='sector'>Sector <span className="text-danger">*</span></label>
                                                <Field value={this.state.nombre_sector} disabled type="text" id="sector" name="sector" className="form-control" />

                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label htmlFor='secretaria'>Secretaria <span className="text-danger">*</span></label>
                                                <Field value={this.state.nombre_secretaria} type="text" id="secretaria" disabled name="secretaria" className="form-control" />

                                            </div>
                                        </div>
                                    </div>

                                    <div className="col">
                                        <div className="form-group">
                                            <div className="block-header block-header-default">
                                                <h3 className="block-title">Datos básicos de la entidad seleccionada:</h3>
                                            </div>

                                            <div className="block block-rounded block-bordered">

                                                <div className="block-content block-content-full">
                                                    <div className="row">
                                                        <div className="col-sm-12">
                                                            <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                                                <thead>
                                                                    <tr>
                                                                        <th colspan="4" className="block-title">{this.state.idEntidad} / {this.state.nombreEntidad}</th>
                                                                    </tr>
                                                                </thead>
                                                                <tbody>
                                                                    <tr>
                                                                        <td >
                                                                            Dirección
                                                                        </td>
                                                                        <td>
                                                                            <Field value={this.state.direccion} disabled type="text" className="form-control" />
                                                                        </td>
                                                                        <td >
                                                                            Página Web
                                                                        </td>
                                                                        <td>
                                                                            <Field value={this.state.paginaweb} disabled type="text" className="form-control" />
                                                                        </td>
                                                                    </tr>
                                                                    <tr>
                                                                        <td >
                                                                            Correo
                                                                        </td>
                                                                        <td >
                                                                            <Field value={this.state.correo} disabled type="text" className="form-control" />
                                                                        </td>
                                                                        <td >
                                                                            Teléfono
                                                                        </td>
                                                                        <td >
