import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import { getUser } from '../../Utils/Common';
import { useLocation } from 'react-router-dom';
import '../../Utils/Constants';
import ModalGen from '../../Utils/Modals/ModalGeneric';
import GenericApi from '../../Api/Services/GenericApi';

function ProcesoDisciplinarioMigracion() {


    const [getModalState, setModalState] = useState({ title: "", message: "", show: false });
    const [getListaFases, setListaFases] = useState({ data: [], links: [], meta: [] });
    const [getProcesoDisciplinario, setProcesoDisciplinario] = useState({ data: [], links: [], meta: [] });
    const [getRadicado, setRadicado] = useState(false);
    const [getEtapa, setEtapa] = useState(false);
    const [getUbicacionExpediente, setUbicacionExpediente] = useState(false);
    const [getAntecednete, setAntecedente] = useState(false);
    const [getDependenciaOrigen, setDependenciaOrigen] = useState(false);


    const location = useLocation()
    const { from } = location.state


    useEffect(() => {
        async function fetchData() {
            listaFases();
            listaProcesoDisciplinarioMigracion();
           
        }
        fetchData();
    }, []);


    const listaFases = () => {
        try {

            //buscamos el parametro
            GenericApi.getGeneric("get-fases-orden",).then(
                datos => {

                    if (!datos.error) {
                        setListaFases(datos);
                        //setRtaListaFases(true);
                    } else {
                        setModalState({ title: "Antecedentes", message: datos.error.toString(), show: true, redirect: '/AntecedentesForm', from: {from}, alert: global.Constants.TIPO_ALERTA.ERROR });
                    }
                }
            )
        } catch (error) {
            // console.log(error);
        }
    }


    const listaProcesoDisciplinarioMigracion = () => {
        try {

            //buscamos el parametro
            GenericApi.getGeneric("migracion-proceso-disciplinario/308232/1/1",).then(
                datos => {

                    if (!datos.error) {
                        setProcesoDisciplinario(datos);
                        console.log(datos.data.attributes.radicado);
                        //setRtaListaFases(true);
                    } else {
                        setModalState({ title: "Antecedentes", message: datos.error.toString(), show: true, redirect: '/AntecedentesForm', from: {from}, alert: global.Constants.TIPO_ALERTA.ERROR });
                    }
                }
            )
        } catch (error) {
            // console.log(error);
        }
    }

    const procesoDiscplinario = () => {

        if (getProcesoDisciplinario.data != null && typeof (getProcesoDisciplinario.data) != 'undefined') {
            return (
                <>
                    <span className="txt-blue-primary"><strong>Radicado: </strong> {getProcesoDisciplinario.data.attributes.radicado} <strong>Vigencia: </strong> <strong>A침o: </strong></span><br /><br />
                    <span className="txt-blue-primary"><strong>Tipo de proceso: </strong></span><br />
                    <span className="txt-blue-primary"><strong>Etapa: </strong></span><br />
                    <span className="txt-blue-primary"><strong>Dependencia: </strong></span><br />
                    <span className="txt-blue-primary"><strong>Antecedente: </strong></span><br />
                    
                    <div className=''>
                        <span className='txt-black-primary' title={(from.antecedente) ? from.antecedente : ""}>{from.antecedente ? from.antecedente.substring(0, 300) : ""}...</span>
                    </div>
                </>   
            )
        }
    }

    
    const tablaFases = () => {

        if (getListaFases.data != null && typeof (getListaFases.data) != 'undefined') {
            return (

                getListaFases.data.map((item, i) => {
                    return (
                        <tr>
                            <td>{i=i+1}</td>
                            <td>{item.attributes.nombre}</td>
                            <td><Link className="text-dark" to={item.attributes.link} state={{ from: from }}><small>Completar informaci칩n</small></Link></td>
                        </tr>
                    )
                })
            )
        }
    }

    return (
        <>
            {<ModalGen data={getModalState} />}
          
            <div className="w2d_block">
                <nav aria-label="breadcrumb">
                    <ol className="breadcrumb breadcrumb-alt push">
                        <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/Buscador`}><small>Buscador</small></Link></li>
                        <li class="breadcrumb-item"> <small>Migraci칩n</small></li>
                    </ol>
                </nav>
            </div>

            <div className="block block-themed">
                <div className="block-header">
                    <h3 className="block-title">Proceso de migraci칩n</h3>
                </div>

                <div className="block-content">

                    <div className="col-lg-12 push">
                        <div className="alert alert-info" role="alert">
                            {getProcesoDisciplinario!=null?
                                procesoDiscplinario():null
                            }
                        </div>
                    </div>

                    
                    <div className="col-md-12">

                        <table class="table">
                            <thead>
                                <tr>
                                    <th scope="col">#</th>
                                    <th scope="col">Fase</th>
                                    <th scope="col">Acciones</th>
                                </tr>
                            </thead>
                            <tbody>
                                {getListaFases!=null?
                                    tablaFases():null
                                }
                                                        
                            </tbody>
                        </table>
                        
                    </div>
                </div>
                
            </div>

                   
        </>
    );
}


export default ProcesoDisciplinarioMigracion;
