import { Link } from "react-router-dom";
import React, { Component, Fragment, useEffect, useState } from "react";
import { ErrorMessage, Field, Form, Formik } from 'formik';
import { useParams } from "react-router";
import { Navigate } from "react-router-dom";
import Spinner from '../../Utils/Spinner';
import InfoErrorApi from '../../Utils/InfoErrorApi';
import InfoExitoApi from '../../Utils/InfoExitoApi';
import ParametrosMasApi from '../../Api/Services/ParametrosMasApi';
import ClasificacionRadicadoApi from '../../Api/Services/ClasificacionRadicadoApi';
import DatePicker from 'react-datetime';
import 'react-datetime/css/react-datetime.css';
import moment from 'moment';
import 'moment/locale/es';
import { getUser } from '../../../components/Utils/Common';
import { useLocation } from 'react-router-dom'
import { ParametroModel } from '../../Models/ParametroModel';
import DatePerson from "../../DatePerson/DatePerson";
import { hasAccess } from '../../../components/Utils/Common';


class ClasificacionRadicado extends Component {

    
    constructor(props) {
        super(props);

        const { getIdProcesoDisciplinario } = this.props;
        const { getIdEtapa } = this.props;


        this.estado_proceso = {
            id_proceso_disciplinario: getIdProcesoDisciplinario,
            id_etapa: getIdEtapa
         
        };

        console.log("PROCESO DISCIPLINARIO: "+getIdProcesoDisciplinario)
        console.log("ETAPA: "+getIdEtapa)

        
        
        
    }

    render() {


        return (
            <Fragment>


                <div className='col-md-9 text-right'>

                    <>
                        <h1> Hola Sandra {this.estado_proceso.id_proceso_disciplinario}</h1>


                        <Spinner />
                        {<InfoExitoApi />}

                        <div className="block-content">
                                        <div className='text-right'>

                                            <Link to={`/ClasificacionRadicadoLista/`} title='Regresar a lista de ClasificaciÃ³n' state={{ from: from }}>
                                                <button type="button" className="btn btn-success"><i className="fas fa-backward"></i> </button>
                                            </Link>
                                        </div>
                                        <div className="row">

                                            <div className="col-md-12">
                                                <div className="form-group">
                                                    <label htmlFor="ingresoTipoExpediente">Tipo de expediente <span className="text-danger">*</span></label>
                                                    <select className="form-control" id="ingresoTipoExpediente" name="ingresoTipoExpediente"
                                                        value={selectedTipoExpediente} onChange={e => selectChangeTipoExpediente(e.target.value)}>
                                                        <option value="">Por favor seleccione</option>
                                                        {respuestaTipoExpediente ? selectTipoExpediente() : null}
                                                    </select>
                                                </div>
                                            </div>
                                        </div>

                                        {/*DERECHO DE PETICION*/}
                                        {selectedTipoExpediente === '1' ? componenteTipoExpediente(1) : ''}
                                        {/*PODER REFERENTE A SOLIICTUD*/}
                                        {selectedTipoExpediente === '2' ? componenteTipoExpediente(2) : ''}
                                        {/*QUEJA*/}
                                        {selectedTipoExpediente === '3' ? componenteTipoExpediente(3) : ''}
                                        {/*TUTELA*/}
                                        {selectedTipoExpediente === '4' ? componenteTipoExpediente(4) : ''}

                                    </div>

                    </>
                        
                </div>
            </Fragment>
        );
    }
    
}

export default ClasificacionRadicado;
