import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import Spinner from '../../Utils/Spinner';
import { Navigate } from "react-router-dom";
import Autocomplete from "../../Autocomplete/Autocomplete";
import { useLocation } from 'react-router-dom'
import '../../Utils/Constants';

import GenericApi from '../../Api/Services/GenericApi';
import ModalGen from '../../Utils/Modals/ModalGeneric';


function EntidadInvestigadoQuejaInterna() {

    const [countTextArea, setCountTextArea] = useState(0);
    const [getMaximoTextArea, setMaximoTextArea] = useState(0);
    const [getMinimoTextArea, setMinimoTextArea] = useState(0);

    const [getModalState, setModalState] = useState({ title: "", message: "", show: false });
    const [observaciones, setObservaciones] = useState('');
    const [getValidar, setValidar] = useState(null);
    const [getValidarRegistrar, setValidarRegistrar] = useState(false);
    const [getPlanta, setPlanta] = useState('');
    const [getContratista, setContratista] = useState('');

    const [listaTipoRespuesta, setListaTipoRespuesta] = useState({ data: {} });
    const [selectedTipoRespuesta, setSelectedTipoRespuesta] = useState("");
    const [respuestaTipoRespuesta, setRespuestaTipoRespuesta] = useState(false);

    const [getListaTipoFuncionario, setListaTipoFuncionario] = useState({ data: {} });
    const [selectedTipoFuncionario, setSelectedTipoFuncionario] = useState("");
    const [respuestaTipoFuncionario, setRespuestaTipoFuncionario] = useState(false);

    const location = useLocation()
    const { from } = location.state;

    let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;

    const redirectToRoutes = () => {
        return <Navigate to={`/EntidadInvestigadoLista/`} state={{ from: from }} />;
    }

    function containsSpecialChars(str) {

        const result = global.Constants.TEXT_AREA.CARACTERES_ESPECIALES.split('').some(specialChar => {
            if (str.includes(specialChar)) {
                return true;
            }

            return false;
        });

        return result;
    }


    useEffect(() => {

        async function fetchData() {
            cargarValoresQuejaInterna(from.datosQuejaInterna.attributes);
            obtenerParametros();
           
        }

        fetchData();
    }, []);

    const cargarValoresQuejaInterna = (data) => {

        // Se validan que hayan valores almacenados previamente
        if (data.investigado == "0") {
            var validarElemento = document.getElementById('no');

            if (validarElemento) {
                validarElemento.checked = true;
            }
            setValidar(false);
            setObservaciones(data.comentario_identifica_investigado);

        } else if (data.investigado == "1") {
            var validarElemento = document.getElementById('si');

            if (validarElemento) {
                validarElemento.checked = true;
            }
            setValidar(true);
            setPlanta(data.planta);
            setContratista(data.contratista);
        }

    }

    const obtenerParametros = () => {
        try {

            const data = {
                "data": {
                    "type": 'mas_parametro',
                    "attributes": {
                        "nombre": "minimo_caracteres_textarea|maximo_caracteres_textarea"
                    }
                }
            }


            GenericApi.getByDataGeneric('parametro/parametro-nombre', data).then(
                //buscamos el parametro
                datos => {

                    if (!datos.error) {

                        if (datos["data"].length > 0) {

                            datos["data"].filter(data => data["attributes"]["nombre"].includes('minimo_caracteres_textarea')).map(filteredName => (
                                setMinimoTextArea(filteredName["attributes"]["valor"])
                            ))
                            datos["data"].filter(data => data["attributes"]["nombre"].includes('maximo_caracteres_textarea')).map(filteredName => (
                                setMaximoTextArea(filteredName["attributes"]["valor"])
                            ))
                        }
                        listaRespuestas();
                    } else {
                        setModalState({ title: "SINPROC No " + radicado + " :: Entidad del investigado", message: datos.error.toString(), show: true, redirect: '/EntidadInvestigadoForm', from: { from }, alert: global.Constants.TIPO_ALERTA.ERROR });
                    }
                }
            )
        } catch (error) {
            console.log(error);
        }
    }
    
    const listaRespuestas = () => {

        GenericApi.getGeneric("tipo-respuesta/").then(

            datos => {
                if (!datos.error) {
                    setListaTipoRespuesta(datos);
                    setRespuestaTipoRespuesta(true);
                    listaTiposFuncionarios();
                }
                else {
                    setModalState({ title: "Entidad del investigado Queja Interna", message: datos.error.toString(), show: true, redirect: '/EntidadInvestigadoQuejaInterna', from: {from} });
                }
            }
        )
    }

    const listaTiposFuncionarios = () => {
        window.showSpinner(true);
        GenericApi.getGeneric("tipo-funcionario").then(
            datos => {
                if (!datos.error) {
                    setListaTipoFuncionario(datos);
                    setRespuestaTipoFuncionario(true);
                }
                else {
                    setModalState({ title: "SINPROC No " + radicado + " :: Entidad del investigado Queja Interna", message: datos.error.toString(), show: true, redirect: '/EntidadInvestigadoQuejaInterna', from: { from }, alert: global.Constants.TIPO_ALERTA.ERROR });
                    window.showSpinner(false);
                }
            }
        )
    }

    const selectTipoRespuesta = () => {
        return (
            listaTipoRespuesta.data.map((tipo_respuesta, i) => {
                return (
                    <>
                        <option key={tipo_respuesta.id} value={tipo_respuesta.id}>{tipo_respuesta.attributes.nombre}</option>
                    </>  
                )
            })
        )
    }

    const selectTipoFuncionario = () => {
        return (
            getListaTipoFuncionario.data.map((tipo_funcionario, i) => {
                return (
                    <>
                        <option key={tipo_funcionario.id} value={tipo_funcionario.id}>{tipo_funcionario.attributes.nombre}</option>
                    </>  
                )
            })
        )
    }

    let selectChangeTipoRespuesta = (e) => {
        setSelectedTipoRespuesta(e);
    }


    let selectChangeTipoFuncionario = (e) => {
        setSelectedTipoFuncionario(e);
    }

    const enviarDatos = (datos) => {

        window.showSpinner(true);
        let data;

        if (getValidar == false) {

            data = {
                "data": {
                    "type": "entidad-investigado",
                    "attributes": {
                        "id_proceso_disciplinario": procesoDisciplinarioId,
                        "investigado": false,
                        "comentario_identifica_investigado": observaciones,
                    }
                }
            }

        } else if (getValidar == true) {

            data = {
                "data": {
                    "type": "entidad-investigado",
                    "attributes": {
                        "id_proceso_disciplinario": procesoDisciplinarioId,
                        "investigado": true,
                        "planta": getPlanta,
                        "contratista": getContratista,
                    }
                }
            }

        }

        let idEntidadInvestigado = from.datosQuejaInterna.id;

        GenericApi.updateGeneric('entidad-investigado', idEntidadInvestigado, data).then(
            datos => {
                if (!datos.error) {
                    setModalState({ title: "SINPROC No " + radicado + " :: Entidad del investigado", message: 'Registro Exitoso', show: true, redirect: '/EntidadInvestigadoLista', from: { from }, alert: global.Constants.TIPO_ALERTA.EXITO });
                }
                else {
                    setModalState({ title: "SINPROC No " + radicado + " :: Entidad del investigado", message: datos.error.toString(), show: true, redirect: '/EntidadInvestigadoForm', from: { from }, alert: global.Constants.TIPO_ALERTA.ERROR });
                }
                window.showSpinner(false);
            }
        )
    }

    const handleInputChange = (event) => {
        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;

        if (name == "observaciones") {
            setCountTextArea(value.length)
            setObservaciones(value);
        }
    }


    // COMPONENTE CONFIRMACION TIPO DE CLASIFICADO
    const componenteTipoRespuesta = (tipo_respuesta) => {

        // CONFIRMACION TIPO DE CLASIFICADO = SI
        if (tipo_respuesta === 1) {
            return (
                <>
                    <Formik

                        initialValues={{
                            aceptacion_clasificacion: true,
                        }}
                        enableReinitialize

                        onSubmit={({ resetForm }) => {
                            //aceptarClasificacion();
                        }}
                        >

                        <Form>
                        <div className="block-content">
                            <div className="row">
                                <div className="col-md-12">
                                    <div className="form-group">
                                        <label htmlFor="ingresoTipoExpediente">Confirma la clasificación del expediente? <span className="text-danger">*</span></label>
                                        <select className="form-control" id="ingresoTipoExpediente" name="ingresoTipoExpediente"
                                            value={selectedTipoRespuesta} onChange={e => selectChangeTipoFuncionario(e.target.value)}>
                                            <option value="">Por favor seleccione</option>
                                            {respuestaTipoFuncionario ? selectTipoFuncionario() : null}
                                        </select>
                                    </div>
                                </div>
                            </div>

                           
                        </div>
                            

                        </Form>

                    </Formik>

                </>
            );
        }

        /// CONFIRMACION TIPO DE CLASIFICADO = NO
        else if (tipo_respuesta === 2) {
            return (
                <>
                   
                </>
            );
        }
    };

    // Componente entidad del investigado
    const componenteEntidadInvestigado = (aplica_entidad) => {

        return (
            <>
                {<Spinner />}
                {<ModalGen data={getModalState} />}

                <Formik
                    initialValues={{
                        observaciones: '',
                        validarInvestigado: '',
                        planta: '',
                        contratista: ''
                    }}
                    enableReinitialize
                    validate={(valores) => {
                        let errores = {};

                        if (valores.validarInvestigado == 'true') {
                            setValidar(true);
                            setValidarRegistrar(true);
                            setObservaciones('');

                            if (getPlanta == "" && getContratista == "") {
                                errores.planta = 'Debe chequear al menos una opción';
                                errores.contratista = 'Debe chequear al menos una opción';
                            }

                        } else if (valores.validarInvestigado == 'false') {
                            setValidar(false);
                            setValidarRegistrar(true);
                            setPlanta('');
                            setContratista('');

                            setCountTextArea(observaciones.length);

                            if (!valores.observaciones && !observaciones) {
                                errores.observaciones = 'Debe agregar observaciones';
                            } else if (observaciones.length <= getMinimoTextArea && observaciones != 'NO_APLICA') {
                                errores.observaciones = 'Las observaciones deben tener almenos ' + getMinimoTextArea + ' caracteres';
                            }


                            if (observaciones) {
                                if (containsSpecialChars(observaciones)) {
                                    errores.observaciones = 'Tiene caracteres inválidos';
                                }
                            }

                        }

                        return errores;
                    }}

                    onSubmit={(valores, { resetForm }) => {
                        enviarDatos(valores);
                    }}
                >

                    {({ errors }) => (
                        <Form>
                            <div className="row">
                                <div className="col">
                                    <div className="form-group">
                                        <div className="block-header block-header-default">
                                            <h3 className="block-title" style={{ color: 'black' }}>Datos básicos de la entidad</h3>
                                        </div>

                                        <div className="block block-rounded block-bordered">

                                            <div className="block-content block-content-full">
                                                <div className="row">
                                                    <div className="col-sm-12">
                                                        <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                                            <tbody>
                                                                <tr>
                                                                    <td style={{ width: '17%' }}>
                                                                        <b> Entidad</b>
                                                                    </td>
                                                                    <td colSpan="1" >
                                                                        <Field value={from.datosQuejaInterna.attributes.nombre_entidad} disabled type="text" className="form-control" />
                                                                    </td>
                                                                </tr>
                                                                <tr>
                                                                    <td >
                                                                        <b> Sector</b>
                                                                    </td>
                                                                    <td colSpan="1" >
                                                                        <Field value="ORGANO DE CONTROL" disabled type="text" className="form-control" />
                                                                    </td>
                                                                </tr>
                                                                <tr>
                                                                    <td >
                                                                        <b>  Dirección</b>
                                                                    </td>
                                                                    <td colSpan="1" >
                                                                        <Field value={from.datosQuejaInterna.direccion} disabled type="text" className="form-control" />
                                                                    </td>
                                                                </tr>
                                                                <tr>
                                                                    <td >
                                                                        <b> Correo</b>
                                                                    </td>
                                                                    <td colSpan="1" >
                                                                        <Field value={from.datosQuejaInterna.correo} disabled type="text" className="form-control" />
                                                                    </td>
                                                                </tr>
                                                                <tr>
                                                                    <td >
                                                                        <b>Telefóno</b>
                                                                    </td>
                                                                    <td colSpan="1" >
                                                                        <Field value={from.datosQuejaInterna.telefono} disabled type="text" className="form-control" />
                                                                    </td>
                                                                </tr>
                                                                <tr>
                                                                    <td>
                                                                        <b>Pagina web</b>
                                                                    </td>
                                                                    <td colSpan="1" >
                                                                        <Field value={from.datosQuejaInterna.paginaWeb} disabled type="text" className="form-control" />
                                                                    </td>
                                                                </tr>
                                                            </tbody>
                                                        </table>
                                                        <div style={{ textAlign: '-webkit-center' }}>
                                                            <span><b>Ultima actualización de datos de la entidad:</b> {from.datosQuejaInterna.attributes.created_at}</span>
                                                        </div>

                                                        <div className='mt-4' style={{ textAlign: 'left' }}>
                                                            <span><b>¿Identifica al presunto investigado?</b></span>
                                                        </div>

                                                        <div className="block-content">
                                                            <div className="row">
                                                                <div className="col-md-12">
                                                                    <div className="form-group">
                                                                        <label htmlFor="ingresoTipoExpediente">Confirma la clasificación del expediente? <span className="text-danger">*</span></label>
                                                                        <select className="form-control" id="ingresoTipoExpediente" name="ingresoTipoExpediente"
                                                                            value={selectedTipoRespuesta} onChange={e => selectChangeTipoRespuesta(e.target.value)}>
                                                                            <option value="">Por favor seleccione</option>
                                                                            {respuestaTipoRespuesta ? selectTipoRespuesta() : null}
                                                                        </select>
                                                                    </div>
                                                                </div>
                                                            </div>

                                                            {/*SI APLICA RECLASIFIFCACION*/}
                                                            {selectedTipoRespuesta === '1' ? componenteTipoRespuesta(1) : ''}
                                                            {/*NO APLICA RECLASIFIFCACION*/}
                                                            {selectedTipoRespuesta === '2' ? componenteTipoRespuesta(2) : ''}
                                                        </div>
                                                        
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    </div>

                                    <div className="row">
                                        <div className="col-md-12">
                                            <div className="block-content block-content-full text-right">
                                                {getValidarRegistrar == true
                                                    ?
                                                    <button type="submit" className="btn btn-rounded btn-primary">Registrar</button>
                                                    : null}
                                                <Link to={`/EntidadInvestigadoLista/`} state={{ from: from }}>
                                                    <button type="button" className="btn btn-rounded btn-outline-primary">Cancelar</button>
                                                </Link>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>

                        </Form>
                    )}

                </Formik>
            </>
        );
    };

    // Componente principal
    return (
        <>
            {<Spinner />}
            {<ModalGen data={getModalState} />}

            <div className="content">
                <div className="col-md-12">
                    <div className="block block-rounded block-bordered">
                        <div className="block-content">
                            <nav aria-label="breadcrumb">
                                <ol className="breadcrumb breadcrumb-alt push">
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`} state={{ from: from }}><small>Ramas del proceso</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" aria-current="page" to={`/EntidadInvestigadoLista/`} state={{ from: from }}><small>Entidad del investigado</small></Link></li>
                                    <li class="breadcrumb-item"><small>Validar Queja Interna</small></li>
                                </ol>
                            </nav>
                        </div>
                    </div>
                </div>

                <div className="row">
                    <div className="col-md-12">
                        <div className="block block-themed">
                            <div className="block-header">
                                <h3 className="block-title">Entidad del investigado SINPROC No. {radicado}:: <strong>Validar Queja Interna</strong></h3>
                            </div>
                            <div className="block-content">
                                <div className="form-group">
                                    <div className='text-right'>
                                        <Link to={`/EntidadInvestigadoLista/`} title='Regresar a lista de Entidades Investigado' state={{ from: from }}>
                                            <button type="button" className="btn btn-rounded btn-outline-primary"><i className="fas fa-backward"></i> </button>
                                        </Link>
                                    </div>

                                    {/* QUEJA INTERNA*/}
                                    {componenteEntidadInvestigado()}

                                </div>

                            </div>
                        </div>
                    </div>
                </div>
            </div>

        </>
    );

}
export default EntidadInvestigadoQuejaInterna;
