import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import { useParams } from "react-router";
import DocumentoSiriusApi from '../../Api/Services/DocumentoSiriusApi';

function SoporteRadicadoLista() {


    const [errorApi, setErrorApi] = useState('');

    let { radicado, procesoDisciplinarioId } = useParams();


    useEffect(() => {
        async function fetchData() {

            const data = {
                "data": {
                    "type": "documento_sirius",
                    "attributes": {
                        "id_proceso_disciplinario": "id_proceso_disciplinario",
                        "id_etapa": "id_etapa",
                        "id_fase": "id_fase",
                        "url_archivo": "url_archivo",
                        "nombre_archivo": "nombre_archivo",
                        "estado": "estado",
                        "file64": "file64"
                    }
                }
            }

            DocumentoSiriusApi.getDocumentacionSiriusByIdProDisciplinario(data, procesoDisciplinarioId).then(
                datos => {
                    if (!datos.error) {
                        setSoporteRadicadoLista(datos)
                        setRespuestaSoporteRadicado(true)
                        setSoporteRadicadoListaTotal(datos)
                        setRespuestaSoporteRadicadoTotal(true)
                    }
                    else {
                        setErrorApi(datos.error.toString())
                        window.showModal()
                    }
                    window.showSpinner(false);
                }
            )
        }
        fetchData();
    }, []);


    ///////////////////////////////////////////////////////////
    /////////////////// PARA LA BÚSQUEDA //////////////////////
    ///////////////////////////////////////////////////////////

    const [respuestaSoporteRadicado, setRespuestaSoporteRadicado] = useState(false);
    const [soporteRadicadoLista, setSoporteRadicadoLista] = useState({ data: [], links: [], meta: [] });

    const [respuestaSoporteRadicadoTotal, setRespuestaSoporteRadicadoTotal] = useState(false);
    const [soporteRadicadoListaTotal, setSoporteRadicadoListaTotal] = useState({ data: [], links: [], meta: [] });

    const handleSearch = (event) => {
        try {
            if ((event.target.value.trim().trim() == "") || (event.target.value.trim().length == 0)) {

                setSoporteRadicadoLista(soporteRadicadoListaTotal);
            }
            else {

                setSoporteRadicadoLista(soporteRadicadoListaTotal.data.filter(
                    suggestion => ((suggestion.attributes.nombre_archivo
                        + suggestion.attributes.created_at + suggestion.attributes.id_etapa +
                        suggestion.attributes.id_fase).toLowerCase().includes(event.target.value.toLowerCase()))
                ));
            }
        } catch (error) {
            console.error(error);
        }

    };


    const listaSoporteRadicado = () => {

        if (soporteRadicadoLista.data != null && typeof (soporteRadicadoLista.data) != 'undefined') {
            return (


                soporteRadicadoLista.data.map((documento_sirius, i) => {
                    return (
                        <tr key={documento_sirius.id}>
                            <td className="text-center">{i + 1}</td>
                            <td>{documento_sirius.attributes.id_etapa}</td>
                            <td>{documento_sirius.attributes.id_fase}</td>
                            <td>{documento_sirius.attributes.created_user}</td>
                            <td>{documento_sirius.attributes.created_at}</td>
                            <td>{documento_sirius.attributes.nombre_archivo}</td>
                            <td>{documento_sirius.attributes.estado === true || documento_sirius.attributes.estado === 1 ? 'Activo' : 'Inactivo'}</td>
                            <td>
                                <button className="btn btn-sm btn-light" onClick={() => cambiarEstado(documento_sirius.id, documento_sirius.attributes.id_etapa, i)}><i className="fas fa-toggle-off col"></i></button>
                            </td>
                        </tr>
                    )
                })
            )
        }
        else {
            return (

                soporteRadicadoLista.map((documento_sirius, i) => {
                    return (
                        <tr key={documento_sirius.id}>
                            <td className="text-center">{i + 1}</td>
                            <td>{documento_sirius.attributes.id_etapa}</td>
                            <td>{documento_sirius.attributes.id_fase}</td>
                            <td>{documento_sirius.attributes.created_user}</td>
                            <td>{documento_sirius.attributes.created_at}</td>
                            <td>{documento_sirius.attributes.nombre_archivo}</td>
                            <td>{documento_sirius.attributes.estado === true || documento_sirius.attributes.estado === 1 ? 'Activo' : 'Inactivo'}</td>
                            <td>
                            <button className="btn btn-sm btn-light" onClick={() => cambiarEstado(documento_sirius.id, documento_sirius.attributes.id_etapa, i)}><i className="fas fa-toggle-off col"></i></button>
                            </td>
                        </tr>
                    )
                })
            )
        }

    }

    ///////////////////////////////////////////////////////////
    /////////////////// PARA LA BÚSQUEDA //////////////////////
    ///////////////////////////////////////////////////////////


    const cambiarEstado = (id, id_etapa, positionLista) => {

        setRespuestaSoporteRadicado(false);

        let data;

        data = {
            "data": {
                "type": "documento_sirius",
                "id": id,
                "attributes": {
                    "id_proceso_disciplinario": "id_proceso_disciplinario",
                    "id_etapa": "1",
                    "id_fase": "1",
                    "url_archivo": "www.prueba.com",
                    "nombre_archivo": "nombre_archivo",
                    "file64": "1",
                    "estado": "1"

                }
            }
        }

        DocumentoSiriusApi.updateEstadoDocumentoSirius(data, id).then(
            datos => {
                if (!datos.error) {
                    soporteRadicadoLista.data[positionLista].attributes.estado = datos.data.attributes.estado;
                    setSoporteRadicadoLista(soporteRadicadoLista);
                    setRespuestaSoporteRadicado(true)
                }
                else {
                    setErrorApi(datos.error.toString())
                    window.showModal()
                }
                window.showSpinner(false);
            }
        )

    }


   


    return (
        <><Formik>
            <Form>
                <div className="col-md-12">
                    <div className="block block-rounded block-bordered">
                        <div className="block-content">
                            <nav aria-label="breadcrumb">
                                <ol className="breadcrumb breadcrumb-alt push">
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/${radicado}/${procesoDisciplinarioId}`}><small>Ramas del proceso</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" aria-current="page" to={`/SoporteRadicadoLista/${radicado}/${procesoDisciplinarioId}`}><small>Soporte del radicado</small></Link></li>
                                </ol>
                            </nav>
                        </div>
                    </div>
                </div>

                <div className="block block-themed">
                    <div className="block-header block-header-default">
                        <h3 className="block-title">SINPROC No {radicado} :: Soporte del radicado</h3>
                        <Link to={`/SoporteradicadoForm/${radicado}/${procesoDisciplinarioId}`}>
                            <button type="button" className="btn btn-sm btn-dark mr-1 mb-3"><i class="far fa-plus-square"></i> Agregar Soporte del radicado</button>
                        </Link>
                    </div>

                    <div className="block-content">
                        <div className='row'>

                            <div className='col-md-12'>
                                <div className="form-group ">
                                    <label htmlFor='search'>Buscar: </label>
                                    <Field type="text" id="search" name="search" onChange={handleSearch} className="form-control border border-success" placeholder="Buscar" />

                                </div>
                            </div>
                        </div>
                        <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                            <thead>
                                <tr>
                                    <th className="text-center">No.</th>
                                    <th>Etapa</th>
                                    <th>Fase</th>
                                    <th>Registrado Por</th>
                                    <th>Fecha de registro</th>
                                    <th>Archivo</th>
                                    <th>Estado</th>
                                    <th>Acciones</th>
                                </tr>
                            </thead>
                            <tbody>
                                {respuestaSoporteRadicado ? listaSoporteRadicado() : null}
                            </tbody>
                        </table>

                        <div className="text-right">
                            <Link to={`/RamasProceso/${radicado}/${procesoDisciplinarioId}`}>
                                <button type="button" className="btn btn-sm btn-success mr-1 mb-3"><i class="fas fa-backward"></i> Regresar </button>
                            </Link>
                        </div>

                    </div>
                </div>
            </Form>
        </Formik>
        </>
    );


}
export default SoporteRadicadoLista;
