import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { useLocation , Link } from 'react-router-dom';
import Spinner from '../../Utils/Spinner';
import ModalGen from '../../Utils/Modals/ModalGeneric';
import DataTable from 'react-data-table-component';
import GenericApi from '../../Api/Services/GenericApi';
import { getUser } from '../../../components/Utils/Common';

function Buscador() {

    const location = useLocation()
    const { from } = location.state;

    const paganationPerPages = global.Constants.DATA_TABLE.PAGINATION_PER_PAGE;
    const [getModalState, setModalState] = useState({ title: "", message: "", show: false });
    const [listaPendientes, setListaPendientes] = useState({ data: [], links: [], meta: [] });
    const [getSeach, setSeach] = useState('');
    const [perPage, setPerPage] = useState(global.Constants.DATA_TABLE.PAGINATION_PER_PAGE);
    const [pageActual, setPageActual] = useState(1);
    const [getFechaFiltro, setFechaFiltro] = useState();
    const [nombreUsuario, setNombreUsuario] = useState("");
    const [casosActivos, setCasosActivos] = useState(0);

    const [getExpediente , setExpediente] = useState(false);
    const [getagregarVigencia , setagregarVigencia] = useState(false);
    const [getagregarEstadoDelExpediente , setagregarEstadoDelExpediente] = useState(false);
    const [getagregarUbicacionDelExpediente , setagregarUbicacionDelExpediente] = useState(false);
    const [getagregarNombreDisciplinado , setagregarNombreDisciplinado] = useState(false);
    const [getagregarIdentificacionDisciplinado , setagregarIdentificacionDisciplinado] = useState(false);
    const [getagregarAsuntoDelExpediente , setagregarAsuntoDelExpediente] = useState(false);
    const [getagregarSector , setagregarSector] = useState();
    const [getagregarNombreEntidad , setagregarNombreEntidad] = useState(false);
    const [getagregarNombreQuejoso , setagregarNombreQuejoso] = useState(false);
    const [getagregarIdentificacionQuejoso , setagregarIdentificacionQuejoso] = useState(false);
    const [getagregarTipoQuejoso , setagregarTipoQuejoso] = useState(false);
    const [getagregarEtapaDelExpediente , setagregarEtapaDelExpediente] = useState(false);
    const [getagregarDelegada , setagregarDelegada] = useState(false);

    const [getValorExpediente , setValorExpediente] = useState("");
    const [getValorVigencia , setValorVigencia] = useState("");
    const [getValorEstadoDelExpediente , setValorEstadoDelExpediente] = useState("");
    const [getValorUbicacionDelExpediente , setValorUbicacionDelExpediente] = useState("");
    const [getValorNombreDisciplinado , setValorNombreDisciplinado] = useState("");
    const [getValorIdentificacionDisciplinado , setValorIdentificacionDisciplinado] = useState("");
    const [getValorAsuntoDelExpediente , setValorAsuntoDelExpediente] = useState("");
    const [getValorSector , setValorSector] = useState("");
    const [getValorNombreEntidad , setValorNombreEntidad] = useState("");
    const [getValorNombreQuejoso , setValorNombreQuejoso] = useState("");
    const [getValorIdentificacionQuejoso , setValorIdentificacionQuejoso] = useState("");
    const [getValorTipoQuejoso , setValorTipoQuejoso] = useState("");
    const [getValorEtapaDelExpediente , setValorEtapaDelExpediente] = useState("");
    const [getValorDelegada , setValorDelegada] = useState("");

    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);
            setNombreUsuario(getUser().nombre);
            cargarPendientes(1, paganationPerPages);


        }
        fetchData();
    }, []);

    const enviarDatos = () =>{

        let data = {
            "data":{
                "type":"buscador",
                "attributes": {
                    "n_expediente": getValorExpediente,
                    "Vigencia": getValorVigencia,
                    "estado_del_expediente": getValorEstadoDelExpediente,
                    "ubicacion_del_expediente": getValorUbicacionDelExpediente,
                    "nombre_disciplinado": getValorNombreDisciplinado,
                    "identificacion_disciplinado": getValorIdentificacionDisciplinado,
                    "asunto_del_expediente": getValorAsuntoDelExpediente,
                    "sector": getValorSector,
                    "nombre_entidad": getValorNombreEntidad,
                    "nombre_quejoso": getValorNombreQuejoso,
                    "identificacion_quejoso": getValorIdentificacionQuejoso,
                    "tipo_quejoso": getValorTipoQuejoso,
                    "etapa_del_expediente": getValorEtapaDelExpediente,
                    "delegada": getValorDelegada
                }
            }
        }

        GenericApi.getByDataGeneric('buscador-general', data).then(
            datos => {
                if (!datos.error) {
                    console.log(datos);
                    setListaPendientes(datos)
                }
                else {
                    console.log("Error en la consulta");
                }
            }
        )
        
    }

    const handlePageChange = page => {
        setPageActual(page);
    }

    const handlePerRowsChange = async (newPerPage, page) => {
        setPerPage(newPerPage);
        setPageActual(page);
    }

    const columns = [
        {
            name: 'Delegada',
            selector: pendiente => pendiente.attributes.radicado,
            sortable: true,
            width: "7.7%"
        },
        {
            name: 'Número expediente',
            selector: pendiente => pendiente.attributes.Numero_expediente,
            sortable: true,
            width: "7.7%"
        },
        {
            name: 'Estado expediente',
            selector: pendiente => pendiente.attributes.radicado,
            sortable: true,
            width: "7.7%"
        },
        {
            name: 'Ubicación expediente',
            selector: pendiente => pendiente.attributes.radicado,
            sortable: true,
            width: "7.7%"
        },
        {
            name: 'Entidad origen',
            selector: pendiente => pendiente.attributes.radicado,
            sortable: true,
            width: "7.7%"
        },
        {
            name: 'Nombre quejoso',
            selector: pendiente => pendiente.attributes.radicado,
            sortable: true,
            width: "7.7%"
        },
        {
            name: 'Sector',
            selector: pendiente => pendiente.attributes.radicado,
            sortable: true,
            width: "7.7%"
        },
        {
            name: 'Nombre disciplinados',
            selector: pendiente => pendiente.attributes.radicado,
            sortable: true,
            width: "7.7%"
        },
        {
            name: 'Etapa expediente',
            selector: pendiente => pendiente.attributes.Etapa_expediente,
            sortable: true,
            width: "7.7%"
        },
        {
            name: 'Fecha auto',
            selector: pendiente => pendiente.attributes.radicado,
            sortable: true,
            width: "7.7%"
        },
        {
            name: 'Auto',
            selector: pendiente => pendiente.attributes.radicado,
            sortable: true,
            width: "7.7%"
        },
        {
            name: 'Tipo de auto',
            selector: pendiente => pendiente.attributes.radicado,
            sortable: true,
            width: "7.7%"
        },
        {
            name: 'Asunto del expediente',
            selector: pendiente => pendiente.attributes.radicado,
            sortable: true,
            width: "7.7%"
        },

    ];

    const cargarPendientes = (page, perPage) => {

        
        window.showSpinner(false);
    }
    
    // Agregar inputs a la busqueda
        const agregarExpediente = (value, cheked) =>{
            if(cheked){
                setExpediente(true);
            }else{
                setExpediente(false);
            }
        }

        const agregarVigencia = (value, cheked) =>{
            if(cheked){
                setagregarVigencia(true);
            }else{
                setagregarVigencia(false);
                setValorVigencia("");
            }
        }

        const agregarEstadoDelExpediente = (value, cheked) =>{
            if(cheked){
                setagregarEstadoDelExpediente(true);
            }else{
                setagregarEstadoDelExpediente(false);
            }
        }

        const agregarUbicacionDelExpediente = (value, cheked) =>{
            if(cheked){
                setagregarUbicacionDelExpediente(true);
            }else{
                setagregarUbicacionDelExpediente(false);
            }
        }

        const agregarNombreDisciplinado = (value, cheked) =>{
            if(cheked){
                setagregarNombreDisciplinado(true);
            }else{
                setagregarNombreDisciplinado(false);
            }
        }

        const agregarIdentificacionDisciplinado = (value, cheked) =>{
            if(cheked){
                setagregarIdentificacionDisciplinado(true);
            }else{
                setagregarIdentificacionDisciplinado(false);
            }
        }

        const agregarAsuntoDelExpediente = (value, cheked) =>{
            if(cheked){
                setagregarAsuntoDelExpediente(true);
            }else{
                setagregarAsuntoDelExpediente(false);
            }
        }

        const agregarSector = (value, cheked) =>{
            if(cheked){
                setagregarSector(true);
            }else{
                setagregarSector(false);
            }
        }

        const agregarNombreEntidad = (value, cheked) =>{
            if(cheked){
                setagregarNombreEntidad(true);
            }else{
                setagregarNombreEntidad(false);
            }
        }

        const agregarNombreQuejoso = (value, cheked) =>{
            if(cheked){
                setagregarNombreQuejoso(true);
            }else{
                setagregarNombreQuejoso(false);
            }
        }

        const agregarIdentificacionQuejoso = (value, cheked) =>{
            if(cheked){
                setagregarIdentificacionQuejoso(true);
            }else{
                setagregarIdentificacionQuejoso(false);
            }
        }

        const agregarTipoQuejoso = (value, cheked) =>{
            if(cheked){
                setagregarTipoQuejoso(true);
            }else{
                setagregarTipoQuejoso(false);
            }
        }

        const agregarEtapaDelExpediente = (value, cheked) =>{
            if(cheked){
                setagregarEtapaDelExpediente(true);
            }else{
                setagregarEtapaDelExpediente(false);
            }
        }

        const agregarDelegada = (value, cheked) =>{
            if(cheked){
                setagregarDelegada(true);
            }else{
                setagregarDelegada(false);
            }
        }

        const handleInputChange = (event) => {
            const target = event.target;
            const value = target.type === 'checkbox' ? target.checked : target.value;
            const name = target.name;

            if(name == "n_expediente"){
                setValorExpediente( target.value );
            }else if(name == "Vigencia"){
                setValorVigencia( target.value );
            }else if(name == "estado_del_expediente"){
                setValorEstadoDelExpediente( target.value );
            }else if(name == "ubicacion_del_expediente"){
                setValorUbicacionDelExpediente( target.value );
            }else if(name == "nombre_disciplinado"){
                setValorNombreDisciplinado( target.value );
            }else if(name == "identificacion_disciplinado"){
                setValorIdentificacionDisciplinado( target.value );
            }else if(name == "asunto_del_expediente"){
                setValorAsuntoDelExpediente( target.value );
            }else if(name == "sector"){
                setValorSector( target.value );
            }else if(name == "nombre_entidad"){
                setValorNombreEntidad( target.value );
            }else if(name == "nombre_quejoso"){
                setValorNombreQuejoso( target.value );
            }else if(name == "identificacion_quejoso"){
                setValorIdentificacionQuejoso( target.value );
            }else if(name == "tipo_quejoso"){
                setValorTipoQuejoso( target.value );
            }else if(name == "etapa_del_expediente"){
                setValorEtapaDelExpediente( target.value );
            }else if(name == "delegada"){
                setValorDelegada( target.value );
            }

        }
console.log(listaPendientes);

    return (
        <>
            {<Spinner />}
            {<ModalGen data={getModalState} />}
            <Formik
                initialValues={{
                }}
                enableReinitialize
                validate={(valores) => {
                    
                    let errores = {}

                    return errores;
                }}
                onSubmit={(valores, { resetForm }) => {

                    enviarDatos();
                }}
            >

                {({ errors }) => (
                    <Form>
                        <div className="w2d_block">
                            <nav aria-label="breadcrumb">
                                <ol className="breadcrumb breadcrumb-alt push">
                                    <li className="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                    <li className="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`} state={{ from: from }}><small>Ramas del proceso</small></Link></li>
                                    <li className="breadcrumb-item"> <small>Buscador de Expediente</small></li>
                                </ol>
                            </nav>
                        </div>

                        <div className="block block-themed">
                            <div className="block-header">
                                <h3 className="block-title">Buscador de expediente :: Seleccione los campos a consultar </h3>
                            </div>

                            <div className="block-content">
                                <div className="row">
                                    <div className='col-md-6'>
                                        <h2>Filtros: </h2>
                                    </div>
                                    <div className='col-md-6'>
                                        <h2>Busqueda: </h2>
                                    </div>
                                    <div className="col-md-3">

                                        <div className="custom-control custom-switch custom-control-lg mb-2">
                                            <div>
                                                <input type="checkbox" value="Expediente" className="custom-control-input" id="Expediente" name="Expediente" onChange={e => agregarExpediente(e.target.value , e.target.checked)}/>
                                                <label className="custom-control-label" htmlFor="Expediente">No. expediente</label>
                                            </div>
                                        </div>
                                        <div className="custom-control custom-switch custom-control-lg mb-2">
                                            <div>
                                                <input type="checkbox" value="Vigencia" className="custom-control-input" id="Vigencia" name="Vigencia" onChange={e => agregarVigencia(e.target.value , e.target.checked)}/>
                                                <label className="custom-control-label" htmlFor="Vigencia">Vigencia</label>
                                            </div>
                                        </div>
                                        <div className="custom-control custom-switch custom-control-lg mb-2">
                                            <div>
                                                <input type="checkbox" value="EstadoDelExpediente" className="custom-control-input" id="EstadoDelExpediente" name="EstadoDelExpediente" onChange={e => agregarEstadoDelExpediente(e.target.value , e.target.checked)}/>
                                                <label className="custom-control-label" htmlFor="EstadoDelExpediente">Estado del expediente</label>
                                            </div>
                                        </div>
                                        <div className="custom-control custom-switch custom-control-lg mb-2">
                                            <div>
                                                <input type="checkbox" value="UbicacionDelExpediente" className="custom-control-input" id="UbicacionDelExpediente" name="UbicacionDelExpediente" onChange={e => agregarUbicacionDelExpediente(e.target.value , e.target.checked)}/>
                                                <label className="custom-control-label" htmlFor="UbicacionDelExpediente">Ubicación del expediente</label>
                                            </div>
                                        </div>
                                        <div className="custom-control custom-switch custom-control-lg mb-2">
                                            <div>
                                                <input type="checkbox" value="NombreDisciplinado" className="custom-control-input" id="NombreDisciplinado" name="NombreDisciplinado" onChange={e => agregarNombreDisciplinado(e.target.value , e.target.checked)}/>
                                                <label className="custom-control-label" htmlFor="NombreDisciplinado">Nombre disciplinado</label>
                                            </div>
                                        </div>
                                        <div className="custom-control custom-switch custom-control-lg mb-2">
                                            <div>
                                                <input type="checkbox" value="IdentificacionDisciplinado" className="custom-control-input" id="IdentificacionDisciplinado" name="IdentificacionDisciplinado" onChange={e => agregarIdentificacionDisciplinado(e.target.value , e.target.checked)}/>
                                                <label className="custom-control-label" htmlFor="IdentificacionDisciplinado">Identificación disciplinado</label>
                                            </div>
                                        </div>
                                        <div className="custom-control custom-switch custom-control-lg mb-2">
                                            <div>
                                                <input type="checkbox" value="AsuntoDelExpediente" className="custom-control-input" id="AsuntoDelExpediente" name="AsuntoDelExpediente" onChange={e => agregarAsuntoDelExpediente(e.target.value , e.target.checked)}/>
                                                <label className="custom-control-label" htmlFor="AsuntoDelExpediente">Asunto del expediente</label>
                                            </div>
                                        </div>
                                        
                                        
                                    </div>
                                    <div className="col-md-3">
                                    <div className="custom-control custom-switch custom-control-lg mb-2">
                                            <div>
                                                <input type="checkbox" value="Sector" className="custom-control-input" id="Sector" name="Sector" onChange={e => agregarSector(e.target.value , e.target.checked)}/>
                                                <label className="custom-control-label" htmlFor="Sector">Sector</label>
                                            </div>
                                        </div>
                                        <div className="custom-control custom-switch custom-control-lg mb-2">
                                            <div>
                                                <input type="checkbox" value="NombreEntidad" className="custom-control-input" id="NombreEntidad" name="NombreEntidad" onChange={e => agregarNombreEntidad(e.target.value , e.target.checked)}/>
                                                <label className="custom-control-label" htmlFor="NombreEntidad">Nombre entidad</label>
                                            </div>
                                        </div>
                                        <div className="custom-control custom-switch custom-control-lg mb-2">
                                            <div>
                                                <input type="checkbox" value="NombreQuejoso" className="custom-control-input" id="NombreQuejoso" name="NombreQuejoso" onChange={e => agregarNombreQuejoso(e.target.value , e.target.checked)}/>
                                                <label className="custom-control-label" htmlFor="NombreQuejoso">Nombre quejoso</label>
                                            </div>
                                        </div>
                                        <div className="custom-control custom-switch custom-control-lg mb-2">
                                            <div>
                                                <input type="checkbox" value="IdentificacionQuejoso" className="custom-control-input" id="IdentificacionQuejoso" name="IdentificacionQuejoso" onChange={e => agregarIdentificacionQuejoso(e.target.value , e.target.checked)}/>
                                                <label className="custom-control-label" htmlFor="IdentificacionQuejoso">Identificación quejoso</label>
                                            </div>
                                        </div>
                                        <div className="custom-control custom-switch custom-control-lg mb-2">
                                            <div>
                                                <input type="checkbox" value="TipoQuejoso" className="custom-control-input" id="TipoQuejoso" name="TipoQuejoso" onChange={e => agregarTipoQuejoso(e.target.value , e.target.checked)}/>
                                                <label className="custom-control-label" htmlFor="TipoQuejoso">Tipo quejoso</label>
                                            </div>
                                        </div>
                                        <div className="custom-control custom-switch custom-control-lg mb-2">
                                            <div>
                                                <input type="checkbox" value="EtapaDelExpediente" className="custom-control-input" id="EtapaDelExpediente" name="EtapaDelExpediente" onChange={e => agregarEtapaDelExpediente(e.target.value , e.target.checked)}/>
                                                <label className="custom-control-label" htmlFor="EtapaDelExpediente">Etapa del expediente</label>
                                            </div>
                                        </div>
                                        <div className="custom-control custom-switch custom-control-lg mb-2">
                                            <div>
                                                <input type="checkbox" value="Delegada" className="custom-control-input" id="Delegada" name="Delegada" onChange={e => agregarDelegada(e.target.value , e.target.checked)}/>
                                                <label className="custom-control-label" htmlFor="Delegada">Delegada</label>
                                            </div>
                                        </div>

                                    
                                    </div>
                                    <div className='col-md-6'>
                                        
                                        {getExpediente == true ? (
                                            <div className="form-group">
                                                <label htmlFor="n_expediente">No. expediente</label>
                                                <Field as="input" type="text" className="form-control" id="n_expediente" name="n_expediente" onChange={handleInputChange}></Field>
                                                <ErrorMessage name="n_expediente" component={() => (<span className="text-danger">{errors.n_expediente}</span>)} />
                                            </div>
                                        ) : null}

                                        {getagregarVigencia == true ? (
                                            <div className="form-group">
                                                <label htmlFor="Vigencia">Vigencia</label>
                                                <Field as="input" type="text" className="form-control" id="Vigencia" name="Vigencia" onChange={handleInputChange}></Field>
                                                <ErrorMessage name="Vigencia" component={() => (<span className="text-danger">{errors.Vigencia}</span>)} />
                                            </div>
                                        ) : null}

                                        {getagregarEstadoDelExpediente == true ? (
                                            <div className="form-group">
                                                <label htmlFor="estado_del_expediente">Estado del expediente</label>
                                                <Field as="input" type="text" className="form-control" id="estado_del_expediente" name="estado_del_expediente" onChange={handleInputChange}></Field>
                                                <ErrorMessage name="estado_del_expediente" component={() => (<span className="text-danger">{errors.estado_del_expediente}</span>)} />
                                            </div>
                                        ) : null}

                                        {getagregarUbicacionDelExpediente == true ? (
                                            <div className="form-group">
                                                <label htmlFor="ubicacion_del_expediente">Ubicación del expediente</label>
                                                <Field as="input" type="text" className="form-control" id="ubicacion_del_expediente" name="ubicacion_del_expediente" onChange={handleInputChange}></Field>
                                                <ErrorMessage name="ubicacion_del_expediente" component={() => (<span className="text-danger">{errors.ubicacion_del_expediente}</span>)} />
                                            </div>
                                        ) : null}

                                        {getagregarNombreDisciplinado == true ? (
                                            <div className="form-group">
                                                <label htmlFor="nombre_disciplinado">Nombre disciplinado</label>
                                                <Field as="input" type="text" className="form-control" id="nombre_disciplinado" name="nombre_disciplinado" onChange={handleInputChange}></Field>
                                                <ErrorMessage name="nombre_disciplinado" component={() => (<span className="text-danger">{errors.nombre_disciplinado}</span>)} />
                                            </div>
                                        ) : null}

                                        {getagregarIdentificacionDisciplinado == true ? (
                                            <div className="form-group">
                                                <label htmlFor="identificacion_disciplinado">Identificación disciplinado</label>
                                                <Field as="input" type="text" className="form-control" id="identificacion_disciplinado" name="identificacion_disciplinado" onChange={handleInputChange}></Field>
                                                <ErrorMessage name="identificacion_disciplinado" component={() => (<span className="text-danger">{errors.identificacion_disciplinado}</span>)} />
                                            </div>
                                        ) : null}

                                        {getagregarAsuntoDelExpediente == true ? (
                                            <div className="form-group">
                                                <label htmlFor="asunto_del_expediente">Asunto del expediente</label>
                                                <Field as="input" type="text" className="form-control" id="asunto_del_expediente" name="asunto_del_expediente" onChange={handleInputChange}></Field>
                                                <ErrorMessage name="asunto_del_expediente" component={() => (<span className="text-danger">{errors.asunto_del_expediente}</span>)} />
                                            </div>
                                        ) : null}

                                        {getagregarSector == true ? (
                                            <div className="form-group">
                                                <label htmlFor="sector">Sector</label>
                                                <Field as="input" type="text" className="form-control" id="sector" name="sector" onChange={handleInputChange}></Field>
                                                <ErrorMessage name="sector" component={() => (<span className="text-danger">{errors.sector}</span>)} />
                                            </div>
                                        ) : null}

                                        {getagregarNombreEntidad == true ? (
                                            <div className="form-group">
                                                <label htmlFor="nombre_entidad">Nombre entidad</label>
                                                <Field as="input" type="text" className="form-control" id="nombre_entidad" name="nombre_entidad" onChange={handleInputChange}></Field>
                                                <ErrorMessage name="nombre_entidad" component={() => (<span className="text-danger">{errors.nombre_entidad}</span>)} />
                                            </div>
                                        ) : null}

                                        {getagregarNombreQuejoso == true ? (
                                            <div className="form-group">
                                                <label htmlFor="nombre_quejoso">Nombre quejoso</label>
                                                <Field as="input" type="text" className="form-control" id="nombre_quejoso" name="nombre_quejoso" onChange={handleInputChange}></Field>
                                                <ErrorMessage name="nombre_quejoso" component={() => (<span className="text-danger">{errors.nombre_quejoso}</span>)} />
                                            </div>
                                        ) : null}

                                        {getagregarIdentificacionQuejoso == true ? (
                                            <div className="form-group">
                                                <label htmlFor="identificacion_quejoso">Identificación quejoso</label>
                                                <Field as="input" type="text" className="form-control" id="identificacion_quejoso" name="identificacion_quejoso" onChange={handleInputChange}></Field>
                                                <ErrorMessage name="identificacion_quejoso" component={() => (<span className="text-danger">{errors.identificacion_quejoso}</span>)} />
                                            </div>
                                        ) : null}

                                        {getagregarTipoQuejoso == true ? (
                                            <div className="form-group">
                                                <label htmlFor="tipo_quejoso">Tipo quejoso</label>
                                                <Field as="input" type="text" className="form-control" id="tipo_quejoso" name="tipo_quejoso" onChange={handleInputChange}></Field>
                                                <ErrorMessage name="tipo_quejoso" component={() => (<span className="text-danger">{errors.tipo_quejoso}</span>)} />
                                            </div>
                                        ) : null}

                                        {getagregarEtapaDelExpediente == true ? (
                                            <div className="form-group">
                                                <label htmlFor="etapa_del_expediente">Etapa del expediente</label>
                                                <Field as="input" type="text" className="form-control" id="etapa_del_expediente" name="etapa_del_expediente" onChange={handleInputChange}></Field>
                                                <ErrorMessage name="etapa_del_expediente" component={() => (<span className="text-danger">{errors.etapa_del_expediente}</span>)} />
                                            </div>
                                        ) : null}

                                        {getagregarDelegada == true ? (
                                            <div className="form-group">
                                                <label htmlFor="delegada">Delegada</label>
                                                <Field as="input" type="text" className="form-control" id="delegada" name="delegada" onChange={handleInputChange}></Field>
                                                <ErrorMessage name="delegada" component={() => (<span className="text-danger">{errors.delegada}</span>)} />
                                            </div>
                                        ) : null}


                                        <button type="submit" className="btn btn-primary mb-3" >Buscar</button>
                                    </div>
                                </div>
                            </div>

                        </div>

                    </Form>
                )}
            </Formik>

            <Formik
                initialValues={{
                    rango: ''
                }}
                enableReinitialize
                validate={(valores) => {
                    let errores = {}
                    return errores
                }}
                onSubmit={(valores, { resetForm }) => {

                    enviarDatos(valores);
                }}
            >

                {({ errors }) => (
                    <Form>
                        <div className="block block-themed">
                            <div className="block-header">
                                <h3 className="block-title">Mostrando registros del 0 al 0 de un total de 0 registros</h3>
                            </div>

                            <div className="block-content">
                                <div className='row'>
                                    <div className='col-md-12'>
                                        <div className="form-group ">
                                            <label htmlFor='search'>Buscar: </label>
                                            <Field type="text" id="search" name="search" value={getSeach} onChange={e => setSeach(e.target.value)} onKeyPress={(e) => { e.key === 'Enter' && e.preventDefault(); }} className="form-control border border-success" placeholder="Buscar" />

                                        </div>
                                    </div>
                                </div>

                                <DataTable className="table table-bordered table-striped table-vcenter js-dataTable-full"
                                    columns={columns}
                                    
                                    data={listaPendientes.data.filter((suggestion) => {
                                        if (getSeach === "") {
                                            return suggestion;
                                        } else if (

                                            ((suggestion.attributes.radicado + suggestion.attributes.nombre_tipo_expediente
                                                + suggestion.attributes.vigencia + suggestion.attributes.created_at +
                                                suggestion.attributes.dias_habiles + suggestion.attributes.dias_calendario).toLowerCase().includes(getSeach.toLowerCase()))

                                        ) {
                                            return suggestion;
                                        }
                                    })}
                                    perPage={perPage}
                                    page={pageActual}
                                    pagination
                                    noDataComponent="Sin datos"
                                    paginationTotalRows={listaPendientes.data.length}
                                    onChangePage={handlePageChange}
                                    onChangeRowsPerPage={handlePerRowsChange}
                                    defaultSortFieldId="Nombre"
                                    striped
                                    paginationComponentOptions={{ rowsPerPageText: 'Filas por página' }}
                                    defaultSortAsc={false}
                                />
                            </div>
                        </div>
                    </Form>
                )}
            </Formik>

            
        </>
    );

}

export default Buscador;
