import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import { useParams } from "react-router";
import InfoErrorApi from '../../Utils/InfoErrorApi';
import InfoExitoApi from '../../Utils/InfoExitoApi';
import DocumentoSiriusApi from '../../Api/Services/DocumentoSiriusApi';
import { useLocation } from 'react-router-dom'
import DatosInteresadoApi from '../../Api/Services/DatosInteresadoApi';
import Spinner from '../../Utils/Spinner';
import { Navigate } from "react-router-dom";
import ParametrosMasApi from "../../Api/Services/ParametrosMasApi";
import { ParametroModel } from '../../Models/ParametroModel';

function ComunicacionInteresadoForm() {

    const [respuestaDatosInteresado, setRespuestaDatosInteresado] = useState(false);
    const [DatosInteresadoLista, setDatosInteresadoLista] = useState({ data: [], links: [], meta: [] });
    const [errorApi, setErrorApi] = useState('');
    const [isRedirect, setIsRedirect] = useState(false);


    const [respuestaSoporteRadicado, setRespuestaSoporteRadicado] = useState(false);
    const [soporteRadicadoLista, setSoporteRadicadoLista] = useState({ data: [], links: [], meta: [] });

    const location = useLocation()
    const { from } = location.state
	let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;
	let vigencia = from.vigencia;
    
    let asociacionAraay = [];

    const redirectToRoutes = () => {
        return <Navigate to={`/ComunicacionInteresadoForm/`} state={{ from: new ParametroModel(radicado, procesoDisciplinarioId, vigencia)}}/>;
    }

    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);
            const data = {
                "data": {
                    "type": "interesado",
                    "attributes": {
                        "id_proceso_disciplinario": procesoDisciplinarioId,
                        "tipo_documento": "1",
                        "numero_documento": "1",
                        "primer_nombre": "1",
                        "segundo_nombre": "1",
                        "primer_apellido": "1",
                        "segundo_apellido": "1"
                    }
                }
            }
            //console.log(JSON.stringify(data));
            DatosInteresadoApi.getAllDatosInteresadoByIdProDisciplinario(data, procesoDisciplinarioId).then(
                datos => {
                    if (!datos.error) {

                        setDatosInteresadoLista(datos)
                        setRespuestaDatosInteresado(true)
                        window.showSpinner(false);
                    }
                    else {

                        setErrorApi(datos.error.toString())
                        window.showModal(1)
                        window.showSpinner(false);
                    }

                }
            )

            const dataComunicacion = {
                "data": {
                    "type": "documento_sirius",
                    "attributes": {
                        "id_proceso_disciplinario": "id_proceso_disciplinario",
                        "id_etapa": "id_etapa",
                        "id_fase": "id_fase",
                        "url_archivo": "url_archivo",
                        "nombre_archivo": "nombre_archivo",
                        "estado": "estado",
                        "file64": "file64",
                        "num_folios": "num_folios",
                        "num_radicado": "pendiente",
                        "extension": "doc",
                        "peso": "peso",
                    }
                }
            }

            DocumentoSiriusApi.getDocumentacionSiriusByIdProDisciplinario(dataComunicacion, procesoDisciplinarioId).then(
                datos => {
                    if (!datos.error) {
                        setSoporteRadicadoLista(datos)
                        setRespuestaSoporteRadicado(true)
                        window.showSpinner(false);
                    }
                    else {
                        setErrorApi(datos.error.toString())
                        window.showModal()
                        window.showSpinner(false);
                    }
                    window.showSpinner(false);
                }

            )



        }
        fetchData();
    }, []);

    const selectRadicadosSirius = () => {
        return (
            soporteRadicadoLista.data.map((soporte, i) => {
                return (
                    <option key={soporte.id} value={soporte.id}>{soporte.attributes.num_radicado} | {soporte.attributes.nombre_archivo}</option>
                )
            })
        )
    }
    const handleChange = idInteresado => (event) => {

        let valorRadicado = event.target.value;

        let concat = idInteresado + "|" + valorRadicado;

        if (asociacionAraay.length == 0) {

            asociacionAraay.push(concat)

        }
        else {
            console.log("entra 2")
            let yaExiste = false;

            asociacionAraay.forEach(function(element,index){


                if (idInteresado == element.split("|")[0]) {
                    asociacionAraay[index] =  idInteresado + "|" + valorRadicado;
                    yaExiste = true;
                }
               
            });

           

            if (!yaExiste) {
                asociacionAraay.push(concat)
            }
        }

        //setAsociaciones({ asociaciones: [asociaciones, listado] })
        console.log(asociacionAraay);

    }

    const listaDatosInteresado = () => {

        return (
            DatosInteresadoLista.data.map((datosInteresado, i) => {
                return (
                    <tr key={datosInteresado.id}>

                        <td>{datosInteresado.attributes.primer_apellido} {datosInteresado.attributes.segundo_apellido} {datosInteresado.attributes.primer_nombre} {datosInteresado.attributes.segundo_nombre}</td>
                        <td >
                            <Field name={datosInteresado.id} id={datosInteresado.id} as="select" className="form-control" onChange={handleChange(datosInteresado.id)}>
                                <option value="">Por favor seleccione</option>
                                {respuestaSoporteRadicado ? selectRadicadosSirius() : null}
                            </Field>
                        </td>


                    </tr>
                )
            })
        )
    }

    const enviarDatos = (datos) => {
        console.log(JSON.stringify(asociacionAraay));

    }



    return (
        <>
            <div className="col-md-12">
                <div className="block block-rounded block-bordered">
                    <div className="block-content">
                        <nav aria-label="breadcrumb">
                            <ol className="breadcrumb breadcrumb-alt push">

                                <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`} state={{ from: new ParametroModel(radicado, procesoDisciplinarioId, vigencia)}}><small>Ramas del proceso</small></Link></li>
                                <li class="breadcrumb-item"> <small>Comunicación Interesado(s)</small></li>
                            </ol>
                        </nav>
                    </div>
                </div>
            </div>

            {isRedirect ? redirectToRoutes() : null}
            {<Spinner />}
            {<InfoErrorApi error={errorApi} />}
            {<InfoExitoApi />}
            <Formik
                initialValues={{
                    descripcion: ''
                }}
                enableReinitialize
                validate={(valores) => {
                    let errores = {}
                    /*if(!valores.descripcion){
                        errores.descripcion = 'Debe ingresar un antecedente'
                    }
                    else if(valores.descripcion.length < 10){
                        errores.descripcion = 'Debe ingresar mínimo 10 caracteres'
                    }
                    else if(valores.descripcion.length > 4000){
                        errores.descripcion = 'Debe ingresar mínimo 10 caracteres'
                    }
                    return errores*/
                }}
                onSubmit={(valores, { resetForm }) => {
                    enviarDatos(valores);
                }}
            >


                {({ errors }) => (
                    <Form>
                        <div className="block-header block-header-default">
                            <h3 className="block-title">Número radicado: {radicado}</h3>
                        </div>

                        <div className="block block-rounded block-bordered">

                            <div className="block-content block-content-full">
                                <div className="row">
                                    <div className="col-sm-12">
                                        <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                            <thead>
                                                <tr>
                                                    <th className="text-center">Persona</th>
                                                    <th>Radicado para asociar</th>
                                                </tr>
                                            </thead>
                                            <tbody>
                                                {respuestaSoporteRadicado ? listaDatosInteresado() : null}
                                            </tbody>
                                        </table>
                                    </div>
                                </div>
                            </div>
                        </div>


                        <div className="block-content block-content-full text-right bg-light">
                            <button type="submit" className="btn btn-primary mr-1 mb-3"><i class="far fa-plus-square"></i> Guardar y asociar</button>
                        </div>
                    </Form>
                )}
            </Formik>
        </>
    );


}
