import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import { useParams } from "react-router";
import ClasificacionRadicadoApi from '../../Api/Services/ClasificacionRadicadoApi';

function SoporteRadicadoLista() {

    const [respuestaSoporteRadicado, setRespuestaSoporteRadicado] = useState(false);
    const [soporteRadicadoLista, setSoporteRadicadoLista] = useState({ data: [], links: [], meta: [] });
    const [errorApi, setErrorApi] = useState('');

    let { radicado, procesoDisciplinarioId } = useParams();


    useEffect(() => {
        async function fetchData() {

            const data = {
                "data": {
                    "type": "clasificacion_radicado",
                    "attributes": {
                        "id_proceso_disciplinario": "123123",
                        "id_etapa": "1",
                        "id_fase": "1",
                        "url_archivo": "www.prueba.com",
                        "nombre_archivo": "prueba7.docx",
                        "estado": "1"
                    }
                }
            }

            ClasificacionRadicadoApi.getDocumentacionSiriusByIdProDisciplinario(data, procesoDisciplinarioId).then(
                datos => {
                    if(!datos.error){
                        setSoporteRadicadoLista(datos)
                        setRespuestaSoporteRadicado(true)
                    }
                    else{
                        setErrorApi(datos.error.toString())
                        window.showModal()
                    }
                    window.showSpinner(false);
                }
            )
        }
        fetchData();
    }, []);


    const listaClasificacionRadicado = () => {

        return(
            clasificacionRadicadoLista.data.map((clasificacion_radicado, i) => {
                return (
                    <tr key={clasificacion_radicado.id}>
                        <td className="text-center">{i+1}</td>
                        <td>{ clasificacion_radicado.attributes.expediente.nombre }</td>
                        <td>PENDIENTE</td>
                        <td>PENDIENTE</td>
                        <td>{clasificacion_radicado.attributes.created_at}</td>
                        <td>{clasificacion_radicado.attributes.estado ? 'Activo' : 'Inactivo'}</td>
                        <td>PENDIENTE</td>
                        <td>{clasificacion_radicado.id}</td>
                        <td> 
                            <Link to={`/ClasificacionRadicadoInfo/${radicado}/${clasificacion_radicado.id}`}>
                                <i className="fa fa-fw fa-archive mr-1"></i> Ver Historial
                            </Link></td>
                    </tr>
                )
            })
        )
    }


    return(
        <>
        <div className="col-md-12">
                    <div className="block block-rounded block-bordered">
                        <div className="block-content">
                            <nav aria-label="breadcrumb">
                                <ol className="breadcrumb breadcrumb-alt push">
    
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark"  to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark"  to={`/RamasProceso/${radicado}/${radicado}`}><small>Ramas del proceso</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" aria-current="page"  to={`/ClasificacionRadicadoForm/${radicado}/${radicado}`}><small>Clasificación del radicado</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-primary" aria-current="page"  to={`/ClasificacionRadicadoLista/${radicado}/${radicado}`}><small> Historial</small></Link></li>
                                </ol>
                            </nav>
                        </div>
                    </div>
                </div>

            <div className="block-header block-header-default">
                <h3 className="block-title">Historial clasificaciones del rádicado (RADICADO) - (PENDIENTE NOMBRE): {radicado}</h3>
            </div> 

            <div className="block block-rounded block-bordered">
                        
                <div className="block-content block-content-full">
                    <div className="row">
                        <div className="col-sm-12">
                            <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                <thead>
                                    <tr>
                                        <th className="text-center">No.</th>
                                        <th>Etapa</th>
                                        <th>Registrado Por</th>
                                        <th>Fecha de registro</th>
                                        <th>Nombre documento</th>
                                        <th>Descripcion</th>
                                        <th>Acciones</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    { respuestaClasificacionRadicado ? listaClasificacionRadicado() : null }
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>
            </div> 
        </>
    );

  
}
export default SoporteRadicadoLista;
