import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { Link, } from "react-router-dom";
import InfoErrorApi from '../../Utils/InfoErrorApi';
import InfoExitoApi from '../../Utils/InfoExitoApi';
import ModalGen from '../../Utils/Modals/ModalGeneric';
import GenericApi from '../../Api/Services/GenericApi';
import Spinner from '../../Utils/Spinner';
import { Navigate } from "react-router-dom";
import DataTable from 'react-data-table-component';
import ListaBotones from '../../Utils/ListaBotones';
import '../../Utils/Constants';
import { useLocation } from 'react-router-dom';
import { quitarAcentos } from '../../Utils/Common';

function ActuacionesInactivar() {

    const [errorApi, setErrorApi] = useState('');
    const [isRedirect, setIsRedirect] = useState(false);
    const [getActuacionesActivasListaSearch, setActuacionesActivasListaSearch] = useState({ data: [], links: [], meta: [] });
    const [getSeach, setSeach] = useState('');
    const paganationPerPages = global.Constants.DATA_TABLE.PAGINATION_PER_PAGE;
    const [perPage, setPerPage] = useState(global.Constants.DATA_TABLE.PAGINATION_PER_PAGE);
    const [pageActual, setPageActual] = useState(1);
    const [getEstadoLista, setEstadoLista] = useState('');
    const [getElementArray, setElementArray] = useState([]);
    const [getMostrarBotonAgregar, setMostrarBotonAgregar] = useState(null);
    const [getModalState, setModalState] = useState({ title: "", message: "", show: false });
    const [getCountCantidad, setCountCantidad] = useState(0);
    const [getMuestraBotonFechaParaSemaforo, setMuestraBotonFechaParaSemaforo] = useState(false);
    const [getidMasActuacion, setidMasActuacion] = useState();

    const location = useLocation()
    const { from, selected_id_etapa, nombre, uuid_actuacion, tipoActuacion, estadoActualActuacion } = location.state

    let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;
    let idEtapa = from.idEtapa >= 3 ? from.idEtapa : 3;
    let estado = 1;

    const [getRoutes, setRoutes] = useState({
        crear_registro: "/ActuacionesForm/",
        consultar_registros: "/ActuacionesLista/",
        ver_detalle: "/ActuacionesVer/" + procesoDisciplinarioId + "/" + selected_id_etapa + "/" + estado,
        muestra_atras: true,
        id_etapa: global.Constants.ETAPAS.NINGUNA,
        selected_id_etapa: selected_id_etapa,
        muestra_inactivos: true,
    });


    const handleCallback = (childData) => {
        try {
            window.showSpinner(true);
            setEstadoLista(childData == global.Constants.ESTADOS.INACTIVO ? "Inactivos" : "Activos")
            cargarActuacionesActivas(1, paganationPerPages, childData);
        } catch (error) {

        }
    }

    const columns = [
        {
            name: 'Nombre actuación',
            cell: actuaciones => actuaciones.attributes.nombre_actuacion,
            selector: actuaciones => actuaciones.attributes.nombre_actuacion,
            sortable: true,
            width: "20%"
        },
        {
            name: 'Usuario registrado',
            cell: actuaciones => actuaciones.attributes.usuario.nombre + " " + actuaciones.attributes.usuario.apellido + " (" + actuaciones.attributes.id_dependencia.nombre + ")",
            selector: actuaciones => actuaciones.attributes.created_user,
            sortable: true,
            width: "25%"
        },
        {
            name: 'Usuario aprueba/rechaza',
            cell: actuaciones => actuaciones.attributes.usuario_accion,
            selector: actuaciones => actuaciones.attributes.usuario_accion,
            sortable: true,
            width: "20%"
        },
        {
            name: 'Estado',
            cell: actuaciones => actuaciones.attributes.nombre_estado_actuacion,
            selector: actuaciones => actuaciones.attributes.nombre_estado_actuacion,
            sortable: true,
            width: "10%"
        },
        {
            name: 'Auto',
            cell: actuaciones => actuaciones.attributes.auto,
            selector: actuaciones => actuaciones.attributes.auto,
            sortable: true,
            width: "20%"
        },
        // {
        //     name: 'Acciones',
        //     cell: actuaciones =>
        //         <div className='row'>
        //             <div className='col-4'>
        //                 <Link to={getRoutes.ver_detalle} state={{ from: from, selected_id_etapa: selected_id_etapa, id: actuaciones.id, nombre: actuaciones.attributes.nombre_actuacion, estadoActualActuacion: actuaciones.attributes.nombre_estado_actuacion }} >
        //                     <button type='button' title='Ver actuación' className='btn btn-sm btn-primary' data-toggle="modal" ><i className="fas fa-search"></i></button>
        //                 </Link>
        //             </div>
        //         </div >,
        //     width: "8%"
        // }
    ];

    const redirectToRoutes = () => {
        return <Navigate to={`/ActuacionesLista/`} state={{ from: from, selected_id_etapa: selected_id_etapa }} />;
    }

    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);

            GenericApi.getGeneric('mas_actuaciones/getActuacionesByName/' + nombre + '/' + selected_id_etapa).then(
                datos => {
                    if (!datos.error) {
                        setidMasActuacion(datos.data[0].id)
                        GenericApi.getGeneric('semaforo').then(
                            datosSemaforo => {
                                if (!datosSemaforo.error) {
                                    datosSemaforo.data.forEach(element => {
                                        if(element.attributes.id_mas_actuacion_inicia != null){
                                            if(element.attributes.id_mas_actuacion_inicia.id == datos.data[0].id){
                                                setMuestraBotonFechaParaSemaforo(true);
                                            }
                                        }
                                        
                                    });
                                    window.showSpinner(false);
                                }else{
            
                                }
                            }
                        )
                    } else {

                    }
                }
            )

            setEstadoLista("Activos");
            cargarActuacionesActivas(1, paganationPerPages, global.Constants.ESTADOS.ACTIVO);
        }
        fetchData();
    }, []);

    const cargarActuacionesActivas = (page, perPage, estado) => {

        GenericApi.getGeneric('actuaciones/get-actuaciones-active/' + 1 + '/' + selected_id_etapa).then(
            datos => {
                if (!datos.error) {
                    if (datos["data"] != "") {
                        setActuacionesActivasListaSearch(datos)
                    }
                    window.showSpinner(false);
                } else {
                    setErrorApi(datos.error.toString())
                    window.showModal(1)
                    window.showSpinner(false);
                }

            }
        )
    }

    const handlePageChange = page => {
        setPageActual(page);
        cargarActuacionesActivas(page, perPage);
    }

    const handlePerRowsChange = async (newPerPage, page) => {
        setPerPage(newPerPage);
        setPageActual(page);
        cargarActuacionesActivas(page, newPerPage);
    }

    const handleChangeCheck = ({ selectedRows }) => {

        // Se setea en 0 la cantidad de actuaciones checkeadas para resetear el valor del boton
        setCountCantidad(0);

        // Se valida que haya seleccionado por lo menos un dato
        if (selectedRows) {

            // Se añade al array de los elementos seleccionados
            setElementArray([...selectedRows]);
        }

        // Se setea la cantidad de actuaciones checkeadas
        setCountCantidad(selectedRows.length);
    };

    const inactivarActuaciones = () => {

        // Se activa el loading
        window.showSpinner(true);

        // Se valida cuando sea continuar el valor
        if (getCountCantidad == 0) {

            // Se inactiva el loading
            window.showSpinner(false);

            // Se muestra el modal para redirigir a las transacciones
            setModalState({ title: "SINPROC No " + from.radicado + " :: Actuaciones inactivar", 
            message: "Redirigiendo al siguiente paso...", 
            show: true, 
            redirect: '/SeleccionDeFechaParaSemaforo', 
            from: { from: from, selected_id_etapa: selected_id_etapa, id: uuid_actuacion, nombre: nombre, estadoActualActuacion: estadoActualActuacion, tipoActuacion: tipoActuacion  }, 
            alert: global.Constants.TIPO_ALERTA.EXITO });

            // Se retorna en falso
            return;
        }

        let data = {
            "data": {
                "type": "actuaciones/actuaciones-inactivar",
                "attributes": {
                    "id_actuacion": "1",
                    "usuario_accion": "1",
                    "id_estado_actuacion": "1",
                    "documento_ruta": "1",
                    "estado": "1",
                    "uuid_proceso_disciplinario": "1",
                    "id_etapa": "1",
                    "id_proceso_disciplinario": "1",
                    "data": getElementArray
                }
            }
        }

        GenericApi.addGeneric('actuaciones/actuaciones-inactivar', data).then(
            datos => {
                if (!datos.error) {
                    if (!datos.error) {
                        window.showSpinner(false);

                        const mensaje = datos.OK == 1 ? "Actuaciones Inactivadas con exito" : "Hubo un error al tratar de inactivar las actuaciones";

                        if (datos.OK == 1) {
                            if(getMuestraBotonFechaParaSemaforo){
                                setModalState({ title: "SINPROC No " + from.radicado + " :: Actuaciones inactivar", 
                                message: mensaje, 
                                show: true, 
                                redirect: '/SeleccionDeFechaParaSemaforo', 
                                from: { from: from, selected_id_etapa: selected_id_etapa, id: uuid_actuacion, nombre: nombre, estadoActualActuacion: estadoActualActuacion, tipoActuacion: tipoActuacion }, 
                                alert: global.Constants.TIPO_ALERTA.EXITO });
                            }else{
                                setModalState({ title: "SINPROC No " + from.radicado + " :: Actuaciones inactivar", 
                                message: mensaje, 
                                show: true, 
                                redirect: '/Transacciones', 
                                from: {from: from, selected_id_etapa: selected_id_etapa, id_actuacion: getidMasActuacion}, 
                                alert: global.Constants.TIPO_ALERTA.EXITO });
                            }
                        } else {
                            // console.log("Entro al OK 0");
                            setModalState({ title: "SINPROC No " + from.radicado + " :: Actuaciones inactivar", message: datos.error.toString(), show: true, redirect: '/MisPendientes', from: { from }, alert: global.Constants.TIPO_ALERTA.ERROR });
                        }


                    } else {
                        setModalState({ title: "SINPROC No " + from.radicado + " :: Actuaciones inactivar", message: datos.error.toString(), show: true, redirect: '/MisPendientes', alert: global.Constants.TIPO_ALERTA.ERROR });
                    }
                }
            }
        )

        return false;
    }

    return (
        <>
            {isRedirect ? redirectToRoutes() : null}
            {<Spinner />}
            {<InfoErrorApi error={errorApi} />}
            {<ModalGen data={getModalState} />}

            <Formik>
                <Form>
                    <div className="w2d_block">
                        <nav aria-label="breadcrumb">
                            <ol className="breadcrumb breadcrumb-alt push">
                                <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`} state={{ from: from }}><small>Ramas del proceso</small></Link></li>
                                <li class="breadcrumb-item"> <small>Actuaciones Inactivar</small></li>
                            </ol>
                        </nav>
                    </div>

                    <div className="block block-themed">
                        <div className="block-header">
                            <h3 className="block-title">SINPROC No {radicado} :: <strong>Lista actuaciones a inactivar</strong></h3>
                        </div>
                        <div className="block-content">
                            <div className='row'>
                                <div className='col-md-3'>
                                    <div className="form-group ">
                                        <Field type="text" id="search" name="search" value={getSeach} onChange={e => setSeach(e.target.value)} onKeyPress={(e) => { e.key === 'Enter' && e.preventDefault(); }} className="form-control border border-success" placeholder="Buscar" />
                                    </div>
                                </div>
                                {/* <ListaBotones getRoutes={getRoutes} from={from} mostrarBotonAgregar={getMostrarBotonAgregar != null ? getMostrarBotonAgregar : validacionDeBotonAgregar()} parentCallback={handleCallback} id="botonesNavegacion" name="botonesNavegacion" /> */}
                            </div>

                            <div className="row">
                                <div className="col-sm-12">
                                    <DataTable className="table table-bordered table-striped table-vcenter js-dataTable-full"
                                        columns={columns}
                                        data={getActuacionesActivasListaSearch.data.filter((suggestion) => {

                                            if (getSeach === "") {
                                                return suggestion;
                                            } else if (

                                                ((quitarAcentos(suggestion.attributes.nombre_actuacion)
                                                    + quitarAcentos(suggestion.attributes.created_user) + quitarAcentos(suggestion.attributes.usuario_accion) +
                                                    quitarAcentos(suggestion.attributes.nombre_estado_actuacion) + quitarAcentos(suggestion.attributes.auto)
                                                ).toLowerCase().includes(getSeach.toLowerCase()))

                                            ) {
                                                return suggestion;
                                            }
                                        })}
                                        perPage={perPage}
                                        page={pageActual}
                                        pagination
                                        noDataComponent={global.Constants.DATA_TABLE.SIN_DATOS_TABLE}
                                        paginationTotalRows={getActuacionesActivasListaSearch.length}
                                        onChangePage={handlePageChange}
                                        onChangeRowsPerPage={handlePerRowsChange}
                                        defaultSortFieldId="Nombre"
                                        striped
                                        paginationComponentOptions={{ rowsPerPageText: global.Constants.DATA_TABLE.FILTRO_POR_PAGINA }}
                                        defaultSortAsc={false}
                                        selectableRows
                                        onSelectedRowsChange={handleChangeCheck}
                                    />
                                </div>
                            </div>

                            <div className="block-content block-content-full text-right">
                                <button type="button" className="btn btn-rounded btn-primary" onClick={inactivarActuaciones}><i class="fas fa-save"></i> {getCountCantidad > 0 ? "Inactivar" : "Continuar"}</button>
                                <Link to={`/MisPendientes/`}>
                                    <button type="button" className="btn btn-rounded btn-outline-primary"> Cancelar</button>
                                </Link>
                            </div>
                        </div>
                    </div>
                </Form>
            </Formik>
        </>
    );

}

export default ActuacionesInactivar;
