import React, { useEffect, useState } from 'react';
import { ErrorMessage, Field, Form, Formik } from 'formik';
import AntecedentesApi from '../../Api/Services/AntecedentesApi';
import InfoErrorApi from '../../Utils/InfoErrorApi';
import InfoExitoApi from '../../Utils/InfoExitoApi';
import { Link } from "react-router-dom";
import { useParams } from "react-router";
import { Navigate } from "react-router-dom";
import Spinner from '../../Utils/Spinner';
import ParametrosMasApi from "./../../Api/Services/ParametrosMasApi";
import CierreEtapaApi from '../../Api/Services/CierreEtapaApi';
import { useLocation } from 'react-router-dom'
import ListaBotones from '../../Utils/ListaBotones';
import { hasAccess } from '../../../components/Utils/Common';

function AntecedentesLista() {


    const [errorApi, setErrorApi] = useState('');

    const [getRoutes, setRoutes] = useState('');

    const [isRedirect, setIsRedirect] = useState(false);
    const [listaDependenciaOrigen, setListaDependenciaOrigen] = useState({ data: {} });
    const [respuestaDependenciaOrigen, setDependenciaOrigen] = useState(false);
    const [estadoEtapaCapturaReparto, setEstadoEtapaCapturaReparto] = useState(false);


    const [bandEstadoInactivo, setBandEstadoInactivo] = useState(false);

    const location = useLocation()
    const { from } = location.state
    let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;
    let vigencia = from.vigencia;
    let estadoActividad = from.estadoActividad;
    let id_etapa = from.id_etapa;

    ///////////////////////////////////////////////////////////
    /////////////////// PARA LA BÚSQUEDA //////////////////////
    ///////////////////////////////////////////////////////////

    const [getRespuestaAntecedentes, setRespuestaAntecedentes] = useState(false);
    const [getAntecedentesLista, setAntecedentesLista] = useState({ data: [], links: [], meta: [] });

    const [respuestaAntecedentesTotal, setRespuestaAntecedentesTotal] = useState(false);
    const [getAntecedentesListaTotal, setAntecedentesListaTotal] = useState({ data: [], links: [], meta: [] });

    const handleSearch = (event) => {
        try {
            if ((event.target.value.trim().trim() == "") || (event.target.value.trim().length == 0)) {

                setAntecedentesLista(getAntecedentesListaTotal);
            }
            else {

                setAntecedentesLista(getAntecedentesListaTotal.data.filter(
                    suggestion => ((suggestion.attributes.descripcion
                        + suggestion.attributes.nombre_dependencia + suggestion.attributes.nombre_etapa +
                        suggestion.attributes.estado + suggestion.attributes.fecha_registro).toLowerCase().includes(event.target.value.toLowerCase()))
                ));
            }
        } catch (error) {
            console.error(error);
        }

    };




    const cambiarEstado = (datos) => {

        setRespuestaAntecedentes(false);

        let data;

        console.log(datos);

        data = {
            "data": {
                "type": 'antecedente',
                "attributes": {
                    "antecedentes": 3,
                    "descripcion": "descripcion",
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "fecha_registro": Date.now(),
                    "created_user": "created_user",
                    "estado": "1",

                }
            }
        }

        /*AntecedentesApi.updateAntecedente(data, id).then(
            datos => {
                if (!datos.error) {
                    antecedentesLista.data[positionLista].attributes.estado = datos.data.attributes.estado;


                    data = {
                        "data": {
                            "type": 'antecedente',
                            "attributes": {
                                "antecedentes": 3,
                                "descripcion": "descripcion",
                                "id_proceso_disciplinario": procesoDisciplinarioId,
                                "fecha_registro": Date.now(),
                                "created_user": "created_user",
                                "estado": !datos.data.attributes.estado,
            
                            }
                        }
                    }

                    AntecedentesApi.getAllAntecedente(data, procesoDisciplinarioId).then(
                        datos => !datos.error ? (
                            setAntecedentesListaTotal(datos), 
                            setRespuestaAntecedentesTotal(true),
                            setAntecedentesLista(datos), 
                            setBandEstadoInactivo(true),
                            setRespuestaAntecedentes(true)) : window.showModal(1)
                    )

                }
                else {
                    setErrorApi(datos.error.toString())
                    window.showModal()
                }
               
            }
        )*/

    }






    ///////////////////////////////////////////////////////////
    /////////////////// PARA LA BÚSQUEDA //////////////////////
    ///////////////////////////////////////////////////////////

    useEffect(() => {
        async function fetchData() {

            const data = {
                "data": {
                    "type": 'antecedente',
                    "attributes": {
                        "antecedentes": "antecedentes",
                        "descripcion": "abc",
                        "id_proceso_disciplinario": procesoDisciplinarioId,
                        "fecha_registro": Date.now(),
                        "id_dependencia": -1,
                        "estado": estadoActividad
                    }
                }
            }


            if (hasAccess('CR_Antecedente', 'Consultar')) {
                AntecedentesApi.getAllAntecedente(data, procesoDisciplinarioId).then(
                    datos => !datos.error ? (setAntecedentesListaTotal(datos), setRespuestaAntecedentesTotal(true),
                        setAntecedentesLista(datos), setRespuestaAntecedentes(true)) : window.showModal(1)
                )
            }



            /*ParametrosMasApi.getAllTiposDependenciaOrigen().then(
                datos => !datos.error ? (setListaDependenciaOrigen(datos), setDependenciaOrigen(true)) : window.showModal(1)
            )*/


            const dataLink = {
                id_etapa: 1,
                id_fase: 1,
                crear_registro: "AntencentesForm",
                consultar_registros: "AntencentesLista",
                adjuntar_documento: "SoporteRadicadoForm",
                repositorio_documentos: ""
            }

            setRoutes(dataLink);



            // VALIDA CIERRE DE ETAPA EVALUACION
            const dataCierreEtapa = {

                "data": {
                    "type": "cerrar_etapa",
                    "attributes": {
                        "id_proceso_disciplinario": "id_proceso_disciplinario",
                        "id_etapa": "id_etapa",
                        "id_delegada": "id_delegada",
                        "id_funcionario": "id_funcionario"
                    }
                }

            }


            CierreEtapaApi.getCierreEtapaByIdProDisciplinario(dataCierreEtapa, procesoDisciplinarioId, 1).then(
                datos => {
                    if (!datos.error) {
                        if (datos["data"].length > 0) {
                            setEstadoEtapaCapturaReparto(true);
                        }
                    }
                    else {
                        setErrorApi(datos.error.toString())
                        window.showModal(1)
                    }
                }
            )

        }
        fetchData();
    }, []);


    const listaAntecedentes = () => {

        if (getAntecedentesLista.data != null && typeof (getAntecedentesLista.data) != 'undefined') {
            return (

                getAntecedentesLista.data.map((antecedente, i) => {
                    return (
                        cargarTabla(antecedente, i)
                    )
                })
            )
        }
        else {
            return (

                getAntecedentesLista.map((antecedente, i) => {
                    cargarTabla(antecedente, i)
                })
            )
        }

    }

    const cargarTabla = (antecedente, i) => {

        from.idAntecedente = antecedente.id;

        return (
            <tr key={antecedente.id}>
                <td style={{ width: "3%" }} className="text-center">{i + 1}</td>
                <td style={{ width: "15%" }}>
                    {antecedente.attributes.created_user}<br />
                    <strong>Etapa: </strong>{antecedente.attributes.etapa.nombre}
                </td>
                <td style={{ width: "15%" }}>{antecedente.attributes.fecha_registro}</td>
                <td style={{ width: "15%" }}>{antecedente.attributes.nombre_dependencia}</td>
                <td style={{ width: "30%" }} data-toggle="popover" data-placement="top" title={antecedente.attributes.descripcion}>{antecedente.attributes.descripcion_corta}</td>
                <td style={{ width: "15%" }}>
                    <>
                        {
                            (hasAccess('CR_Antecedente', 'Inactivar')) ? (
                                <div>
                                    <button type='button' title='Consultar Antecedente' className='btn btn-sm btn-primary' data-toggle="modal" data-target={'#modal-consultar-detalle' + i}><i class="fas fa-search"></i></button>
                                    {
                                        (antecedente.attributes.estado == 0) ? (
                                            <Link to={`/AntencentesCambiarEstadoForm/`} state={{ from: from }}>
                                                <button type='button' title='Activar Antecedente' className='btn btn-sm btn-success' data-toggle="modal" data-target={'#modal-cambiar-estado' + i}><i className="fas fa-plus-circle"></i></button>
                                            </Link>
                                        ) : null
                                    }

                                    {
                                        (antecedente.attributes.estado == 1) ? (
                                            <Link to={`/AntencentesCambiarEstadoForm/`} state={{ from: from }}>
                                                <button type='button' title='Inactivar Antecedente' className='btn btn-sm btn-danger'><i className="fas fa-minus-circle"></i></button>
                                            </Link>


                                        ) : null
                                    }
                                </div>
                            ) : null
                        }
                    </>
                </td>

                <div class="modal" id={'modal-consultar-detalle' + i} tabindex="-1" role="dialog" aria-labelledby="modal-block-normal" aria-hidden="true">
                    <div class="modal-dialog" role="document">
                        <div class="modal-content">
                            <div class="block block-themed block-transparent mb-0">
                                <div class="block-header bg-primary-dark">
                                    <h3 class="block-title">Descripción del antecedente</h3>
                                    <div class="block-options">
                                        <button type="button" class="btn-block-option" data-dismiss="modal" aria-label="Close">
                                            <i class="fa fa-fw fa-times"></i>
                                        </button>
                                    </div>
                                </div>
                                <div class="block-content">
                                    {antecedente.attributes.descripcion}
                                </div>
                                <div class="block-content block-content-full text-right bg-light">
                                    <button type="button" class="btn btn-sm btn-primary" data-dismiss="modal">Cerrar</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>

            </tr>

        )
    }


    return (
        <> <Spinner />
            {<InfoErrorApi error={errorApi} />}
            {<InfoExitoApi />}

            <Formik>
                <Form>
                    <div className="col-md-12">
                        <div className="block block-rounded block-bordered">
                            <div className="block-content">
                                <nav aria-label="breadcrumb">
                                    <ol className="breadcrumb breadcrumb-alt push">

                                        <li className="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                        <li className="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`} state={{ from: from }}><small>Ramas del proceso</small></Link></li>
                                        <li className="breadcrumb-item"> <small>Antecedentes</small></li>
                                    </ol>
                                </nav>
                            </div>
                        </div>
                    </div>


                    <div className="block block-themed">
                        <div className="block-header">
                            {from.estadoActividad}
                            <h3 className="block-title">SINPROC No {radicado} :: Antecedentes</h3>
                        </div>
                        <div className="block-content">
                            <>
                                {
                                    (hasAccess('CR_Antecedente', 'Consultar')) ? (
                                        <div className='row'>

                                            <div className='col-md-3'>
                                                <div className="form-group ">
                                                    <Field type="text" id="search" name="search" onChange={handleSearch} onKeyPress={(e) => { e.key === 'Enter' && e.preventDefault(); }} className="form-control border border-success" placeholder="Buscar" />
                                                </div>
                                            </div>

                                            {ListaBotones(getRoutes)}

                                        </div>
                                    ) : null
                                }

                                {
                                    (hasAccess('CR_Antecedente', 'Consultar')) ? (
                                        <div className='col-md-12'>
                                            <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                                <thead>
                                                    <tr>
                                                        <th className="text-center">No</th>
                                                        <th>Registrado por</th>
                                                        <th>Fecha registro</th>
                                                        <th>Dependencia</th>
                                                        <th>Descripción</th>
                                                        <th>Acciones</th>
                                                    </tr>
                                                </thead>
                                                <tbody>
                                                    {getRespuestaAntecedentes ? listaAntecedentes() : null}
                                                </tbody>
                                            </table>
                                        </div>
                                    ) : null
                                }
                            </>

                        </div>
                    </div>

                </Form>
            </Formik>
        </>
    );
}


export default AntecedentesLista;
