                                <label>Horas para termino</label>  <span className="text-danger">*</span>
                                <Field type="text" id="horasTermino" name="horasTermino" className="form-control" placeholder="" />
                            </div>

                            <div className="block-content block-content-full text-right">
                                <button type="submit" className="btn btn-primary"><i className="fas fa-save"></i> Registrar</button>

                                <Link to={`/ValidarClasificacionRadicadoLista/${radicado}/${procesoDisciplinarioId}`}>
                                    <button type="button" className="btn btn-danger"><i class="fas fa-window-close"></i> Cancelar</button>
                                </Link>

                            </div>

                        </Form>
                    </Formik>
                </>
            )

        }

    };


    const componenteTipoQueja = (tipo_expediente) => {

        return (tipo_expediente);

    }


    return (
        <>

            {isRedirect ? redirectToRoutes() : null}
            <Spinner />
            {<InfoErrorApi error={errorApi} />}
            {<InfoExitoApi />}
            <div className="row">

                <div className="col-md-12">
                    <div className="block block-rounded block-bordered">
                        <div className="block-content">
                            <nav aria-label="breadcrumb">
                                <ol className="breadcrumb breadcrumb-alt push">
                                    <li className="breadcrumb-item"><Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                    <li className="breadcrumb-item"><Link underline="hover" className="text-dark" to={`/RamasProceso/${radicado}/${procesoDisciplinarioId}`}><small>Ramas del proceso</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" aria-current="page" to={`/ValidarClasificacionRadicadoLista/${radicado}/${procesoDisciplinarioId}`}><small>Validar Clasificacion</small></Link></li>
                                    <li className="breadcrumb-item"><small>Confirmar clasificación</small></li>
                                </ol>
                            </nav>
                        </div>
                    </div>
                </div>

                <div className="col-md-12">
                    <div className="block block-themed">
                        <div className="block-header">
                            <h3 className="block-title">SINPROC No {radicado} :: Validar Clasificación</h3>
                        </div>
                        <div className="block-content">
                            <div className="row">
                                <div className="col-md-12">
                                    <div className="form-group">
                                        <label htmlFor="ingresoTipoExpediente">Confirma la clasificación del expediente? <span className="text-danger">*</span></label>
                                        <select className="form-control" id="ingresoTipoExpediente" name="ingresoTipoExpediente"
                                            value={selectedTipoRespuesta} onChange={e => selectChangeTipoRespuesta(e.target.value)}>
                                            <option value="">Por favor seleccione</option>
                                            {respuestaTipoRespuesta ? selectTipoRespuesta() : null}
                                        </select>
                                    </div>
                                </div>
                            </div>

                            {/*DERECHO DE PETICION*/}
                            {selectedTipoRespuesta === '1' ? componenteTipoRespuesta(1) : ''}
                            {/*PODER REFERENTE A SOLIICTUD*/}
                            {selectedTipoRespuesta === '2' ? componenteTipoRespuesta(2) : ''}


                        </div>
                    </div>
                </div>
            </div>


        </>
    );

}
export default ValidarClasificacionRadicadoForm;
