import axios from 'axios'

const baseUrl = process.env.REACT_APP_API_URL;

const headers = {
    'Content-Type': 'application/vnd.api+json',
    'Accept': 'application/vnd.api+json'
}

async function addDocumentoSirius(data){

    const url = baseUrl + "documento-sirius";
   
    console.log(url);

    return await axios.post(url, data, {
        headers: headers
    })
    .then(response => {
        console.log(data);
        return response.data;
    })
    .catch(error => {
        console.log(error);
        return {estado: false, error: error}
    });
}


async function getDocumentacionSiriusByIdProDisciplinario(data, procesoDisciplinarioId){

    const url = baseUrl + "clasificacion-radicado/get-clasificacion-radicado/" + procesoDisciplinarioId;
   
    return await axios.post(url, data, {
        headers: headers
    })
    .then(response => {

        return response.data;
    })
    .catch(error => {
        //console.log(error);
        return {estado: false, error: error}
    });
}




export default { addDocumentoSirius }
