import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import { useParams } from "react-router";
import InfoErrorApi from '../../Utils/InfoErrorApi';
import InfoExitoApi from '../../Utils/InfoExitoApi';
import DatosInteresadoApi from '../../Api/Services/DatosInteresadoApi';
import Spinner from '../../Utils/Spinner';
import { Navigate } from "react-router-dom";
import ParametrosMasApi from "../../Api/Services/ParametrosMasApi";

function ComunicacionQuejosoForm() {

    const [respuestaDatosInteresado, setRespuestaDatosInteresado] = useState(false);
    const [DatosInteresadoLista, setDatosInteresadoLista] = useState({ data: [], links: [], meta: [] });
    const [errorApi, setErrorApi] = useState('');
    const [isRedirect, setIsRedirect] = useState(false);

    const [listaRadicadosSirius, setListaRadicadosSirius] = useState({ data: {} });
    const [respuestaRadicadosSirius, setRespuestaRadicadosSirius] = useState(false);

    let { radicado, procesoDisciplinarioId } = useParams();

    const redirectToRoutes = () => {
        return <Navigate to={`/ComunicacionQuejosoForm/${procesoDisciplinarioId}`} />;
    }

    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);
            const data = {
                "data": {
                    "type": "interesado",
                    "attributes": {
                        "id_proceso_disciplinario": procesoDisciplinarioId,
                        "tipo_documento": "1",
                        "numero_documento": "1",
                        "primer_nombre": "1",
                        "segundo_nombre": "1",
                        "primer_apellido": "1",
                        "segundo_apellido": "1"
                    }
                }
            }
            //console.log(JSON.stringify(data));
            DatosInteresadoApi.getAllDatosInteresadoByIdProDisciplinario(data, procesoDisciplinarioId).then(
                datos => {
                    if (!datos.error) {

                        setDatosInteresadoLista(datos)
                        setRespuestaDatosInteresado(true)
                    }
                    else {

                        setErrorApi(datos.error.toString())
                        window.showModal(1)
                    }
                    window.showSpinner(false);
                }
            )

        }
        fetchData();
    }, []);

    const selectRadicadosSirius = () => {
        /*return(
            listaRadicadosSirius.data.map((tient, i) => {
                return (
                    <option key={tient.id} value={tient.id}>{tient.attributes.nombre}</option>
                )
            })
        )*/
    }


    const listaDatosInteresado = () => {

        return (
            DatosInteresadoLista.data.map((datosInteresado, i) => {
                return (
                    <tr key={datosInteresado.id}>

                        <td>{datosInteresado.attributes.primer_apellido} {datosInteresado.attributes.segundo_apellido} {datosInteresado.attributes.primer_nombre} {datosInteresado.attributes.segundo_nombre}</td>
                        <td>
                            <Field as="select" className="form-control" id="tipoEntidad" name="tipoEntidad">
                                <option value="">Por favor seleccione</option>
                                {respuestaRadicadosSirius ? selectRadicadosSirius() : null}
                            </Field>
                        </td>


                    </tr>
                )
            })
        )
    }


    return (
        <>
            <div className="col-md-12">
                <div className="block block-rounded block-bordered">
                    <div className="block-content">
                        <nav aria-label="breadcrumb">
                            <ol className="breadcrumb breadcrumb-alt push">

                                <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/${radicado}/${procesoDisciplinarioId}`}><small>Ramas del proceso</small></Link></li>
                                <li class="breadcrumb-item"> <Link underline="hover" className="text-primary" aria-current="page" to={`/ComunicacionQuejosoForm/${radicado}/${procesoDisciplinarioId}`}><small>Comunicación quejoso(s)</small></Link></li>
                            </ol>
                        </nav>
                    </div>
                </div>
            </div>

            {isRedirect ? redirectToRoutes() : null}
            <Spinner />
            {<InfoErrorApi error={errorApi} />}
            {<InfoExitoApi />}
            <Formik
                    initialValues={{
                        descripcion: ''
                    }}
                    enableReinitialize
                    validate={(valores) => {
                        let errores = {}
                        /*if(!valores.descripcion){
                            errores.descripcion = 'Debe ingresar un antecedente'
                        }
                        else if(valores.descripcion.length < 10){
                            errores.descripcion = 'Debe ingresar mínimo 10 caracteres'
                        }
                        else if(valores.descripcion.length > 4000){
                            errores.descripcion = 'Debe ingresar mínimo 10 caracteres'
                        }
                        return errores*/
                    }}
                    onSubmit={(valores, {resetForm}) => {
                        //enviarDatos(valores);
                    }}
                >

                
                {({ errors }) => (
                    <Form>
                        <div className="block-header block-header-default">
                            <h3 className="block-title">Número radicado: {radicado}</h3>
                        </div>

                        <div className="block block-rounded block-bordered">

                            <div className="block-content block-content-full">
                                <div className="row">
                                    <div className="col-sm-12">
                                        <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                            <thead>
                                                <tr>
                                                    <th className="text-center">Persona</th>
                                                    <th>Radicado SIRIUS para asociar</th>
                                                </tr>
                                            </thead>
                                            <tbody>
                                                {respuestaDatosInteresado ? listaDatosInteresado() : null}
                                            </tbody>
                                        </table>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </Form>
                )}
            </Formik>
        </>
    );


}
export default ComunicacionQuejosoForm;
