import React, { useEffect, useState } from 'react';
import { useLocation, Link } from 'react-router-dom';
import { hasAccess } from '../../Utils/Common';
import Spinner from '../../Utils/Spinner';
import GenericApi from '../../Api/Services/GenericApi';

function ListaAccionesForm() {
    const location = useLocation();
    const { from } = location.state;
    const [getdDataTransacciones, setdDataTransacciones] = useState("");

    //Funcion principal
    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);
            getAdminTransacciones();
        }
        fetchData();
    }, []);

    const getAdminTransacciones = () =>{
        GenericApi.getGeneric('mas_transacciones/get-all-transacciones-ordenadas').then(
            datos => {
                if (!datos.error) {
                    setdDataTransacciones(datos);
                    window.showSpinner(false);
                }else{

                }
            }
        )
        
    }

    return (
        
        <>
        {<Spinner />}
        {getdDataTransacciones != "" ? (
            
            <div>
                <table className="table table-striped table-borderless table-vcenter">
                    <thead className="thead-light">
                        <tr>
                            <th colSpan="2">Acciones
                                <div className="text-muted mt-2 mb-1">(seleccione una acci√≥n para continuar)</div></th>
                        </tr>
                    </thead>
                    <tbody>
                        
                        <tr>
                            <td className="text-center">
                                <i className="fa fa-check text-success fa-2x"></i>
                            </td>
                            <td>
                                <Link to={'/MisPendientes'} className="font-size-h5 font-w600" >
                                    
                                    {getdDataTransacciones.data[0].attributes.titulo}
                                </Link>
                                <div className="text-muted mt-2 mb-1">{getdDataTransacciones.data[0].attributes.descripcion}</div>
                            </td>
                        </tr>
                        {(hasAccess('Transacciones', 'EnviarUsuarioDependencia')) ? (
                            <tr>
                                <td className="text-center">
                                    <i className="fa fa-user text-success fa-2x"></i>
                                </td>
                                <td>
                                    <Link to={'/EnviaraAlguienDeMiDependencia'} state={{ from: from }} className="font-size-h5 font-w600" >
                                    {getdDataTransacciones.data[1].attributes.titulo}
                                    </Link>
                                    <div className="text-muted mt-2 mb-1">{getdDataTransacciones.data[1].attributes.descripcion}</div>
                                </td>
                            </tr>
                        ) : null}
                        {(hasAccess('Transacciones', 'EnviarAJefe')) ? (
                            <tr>
                                <td className="text-center">
                                    <i className="fa fa-check-double text-success fa-2x"></i>
                                </td>
                                <td>
                                    <Link to={`/EnviarAlJefeDeMiDependencia`} state={{from: from}} className="font-size-h5 font-w600" >
                                    {getdDataTransacciones.data[2].attributes.titulo}
                                    </Link>
                                    <div className="text-muted mt-2 mb-1">{getdDataTransacciones.data[2].attributes.descripcion}</div>
                                </td>
                            </tr>
                        ) : null}
                        {(hasAccess('Transacciones', 'EnviarOtraDependencia')) ? (
                            <tr>
                                <td className="text-center">
                                    <i className="fa fa-paper-plane text-success fa-2x"></i>
                                </td>
                                <td>
                                    <Link to={`/EnviarAotraDependencia`} state={{from: from}} className="font-size-h5 font-w600" >
                                    {getdDataTransacciones.data[3].attributes.titulo}
                                    </Link>
                                    <div className="text-muted mt-2 mb-1">{getdDataTransacciones.data[3].attributes.descripcion}</div>
                                </td>
                            </tr>
                        ) : null}
                        {(hasAccess('Transacciones', 'RegresarUltimoUsuario')) ? (
                            <tr>
                                <td className="text-center">
                                    <i className="fa fa-undo-alt text-success fa-2x"></i>
                                </td>
                                <td>
                                    <Link to={'/EnviarAlAnteriorUsuario'} state={{from: from}} className="font-size-h5 font-w600" >
                                    {getdDataTransacciones.data[4].attributes.titulo}
                                    </Link>
                                    <div className="text-muted mt-2 mb-1">{getdDataTransacciones.data[4].attributes.descripcion}</div>
                                </td>
                            </tr>
                        ) : null}
                    </tbody>
                </table>
            </div>

        ) : null}
        
        </>
    );
}

export default ListaAccionesForm;
