import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import InfoErrorApi from '../Utils/InfoErrorApi';
import GenericApi from '../Api/Services/GenericApi';
import Spinner from '../Utils/Spinner';
import DataTable from 'react-data-table-component';

function CiudadLista() {
    const [ciudadLista, setCiudadLista] = useState({ data: [] });
    const [ciudadListaTotal, setCiudadListaTotal] = useState({ data: [], links: [], meta: [] });

    const [perPage, setPerPage] = useState(10);
    const [pageActual, setPageActual] = useState(1);
    const columns = [
        {
            name: 'Nombre',
            selector: ciudad => ciudad.attributes.nombre,
            sortable: true,
            width: "30%"
        },
        {
            name: 'Departammento',
            selector: ciudad => ciudad.attributes.departamento.nombre,
            sortable: true,
            width: "30%"
        },
        {
            name: 'C칩digo Dane',
            selector: ciudad => ciudad.attributes.codigo_dane,
            sortable: true,
        },
        {
            name: 'Estado',
            selector: ciudad => (ciudad.attributes.estado == "1" ? 'Activo' : 'Inactivo'),
            sortable: true,
        },
        {
            name: 'Acciones',


            cell: row => <div><Link to={`${row.id}`}>
                <button type="button" className="btn btn btn-primary" title='Editar'>
                    <i className="fa fa-fw fa-edit"></i>
                </button>
            </Link></div>
        }
    ];

    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);
            cargarCiudad(1, 10);
        }
        fetchData();
    }, []);

    const cargarCiudad = (page, perPage) => {
      
        GenericApi.getAllGeneric('ciudad/ciudad-paginate' + '/' + page + '/' + perPage).then(
            datos => {
                if (!datos.error) {
                    setCiudadLista(datos);
                    setCiudadListaTotal(datos);
                    window.showSpinner(false);
                }
                else {
                    window.showModal();
                    window.showSpinner(false);
                }

            }
        )
    }



    const ciudad = () => {
        return (
            ciudadLista.data.map((ciudad, i) => {
                return (
                    <tr key={ciudad.id}>

                        <td style={{ width: "50%" }}>
                            {ciudad.attributes.nombre}
                        </td>
                        <td style={{ width: "20%" }}>
                            {ciudad.attributes.departamento.nombre}
                        </td>
                        <td style={{ width: "20%" }}>
                            {ciudad.attributes.codigo_dane}
                        </td>
                        <td>
                            {ciudad.attributes.estado == "1" ? 'Activo' : 'Inactivo'}
                        </td>
                        <td>
                            <Link to={`${ciudad.id}`}>
                                <button type="button" className="btn btn btn-primary" title='Editar'>
                                    <i className="fa fa-fw fa-edit"></i>
                                </button>
                            </Link>

                        </td>
                    </tr>
                )
            })
        )
    }

    const handleSearch = (event) => {
        try {
            if ((event.target.value.trim().trim() == "") || (event.target.value.trim().length == 0)) {
                setCiudadLista(ciudadListaTotal);
            }
            else {
                var filteredData = {
                    data: ciudadListaTotal.data.filter(
                        suggestion => ((suggestion.id
                            + suggestion.attributes.nombre
                            + suggestion.attributes.departamento.nombre
                            + (suggestion.attributes.codigo_dane)
                            + (suggestion.attributes.estado == "1" ? 'Activo' : 'Inactivo')).toLowerCase().includes(event.target.value.toLowerCase()))
                    )
                };
                setCiudadLista(filteredData)
            }
        } catch (error) {
            console.error(error);
        }
    };

    const handlePageChange = page => {
        window.showSpinner(true);
        setPageActual(page);
        cargarCiudad(page, perPage);
    }

    const handlePerRowsChange = async (newPerPage, page) => {
        window.showSpinner(true);
        setPerPage(newPerPage);
        setPageActual(page);
        cargarCiudad(page, newPerPage);

    }

    return (
        <div className="block block-rounded block-bordered">
            {<InfoErrorApi />}
            {<Spinner />}
            <div className="block block-themed">
                <div className="col-md-12">
                    <div className="block-content">
                        <nav aria-label="breadcrumb">
                            <ol className="breadcrumb breadcrumb-alt push">
                                <li class="breadcrumb-item"> <small>Lista de Ciudades</small></li>
                            </ol>
                        </nav>
                    </div>
                </div>
                <div className="block-header">
                    <h3 className="block-title">Ciudades registradas</h3>
                    <Link to={`/Ciudad/Add`} >
                        <button type="button" className="btn btn-sm btn-light mr-1 mb-3"><i class="fas fa-plus"></i> Agregar ciudad</button>
                    </Link>
                </div>
                <div className="block-content">
                    <div className='row'>
                        <div className='col-md-12'>
                            <div className="form-group ">
                                <label htmlFor='search'>Buscar: </label>
                                <input type="text" id="search" name="search" onChange={handleSearch} onKeyPress={(e) => { e.key === 'Enter' && e.preventDefault(); }} className="form-control border border-success" placeholder="Buscar" />
                            </div>
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-sm-12">


                            <DataTable className="table table-bordered table-striped table-vcenter js-dataTable-full"
                                columns={columns}
                                data={ciudadLista.data}
                                perPage={perPage}
                                page={pageActual}
                                pagination
                                noDataComponent="Sin datos"
                                paginationServer
                                paginationTotalRows={ciudadLista.meta ? ciudadLista.meta.total : 1}
                                onChangePage={handlePageChange}
                                onChangeRowsPerPage={handlePerRowsChange}
                                defaultSortFieldId="Nombre"
                                striped
                                icon
                                paginationComponentOptions={{ rowsPerPageText: 'Filas por p치gina' }}
                                defaultSortAsc={false}
                            />

                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default CiudadLista;
