import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import { getUser } from '../../Utils/Common';
import { useLocation } from 'react-router-dom';
import '../../Utils/Constants';
import ModalGen from '../../Utils/Modals/ModalGeneric';
import GenericApi from '../../Api/Services/GenericApi';
import { useParams } from "react-router";
import Spinner from '../../Utils/Spinner';

function AntecedentesMigracionForm() {

    const [getMaximoTextArea, setMaximoTextArea] = useState(0);
    const [getMinimoTextArea, setMinimoTextArea] = useState(0);
    const [countTextArea, setCountTextArea] = useState(0);
    const [getAntecedente, setAntecedente] = useState({ title: "", message: "", show: false });
    const [getModalState, setModalState] = useState({ title: "", message: "", show: false });
    const [getDescripcion, setDescripcion] = useState(false);
    const [getFecha, setFecha] = useState(false);

    const location = useLocation()
    const { from } = location.state
    let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;
    let dependencia = from.dependencia;

    let { idAntecedente } = useParams();

    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);
            antecedente();
            obtenerParametros();
        }
        fetchData();
    }, []);

    
    function containsSpecialChars(str) {

        const result = global.Constants.TEXT_AREA.CARACTERES_ESPECIALES.split('').some(specialChar => {
            if (str.includes(specialChar)) {
                return true;
            }

            return false;
        });

        return result;
    }

    const antecedente= () => {
        try {

            //buscamos el parametro
            GenericApi.getGeneric("migracion-antecedente/51238/1/1/"+idAntecedente,).then(
                datos => {

                    if (!datos.error) {
                        setAntecedente(datos);
                        setDescripcion(datos.data[0].attributes.antecedente);
                        setFecha(datos.data[0].attributes.fecha);
                        console.log(datos.data[0].attributes.antecedente);
                    } else {
                        setModalState({ title: "Antecedentes", message: datos.error.toString(), show: true, redirect: '/AntecedentesForm', from: {from}, alert: global.Constants.TIPO_ALERTA.ERROR });
                    }
                    window.showSpinner(false);
                }
            )
        } catch (error) {
            // console.log(error);
        }
    }

    const obtenerParametros = () => {
        try {

            const data = {
                "data": {
                    "type": 'mas_parametro',
                    "attributes": {
                        "nombre": "minimo_caracteres_textarea|maximo_caracteres_textarea"
                    }
                }
            }

            //buscamos el parametro
            GenericApi.getByDataGeneric("parametro/parametro-nombre",data).then(
                datos => {

                    if (!datos.error) {

                        if (datos["data"].length > 0) {

                            datos["data"].filter(data => data["attributes"]["nombre"].includes('minimo_caracteres_textarea')).map(filteredName => (
                                setMinimoTextArea(filteredName["attributes"]["valor"])
                            ))
                            datos["data"].filter(data => data["attributes"]["nombre"].includes('maximo_caracteres_textarea')).map(filteredName => (
                                setMaximoTextArea(filteredName["attributes"]["valor"])
                            ))

                        }
                    } else {
                        setModalState({ title: "Antecedentes", message: datos.error.toString(), show: true, redirect: '/AntecedentesForm', from: {from}, alert: global.Constants.TIPO_ALERTA.ERROR });
                    }
                    window.showSpinner(false);
                }
            )
        } catch (error) {
            // console.log(error);
        }
    }

    const enviarDescripcion = (event) => {

        const antecedente = document.getElementById('antecedente_origen').value;
        document.getElementById("antecedente_destino").value = antecedente;

        //setDireccionCorrespondencia(direccion);
    }


    const infoAntecedente = () => {

        if (getAntecedente.data != null && typeof (getAntecedente.data) != 'undefined') {
            return (

                getAntecedente.data.map((item, i) => {
                    return (
                        <>
                        <div className="col-md-5">
                            <div className="form-group">
                                <label htmlFor="antecedente_origen">Antecedente</label>
                                <Field as="textarea" className="form-control" id="antecedente_origen" name="antecedente_origen" rows="4" editable={false}>
                                    {item.attributes.antecedente}
                                </Field>
                            </div>
                        </div>    

                        <div className="col-md-2">
                            <div className="form-group">
                                <button type="submit" className="btn btn-rounded btn-primary"><i class="fas fa-angle-double-right"></i></button>
                            </div>
                        </div>    
                            
                        <div className="col-md-5">
                           <div className="form-group">
                                <label htmlFor="antecedente_destino">Antecedente<span className="text-danger">*</span></label>
                                <Field as="textarea" className="form-control" id="antecedente_destino" name="antecedente_destino" rows="4"></Field>
                            </div>
                        </div> 

                        <div className="col-md-5">
                            <div className="form-group">
                                <label htmlFor="fecha_antecedente_origen">Fecha <span className="text-danger">*</span></label>
                                <Field as="text"className="form-control" id="fecha_antecedente_origen" name="fecha_antecedente_origen">{item.attributes.fecha}</Field>
                            </div>
                        </div>    

                        <div className="col-md-2">
                            <div className="form-group">
                                <button type="button" className="btn btn-rounded btn-primary" onClick={enviarDescripcion()}><i class="fas fa-angle-double-right"></i></button>
                            </div>
                        </div>    
                            
                        <div className="col-md-5">
                           <div className="form-group">
                                <label htmlFor="fecha_antecedente_destino">Fecha <span className="text-danger">*</span></label>
                                <Field as="text"className="form-control" id="fecha_antecedente_destino" name="fecha_antecedente_destino"></Field>
                            </div>
                        </div>   
                          
                        </>
                    )
                })
            )
        }
    }

    const enviarDatos = (datos) => {
        window.showSpinner(true);
        let data;

        data = {
            "data": {
                "type": 'antecedente',
                "attributes": {
                    "antecedentes": 3,
                    "descripcion": datos.descripcion,
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "fecha_registro": Date.now(),
                    "id_dependencia": dependencia ? dependencia : "",
                }
            }
        }

        GenericApi.addGeneric("antecedentes",data).then(
            datos => {
                if (!datos.error) {
                    setModalState({ title: "SINPROC No "+radicado+" :: Crear Antecedente", message: 'Registro Exitoso', show: true, redirect: '/AntecedentesLista', from: {from}, alert: global.Constants.TIPO_ALERTA.EXITO});
                }
                else {
                    setModalState({ title: "SINPROC No "+radicado+" :: Crear Antecedente", message: datos.error.toString(), show: true, redirect: '/AntecedentesLista', from: {from}, alert: global.Constants.TIPO_ALERTA.ERROR });
                }
                window.showSpinner(false);

            }
        )
    }

    return (
        <>
            {<Spinner />}   
            {<ModalGen data={getModalState} />}
          
            <Formik
                initialValues={{
                    antecedente_destino: ''
                }}
                enableReinitialize
                validate={(valores) => {
                    let errores = {}

                    return errores
                }}
                onSubmit={(valores, { resetForm }) => {
                    enviarDatos(valores);
                }}
            >

                {({ errors }) => (
                    <Form>

                        <div className="w2d_block">
                            <nav aria-label="breadcrumb">
                                <ol className="breadcrumb breadcrumb-alt push">
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`} state={{ from: from }}><small>Ramas del proceso</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/AntecedentesLista/`} state={{ from: from }}><small>Antecedentes</small></Link></li>
                                    <li class="breadcrumb-item"> <small>Agregar Antecedente</small></li>
                                </ol>
                            </nav>
                        </div>

                        {getAntecedente!=null?

                            <div className="block block-themed">
                                <div className="block-header">
                                    <h3 className="block-title">SINPROC No {radicado} :: <strong>Crear antecedente {idAntecedente}</strong></h3>
                                </div>

                                <div className="block-content">

                                    <div className='text-right'>

                                        <Link to={`/AntecedentesLista/`} title='Regresar a lista de Antecedentes' state={{ from: from }}>
                                            <button type="button" className="btn btn-success"><i className="fas fa-backward"></i> </button>
                                        </Link>
                                    </div>

                                    <div className="row">
                                        {infoAntecedente()}
                                    </div>

                                </div>
                                <div className="block-content block-content-full text-right bg-light">
                                    <button type="submit" className="btn btn-rounded btn-primary">Registrar</button>

                                    <Link to={`/AntecedentesLista/`} state={{ from: from }}>
                                        <button type="button" className="btn btn-rounded btn-outline-primary">Cancelar</button>
                                    </Link>
                                </div>
                            </div>:null
                        }

                    </Form>
                )}
            </Formik>
        </>
    );
}


export default AntecedentesMigracionForm;
