import React, { Component, useState } from 'react';
import { Link } from "react-router-dom";
import { hasAccess } from '../components/Utils/Common';

class Navbar extends Component {

    componentDidMount() {
        
        hasAccess('TiposUsuario', 'Control interno disciplinario2').then((value) => {
            console.log('TiposUsuario-----Control interno disciplinario>>>>' + value);
            this.setState({
                accessNuevaOpcion: value
            });
          });


        // var value = hasAccess('TiposUsuario', 'Control interno disciplinario');
        // console.log('TiposUsuario-----Control interno disciplinario>>>>' + value);
        // this.setState({
        //     accessNuevaOpcion: value
        // });
    }

    render() {
        var accessNuevaOpcion = false;
        return (
            <nav id="sidebar" aria-label="Main Navigation">
                {/* Side Header */}
                <div className="bg-header-white">
                    <div className="content-header bg-white-10">
                        {/* Logo */}
                        <a>
                            <img src={process.env.PUBLIC_URL + "/assets/images/logo_pdb_azul.png"} alt="Logo" height="50px" />
                        </a>
                        {/* END Logo */}
                        <div>
                            <a className="d-lg-none text-primary ml-2" data-toggle="layout" data-action="sidebar_close">
                                <i className="fa fa-times-circle"></i>
                            </a>
                        </div>
                    </div>
                </div>
                {/* END Side Header */}

                {/* Side Navigation */}
                <div className="content-side content-side-full">
                    <ul className="nav-main">
                        <li className="nav-main-heading">Navegaci贸n</li>
                        {/*                    
                        <li className="nav-main-item">
                            <a className="nav-main-link nav-main-link-submenu" data-toggle="submenu" aria-haspopup="true" aria-expanded="false" >
                                <i className="nav-main-link-icon si si-cup"></i>
                                <span className="nav-main-link-name">Ejemplos</span>
                            </a>
                            <ul className="nav-main-submenu">
                                <li className="nav-main-item">                                     
                                    <Link className="nav-main-link" to="/Ejemplos">
                                        <span className="nav-main-link-name">Registro de usuarios</span>
                                    </Link>
                                </li>
                            </ul>                            
                            <ul className="nav-main-submenu">
                                <li className="nav-main-item">                                     
                                    <Link className="nav-main-link" to="/Ejemplos/Departamentos">
                                        <span className="nav-main-link-name">Departamentos (API/REST)</span>
                                    </Link>
                                </li>
                            </ul>
                        </li>
                       */}
                        <li className="nav-main-item">
                            <a className="nav-main-link nav-main-link-submenu" data-toggle="submenu" aria-haspopup="true" aria-expanded="false" >
                                <i className="nav-main-link-icon si si-notebook"></i>
                                <span className="nav-main-link-name">Sistemas Disciplinarios</span>
                            </a>
                            <ul className="nav-main-submenu">
                                <li className="nav-main-item">
                                    <Link className="nav-main-link" to="/ProcesoDisciplinario">
                                        <span className="nav-main-link-name">Inicio Proceso Disciplinario </span>
                                    </Link>
                                </li>
                                <li className="nav-main-item">
                                    <Link className="nav-main-link" to="/MisPendientes">
                                        <span className="nav-main-link-name">Mis Pendientes</span>
                                    </Link>
                                </li>
                            </ul>
                        </li>

                        <li className="nav-main-item">
                            <a className="nav-main-link nav-main-link-submenu" data-toggle="submenu" aria-haspopup="true" aria-expanded="false" >
                                <i className="nav-main-link-icon si si-notebook"></i>
                                <span className="nav-main-link-name">Administraci贸n</span>
                            </a>
                            <ul className="nav-main-submenu">
                                <li className="nav-main-item">
                                    <Link className="nav-main-link" to="/EntidadesInteresadoLista">
                                        <span className="nav-main-link-name">Entidades permitidas comunicaci贸n interesado</span>
                                    </Link>
                                </li>                              
                            </ul>
                        </li>

                        {accessNuevaOpcion ? (
                            <li className="nav-main-item">
                                <a className="nav-main-link">
                                    <i className="nav-main-link-icon si si-settings"></i>
                                    <span className="nav-main-link-name">Nueva opci贸n</span>
                                </a>
                            </li>)
                            : null}


                    </ul>
                </div>
                {/* END Side Navigation */}
            </nav>
        )
    }

}

export default Navbar;
