import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState, useRef } from 'react';
import { Link } from "react-router-dom";
import { getUser } from '../../Utils/Common';
import { useLocation } from 'react-router-dom';
import '../../Utils/Constants';
import ModalGen from '../../Utils/Modals/ModalGeneric';
import GenericApi from '../../Api/Services/GenericApi';
import { useParams } from "react-router";
import Spinner from '../../Utils/Spinner';
import Center from '../../Utils/Styles/Center.js'
import moment from 'moment';

function EntidadMigracionForm() {

    const [getMaximoTextArea, setMaximoTextArea] = useState(0);
    const [getMinimoTextArea, setMinimoTextArea] = useState(0);
    const [countTextArea, setCountTextArea] = useState(0);
    const [getLista, setLista] = useState({ title: "", message: "", show: false });
    const [getModalState, setModalState] = useState({ title: "", message: "", show: false });
   

    let { radicado, vigencia, id, item } = useParams();


    const [entidad, setEntidad] = useState('');
    const [idEntidad, setIdEntidad] = useState('');
    const [direccion, setDireccion] = useState('');
    const [sector, setSector] = useState('');
    const [nombreInvestigado, setNombreInvestigado] = useState('');
    const [cargoInvestigado, setCargoInvestigado] = useState('');
    const [observaciones, setObservaciones] = useState('');
    const [registradoPor, setRegistradoPor] = useState('');
    const [fechaRegistro, setFechaRegistro] = useState('');

    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);
            lista();
            
        }
        fetchData();
    }, []);


    const lista= () => {
        try {

            //buscamos el parametro
            GenericApi.getGeneric("migracion-entidad/"+radicado+"/"+vigencia+"/"+item).then(
                datos => {

                    if (!datos.error) {
                        setLista(datos);
                    } else {
                        //setModalState({ title: "Antecedentes", message: datos.error.toString(), show: true, redirect: '/AntecedentesForm', alert: global.Constants.TIPO_ALERTA.ERROR });
                    }
                    window.showSpinner(false);
                }
            )
        } catch (error) {
            // console.log(error);
        }
    }


    const handleClick = (event, i) => {

        if(i===1){
            setEntidad(getLista.data[0].attributes.entidad);
        }

        else if(i===2){
            setDireccion(getLista.data[0].attributes.direccion);      
        }

        else if(i===3){
            setSector(getLista.data[0].attributes.sector);
        }

        else if(i===4){
            setNombreInvestigado(getLista.data[0].attributes.nombreInvestigado);      
        }

        else if(i===5){
            setCargoInvestigado(getLista.data[0].attributes.cargoInvestigado);      
        }

        else if(i===6){
            setObservaciones(getLista.data[0].attributes.observaciones);      
        }

    };


    const handleInputChange = (event, i) => {
     
        if(i===1){
            setEntidad(event.currentTarget.value);
        }

        else if(i===2){
            setDireccion(event.currentTarget.value)
        }

        else if(i===3){
            setSector(event.currentTarget.value)
        }

        else if(i===4){
            setNombreInvestigado(event.currentTarget.value)
        }

        else if(i===5){
            setCargoInvestigado(event.currentTarget.value)
        }

        else if(i===6){
            setObservaciones(event.currentTarget.value)
        }

        return true;

        //return true;
    }



    const infoInteresado = () => {

        if (getLista.data != null && typeof (getLista.data) != 'undefined') {
            return (

                getLista.data.map((item, i) => {
                    return (
                        <>
                        {/* 1. ENTIDAD */}   
                        <div className="col-md-5">
                            <div className="form-group">
                                <label htmlFor="entidad_origen">Entidad</label>
                                <Field value={item.attributes.entidad} as="input" type="text" autoComplete="off" 
                                className="form-control" id="entidad_origen" name="entidad_origen">                                   
                                </Field>
                            </div>
                        </div>
                        
                        <div className="col-md-2">
                            <div className="form-group">
                                <Center>
                                    <button type="button" className="btn btn-success" onClick={event => handleClick(event, 1)}>
                                        <i class="fas fa-angle-double-right"></i></button>
                                </Center>
                            </div>
                        </div>    
                            
                        <div className="col-md-5">
                           <div className="form-group">
                                <label htmlFor="entidad_destino">Entidad<span className="text-danger">*</span></label>
                                <Field value={entidad} as="input" type="text" autoComplete="off" className="form-control" 
                                id="entidad_destino" name="entidad_destino" onChange={event => handleInputChange(event, 1)}></Field>
                            </div>
                        </div> 

                        {/* 2. DIRECCION */}   
                        <div className="col-md-5">
                            <div className="form-group">
                                <label htmlFor="direccion_origen">Direcci√≥n</label>
                                <Field value={item.attributes.direccion} as="input" type="text" autoComplete="off" 
                                className="form-control" id="direccion_origen" name="direccion_origen"></Field>
                            </div>
                        </div>
                        
                        <div className="col-md-2">
                            <div className="form-group">
                                <Center>
                                    <button type="button" className="btn btn-success" onClick={event => handleClick(event, 2)}>
                                        <i class="fas fa-angle-double-right"></i></button>
                                </Center>
                            </div>
                        </div>    

                        <div className="col-md-5">
                           <div className="form-group">
                                <label htmlFor="direccion_destino">Direcci√≥n<span className="text-danger">*</span></label>
                                <Field value={direccion} as="input" type="text" autoComplete="off" className="form-control" 
                                id="direccion_destino" name="direccion_destino" onChange={event => handleInputChange(event, 2)}>

                                </Field>
                            </div>
                        </div>


                        {/* 3. SECTOR */}   
                        <div className="col-md-5">
                            <div className="form-group">
                                <label htmlFor="sector_origen">Sector</label>
                                <Field value={item.attributes.sector} as="input" type="text" autoComplete="off" 
                                className="form-control" id="sector_origen" name="sector_origen"></Field>
                            </div>
                        </div>
                        
                        <div className="col-md-2">
                            <div className="form-group">
                                <Center>
                                    <button type="button" className="btn btn-success" onClick={event => handleClick(event, 3)}>
                                        <i class="fas fa-angle-double-right"></i></button>
                                </Center>
                            </div>
                        </div>    
                            
                        <div className="col-md-5">
                           <div className="form-group">
                                <label htmlFor="sector_destino">Sector<span className="text-danger">*</span></label>
                                <Field value={sector} as="input" type="text" autoComplete="off" className="form-control" 
                                id="sector_destino" name="sector_destino" 
                                onChange={event => handleInputChange(event, 3)}></Field>
                            </div>
                        </div> 

                        {/* 4. NOMBRE INVESTIGADO */}   
                        <div className="col-md-5">
                            <div className="form-group">
                                <label htmlFor="nombre_investigado_origen">Nombre investigado</label>
                                <Field value={item.attributes.nombreInvestigado} as="input" type="text" 
                                autoComplete="off" className="form-control" 
                                id="nombre_investigado_origen" name="nombre_investigado_origen"></Field>
                            </div>
                        </div>
                        
                        <div className="col-md-2">
                            <div className="form-group">
                                <Center>
                                    <button type="button" className="btn btn-success" onClick={event => handleClick(event, 4)}>
                                        <i class="fas fa-angle-double-right"></i></button>
                                </Center>
                            </div>
                        </div>    
                            
                        <div className="col-md-5">
                           <div className="form-group">
                                <label htmlFor="nombre_investigado_destino">Nombre investigado<span className="text-danger">*</span></label>
                                <Field value={nombreInvestigado} as="input" type="text" autoComplete="off" className="form-control" 
                                id="nombre_investigado_destino" name="nombre_investigado_destino" 
                                onChange={event => handleInputChange(event, 4)}></Field>
                            </div>
                        </div> 


                        {/*5. CARGO INVESTIGADO*/}   
                        <div className="col-md-5">
                            <div className="form-group">
                                <label htmlFor="cargo_investigado_origen">Cargo investigado</label>
                                <Field value={item.attributes.cargoInvestigado} as="input" type="text" autoComplete="off" className="form-control" 
                                id="cargo_investigado_origen" name="cargo_investigado_origen"></Field>
                            </div>
                        </div>
                        
                        <div className="col-md-2">
                            <div className="form-group">
                                <Center>
                                    <button type="button" className="btn btn-success" onClick={event => handleClick(event, 5)}>
                                        <i class="fas fa-angle-double-right"></i></button>
                                </Center>
                            </div>
                        </div>    
                            
                        <div className="col-md-5">
                           <div className="form-group">
                                <label htmlFor="cargo_investigado_destino">Cargo investigado<span className="text-danger">*</span></label>
                                <Field value={cargoInvestigado} as="input" type="text" autoComplete="off" className="form-control" 
                                id="cargo_investigado_destino" name="cargo_investigado_destino" onChange={event => handleInputChange(event, 5)}></Field>
                            </div>
                        </div>

                        {/* 6. OBSERVACIONES */}   
                        <div className="col-md-5">
                            <div className="form-group">
                                <label htmlFor="observaciones_origen">Observaciones</label>
                                <Field patt value={item.attributes.observaciones} autoComplete="off" as="textarea" className="form-control" 
                                id="observaciones_origen" name="observaciones_origen" rows="4"></Field>
                            </div>
                        </div>
                        
                        <div className="col-md-2">
                            <div className="form-group">
                                <Center>
                                    <button type="button" className="btn btn-success" onClick={event => handleClick(event, 6)}>
                                        <i class="fas fa-angle-double-right"></i></button>
                                </Center>
                            </div>
                        </div>    
                            
                        <div className="col-md-5">
                           <div className="form-group">
                                <label htmlFor="observaciones_destino">Observaciones<span className="text-danger">*</span></label>
                                <Field as="textarea" className="form-control" id="observaciones_destino" name="observaciones_destino" 
                                rows="4" value={observaciones} onChange={event => handleInputChange(event, 6)}></Field>
                            </div>
                        </div> 

                        </>
                    )
                })
            )
        }
    }

    const enviarDatos = (datos) => {
        window.showSpinner(true);
        let data;

        /*data = {
            "data": {
                "type": 'antecedente',
                "attributes": {
                    "antecedentes": 3,
                    "descripcion": datos.descripcion,
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "fecha_registro": Date.now(),
                    "id_dependencia": dependencia ? dependencia : "",
                }
            }
        }

        GenericApi.addGeneric("antecedentes",data).then(
            datos => {
                if (!datos.error) {
                    setModalState({ title: "SINPROC No "+radicado+" :: Crear Antecedente", message: 'Registro Exitoso', show: true, redirect: '/AntecedentesLista', from: {from}, alert: global.Constants.TIPO_ALERTA.EXITO});
                }
                else {
                    setModalState({ title: "SINPROC No "+radicado+" :: Crear Antecedente", message: datos.error.toString(), show: true, redirect: '/AntecedentesLista', from: {from}, alert: global.Constants.TIPO_ALERTA.ERROR });
                }
                window.showSpinner(false);

            }
        )*/
    }

    return (
        <>
            {<Spinner />}   
            {<ModalGen data={getModalState} />}
          
            <Formik
                initialValues={{
                    idEntidad: idEntidad,
                    nombreEntidad: entidad,
                    fechaRegistro: fechaRegistro,
                    entidad: entidad,
                    direccion: direccion,
                    sector: sector,
                    nombreInvestigado: nombreInvestigado,
                    cargoInvestigado: cargoInvestigado,
                    observaciones: observaciones,
                }}
                enableReinitialize
                validate={(valores) => {
                    let errores = {}

                    return errores
                }}
                onSubmit={(valores, { resetForm }) => {
                    enviarDatos(valores);
                }}
            >

                {({ errors }) => (
                    <Form>

                    <div className="w2d_block">
                        <nav aria-label="breadcrumb">
                        <ol className="breadcrumb breadcrumb-alt push">
                            <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/Buscador`}><small>Buscador</small></Link></li>
                            <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/ProcesoDisciplinarioMigracion/${radicado}/${vigencia}`}><small>Inicio proceso de migraci√≥n</small></Link></li>
                            <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/ListarMigracionEntidades/${radicado}/${vigencia}`}><small>Lista de entidades</small></Link></li>
                            <li class="breadcrumb-item"> <small>Entidades del investigado</small></li>
                        </ol>
                        </nav>
                    </div>

                        <div className="block block-themed">
                            <div className="block-header">
                                <h3 className="block-title">Proceso disciplinario No  :: <strong>Migrar entidad del interesado {item}</strong></h3>
                            </div>

                            <div className="block-content">

                                <div className='text-right'>

                                    <Link to={`/AntecedentesLista/`} title='Regresar a lista de Antecedentes' >
                                        <button type="button" className="btn btn-success"><i className="fas fa-backward"></i> </button>
                                    </Link>
                                </div>

                                <div className="row">

                                    {infoInteresado()}
                                    
                                </div>

                            </div>
                            <div className="block-content block-content-full text-right bg-light">
                                <button type="submit" className="btn btn-rounded btn-primary">Registrar</button>

                                <Link to={`/AntecedentesLista/`}>
                                    <button type="button" className="btn btn-rounded btn-outline-primary">Cancelar</button>
                                </Link>
                            </div>
                        </div>
                    </Form>
                )}
            </Formik>
        </>
    );
}


