import React, { useEffect, useState, } from 'react';
import { ErrorMessage, Form, Field, Formik } from 'formik';
import { Link } from 'react-router-dom';
import InfoErrorApi from '../Utils/InfoErrorApi';
import InfoExitoApi from '../Utils/InfoExitoApi';
import Spinner from '../Utils/Spinner';
import { hasAccess } from '../../components/Utils/Common';
import DataTable from 'react-data-table-component';
import { Navigate } from "react-router-dom";
import GenericApi from './../Api/Services/GenericApi';
import { useParams } from "react-router";
import { useLocation } from 'react-router-dom'
import '../Utils/Constants';
function CasosActivos() {
    const location = useLocation()
    const { data } = location.state
    const paganationPerPages = process.env.PAGINATION_PER_PAGE;
    const [errorApi, setErrorApi] = useState('');
    const [getListaPendientes, setListaPendientes] = useState({ data: {} });
    const [getUsuarioSeleccionado, setUsuarioSeleccionado] = useState();
    const [perPage, setPerPage] = useState(process.env.PAGINATION_PER_PAGE);
    const [getSelectedData, setSelectedData] = useState([]);
    const [getNombre, setNombre] = useState('');
    const [getApellido, setApellido] = useState('');
    const [getListaUsuarios, setListaUsuarios] = useState({ data: {} });
    const [getRespuestaUsuarios, setRespuestaUsuarios] = useState(false);
    const [pageActual, setPageActual] = useState(1);
    const [getSeach, setSeach] = useState('');
    const [isRedirect, setIsRedirect] = useState(false);
    const [getCasosSeleccionados, setCasosSeleccionados] = useState([]);
    let { usuario } = useParams();

  

    /*Redirects*/
    const redirectToRoutes = () => {
        return <Navigate to={`/TrasladoProcesos/`} />;
    }

    const columns = [
        {
            name: 'SINPROC',
            selector: pendiente => pendiente.attributes.radicado,
            sortable: true,
            width: "20%"
        },

        {
            name: 'Fecha de vigencia',
            selector: pendiente => (pendiente.attributes.vigencia),
            sortable: true,
            width: "25%"
        },
        {
            name: 'Fecha de registro',
            selector: pendiente => (pendiente.attributes.created_at),
            sortable: true,
            width: "25%"
        },
        {
            name: 'Tipo expediente',
            selector: pendiente => (pendiente.attributes.nombre_tipo_expediente),
            sortable: true,
            width: "25%"
        }
    ];
    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);
            cargarPendientes(1, paganationPerPages)
        }
        fetchData();
    }, []);


    const handleInputChange = (event) => {
        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;

        if (name == "nombre") {
            setNombre(value);
        }
        if (name == "apellido") {
            setApellido(value);
        }

    }

    const cargarPendientes = (page, perPage) => {

        const data = {
            "data": {
                "type": "pendietes",
                "attributes": {
                    "fecha": '',
                    'per_page': perPage,
                    'current_page': page,
                    "usuario_actual": usuario
                }
            }
        }
        console.log(JSON.stringify(data));
        GenericApi.getByDataGeneric('mis-pendientes-filter-actual', data).then(
            datos => {
                if (!datos.error) {
                    setListaPendientes(datos)
                    window.showSpinner(false);
                }
                else {
                    setErrorApi(datos.error.toString())
                    window.showModal()
                    window.showSpinner(false);
                }

            }
        )

    }

    const enviarDatos = (datos) => {

    }

    const handlePageChange = page => {
        //window.showSpinner(true);
        setPageActual(page);
        //cargarDatosInvestigado(page, perPage, (getEstadoLista == "Inactivos" ? '0' : "1"));
    }

    const handlePerRowsChange = async (newPerPage, page) => {
        //window.showSpinner(true);
        setPerPage(newPerPage);
        setPageActual(page);
        //cargarDatosInvestigado(page, newPerPage, (getEstadoLista == "Inactivos" ? '0' : "1"));

    }
    const handleChange = (state) => {
        setSelectedData(state.selectedRows);
    };


    const buscarUsuario = () => {

        window.showSpinner(true)
        try {

            if (getApellido || getNombre) {
                let data;


                data = {

                    "data": {
                        "type": "user",
                        "attributes": {
                            "nombre": getNombre ? getNombre : '',
                            "apellido": getApellido ? getApellido : ''

                        }
                    }
                }





                console.log(JSON.stringify(data));

                GenericApi.getByDataGeneric('usuario/usuario-filter', data).then(
                    datos => {

                        if (!datos.error) {
                            setListaUsuarios(datos);
                            setRespuestaUsuarios(true);
                        }

                        window.showSpinner(false);
                    }
                )
            }


        } catch (ex) {
            console.error("Ocurrio este error " + ex);
            inicilizarDatos();
        }


    }

    const inicilizarDatos = () => {
        setNombre("");
        setApellido("");
    }

    const listarCoincidencias = () => {


        if (getListaUsuarios != null && typeof (getListaUsuarios) != 'undefined') {
            return (

                getListaUsuarios.data.map((usuario, i) => {
                    return (

                        <tr key={usuario.id}>
                            <td>{usuario.attributes.nombre}</td>
                            <td>{usuario.attributes.apellido}</td>
                            <td>{usuario.attributes.email}</td>
                            <td>{usuario.attributes.dependencia.nombre}</td>

                            <td>  <Link to={`/CasosActivos/${usuario.attributes.name}`}>
                                <button type='button' data-dismiss="modal" className='btn btn-sm btn-primary' onClick={() => seleccionarUsuario(usuario.attributes)}>Seleccionar</button>
                            </Link></td>
                        </tr >
                    )
                })
            )
        }


    }

    const seleccionarUsuario = (e) => {
        setUsuarioSeleccionado(e);
    }


    const onConfirm = () => {


        window.showSpinner(true)
        try {
            console.log(getSelectedData);
            if (getApellido || getNombre) {

                let data;
                data = {

                    "data": {
                        "type": "proceso_disciplinario",
                        "attributes": {
                            "usuario_a_remitir": getUsuarioSeleccionado ? getUsuarioSeleccionado.name : '',
                            "lista_procesos":


                                getSelectedData.map((suggestion) => {

                                    return suggestion["id"];



                                })

                        }
                    }
                }

                console.log(JSON.stringify(data));

                GenericApi.getByDataGeneric('proceso-diciplinario/traslado-masivo', data).then(
                    datos => {

                        if (!datos.error) {
                            window.showModal(2)
                            setIsRedirect(true);
                        }
                        else {
                            setErrorApi(datos.error.toString())
                            console.log(datos.error);
                            window.showModal(1)
                        }
                        window.showSpinner(false);
                    }
                )
            }


        } catch (ex) {
            console.error("Ocurrio este error " + ex);
            inicilizarDatos();
        }
    }
    return (
        <>
            {isRedirect ? redirectToRoutes() : null}
            {<Spinner />}
            {<InfoErrorApi error={errorApi} />}
            {<InfoExitoApi />}
            <Formik
                initialValues={{

                }}
                enableReinitialize
                validate={(valores) => {

                    let errores = {}
                    return errores
                }}
                onSubmit={(valores, { resetForm }) => {

                    enviarDatos(valores);
                }}
            >

                {({ errors }) => (
                    <Form>

                        <>
                            {
                                (getListaPendientes.data.length > 0) ?
                                    (
                                        <div className="block block-rounded block-bordered">

                                            <div className="block-content block-content-full">
                                                <div className='text-right'>

                                                    <Link to={`/TrasladoProcesos/`} title='Regresar a buscar usuario'>
                                                        <button type="button" className="btn btn-success"><i className="fas fa-backward"></i> </button>
                                                    </Link>
                                                </div>

                                                <div className="block-content block-content-full border border-success mt-1">
                                                  
                                                    <div className="row">
                                                        <div className="col-md-12 text-center mb-3">
                                                            <h4>Usuario origen seleccionado</h4>
                                                        </div>

                                                    </div>
                                                    <div className="row">
                                                        <div className="col-md-6">
                                                            <strong>Nombre</strong> : {data.attributes.nombre}
                                                        </div>
                                                        <div className="col-md-6">
                                                            <strong>Apellido</strong> : {data.attributes.apellido}
                                                        </div>

                                                    </div>
                                                    <div className="row">

                                                        <div className="col-md-6">
                                                            <strong>Correo</strong> : {data.attributes.email}
                                                        </div>
                                                        <div className="col-md-6">
                                                            <strong>Dependencia Origen</strong> : {data.attributes.dependencia.nombre}
                                                        </div>
                                                    </div>


                                                </div>
                                                <hr/>
                                                <div className="row mt-3">
                                                    <div className="col-md-12 text-center mb-3">
                                                        <label>PASO 2. Selecciona los casos que deseas reasignar:</label>
                                                    </div>

                                                </div>
                                                <div className='col-md-12'>
                                                    <div className="form-group ">
                                                        <Field type="text" id="search" name="search" onChange={e => setSeach(e.target.value)} onKeyPress={(e) => { e.key === 'Enter' && e.preventDefault(); }} className="form-control border border-success" placeholder="Buscar" />

                                                    </div>
                                                </div>

                                                {
                                                    (getListaPendientes.data.length > 0) ? (
                                                        <DataTable className="table table-bordered table-striped table-vcenter js-dataTable-full"
                                                            columns={columns}
                                                            data={getListaPendientes.data.filter((suggestion) => {
                                                                if (getSeach === "") {
                                                                    return suggestion;
                                                                } else if (

                                                                    ((suggestion.attributes.radicado
                                                                        + (suggestion.attributes.created_at)
                                                                        + (suggestion.attributes.vigencia) +
                                                                        suggestion.attributes.nombre_tipo_expediente).toLowerCase().includes(getSeach.toLowerCase()))

                                                                ) {
                                                                    return suggestion;
                                                                }
                                                            })}
                                                            perPage={perPage}
                                                            page={pageActual}
                                                            selectableRows={true}
                                                            onSelectedRowsChange={handleChange}
                                                            sele
                                                            pagination
                                                            noDataComponent={global.Constants.DATA_TABLE.SIN_DATOS_TABLE}
                                                            paginationTotalRows={getListaPendientes.data.length}
                                                            onChangePage={handlePageChange}
                                                            onChangeRowsPerPage={handlePerRowsChange}
                                                            defaultSortFieldId="Nombre"
                                                            striped
                                                            paginationComponentOptions={{ rowsPerPageText: global.Constants.DATA_TABLE.FILTRO_POR_PAGINA }}
                                                            defaultSortAsc={false}
                                                        />
                                                    ) : null

                                                }
                                                <div className='col-md-12 text-right'>
                                                    <label className='mx-4 text-success'>Casos seleccionados: {getSelectedData.length}</label>
                                                    <button disabled={getSelectedData.length == 0} type='button' data-toggle="modal" className='btn btn btn-primary' data-target={'#modal-consultar-usuario'}>Asignar a...</button>
                                                </div>
                                                <hr/>
                                                <div className='mt-3'>
                                                    <>
                                                        {
                                                            (getUsuarioSeleccionado) ? (
                                                                <div className="block-content block-content-full border border-success mt-1">
                                                                    <div className="row">
                                                                        <div className="col-md-12 text-center mb-3">
                                                                            <label className='mx-4 text-success'>Casos seleccionados: {getSelectedData.length}</label>
                                                                        </div>
                                                                    </div>
                                                                    <div className="row">
                                                                        <div className="col-md-12 text-center mb-3">
                                                                            <h4>Usuario destino seleccionado</h4>
                                                                        </div>

                                                                    </div>
                                                                    <div className="row">
                                                                        <div className="col-md-6">
                                                                            <strong>Nombre</strong> : {getUsuarioSeleccionado.nombre}
                                                                        </div>
                                                                        <div className="col-md-6">
                                                                            <strong>Apellido</strong> : {getUsuarioSeleccionado.apellido}
                                                                        </div>

                                                                    </div>
                                                                    <div className="row">

                                                                        <div className="col-md-6">
                                                                            <strong>Correo</strong> : {getUsuarioSeleccionado.email}
                                                                        </div>
                                                                        <div className="col-md-6">
                                                                            <strong>Dependencia Destino</strong> : {getUsuarioSeleccionado.dependencia.nombre}
                                                                        </div>
                                                                    </div>

                                                                    <div className='col-md-12 text-center mt-3'>
                                                                        <button disabled={getSelectedData.length == 0} type='button' className='btn btn btn-primary' onClick={() => {
                                                                            if (window.confirm('¿Esta seguro que sesea reasignar '
                                                                                + getSelectedData.length + " " + (getSelectedData.length > 1 ? "casos" : "caso") + ' al usuario ' + getUsuarioSeleccionado.name + " ?")) onConfirm()
                                                                        }} >Asociar casos</button>
                                                                    </div>

                                                                </div>
                                                            ) : null
                                                        }
                                                    </>
                                                </div>

                                                <div className="modal fade" id={'modal-consultar-usuario'} tabindex="-1" role="dialog" aria-labelledby="modal-block-normal" aria-hidden="true">
                                                    <div className="modal-dialog modal-lg" role="document" >
                                                        <div class="modal-content">
                                                            <div class="block block-themed block-transparent mb-0">
                                                                <div class="block-header bg-primary-dark">
                                                                    <h3 class="block-title">Buscar usuario para asinarle los casos</h3>
                                                                    <div class="block-options">
                                                                        <button type="button" class="btn-block-option" data-dismiss="modal" aria-label="Close">
                                                                            <i class="fa fa-fw fa-times"></i>
                                                                        </button>
                                                                    </div>
                                                                </div>
                                                                <div className="block-content block-content-full">
                                                                    <div className="row">

                                                                        <div className="col-4">
                                                                            <div className="form-group">
                                                                                <Field type="text" value={getNombre} onChange={handleInputChange} id="nombre" name="nombre" className="form-control" placeholder="Nombre" />
                                                                                <ErrorMessage name="nombre" component={() => (<span className="text-danger">{errors.nombre}</span>)} />
                                                                            </div>
                                                                        </div>
                                                                        <div className="col-4">
                                                                            <div className="form-group">
                                                                                <Field type="text" value={getApellido} onChange={handleInputChange} id="apellido" name="apellido" className="form-control" placeholder="Apellido" />
                                                                                <ErrorMessage name="apellido" component={() => (<span className="text-danger">{errors.apellido}</span>)} />
                                                                            </div>
                                                                        </div>
                                                                        <div className="col-4">
                                                                            <div className="form-group">
                                                                                <button type="button" className="btn btn-primary" onClick={() => buscarUsuario()}>
                                                                                    <i className="fa fa-fw fa-search"></i> Buscar
                                                                                </button>
                                                                            </div>
                                                                        </div>



                                                                    </div>
                                                                    <hr />
                                                                    {
                                                                        (getListaUsuarios.data.length > 0) ? (
                                                                            <div>


                                                                                <div className="row" >
                                                                                    <div className="col-md-12" style={{ 'height': '400px', 'overflow': 'scroll', 'display': 'block' }}>
                                                                                        <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                                                                            <thead>
                                                                                                <tr>
                                                                                                    <th>Nombre</th>
                                                                                                    <th>Apellido</th>
                                                                                                    <th>Email</th>
                                                                                                    <th>Dependencia</th>
                                                                                                    <th>Seleccionar</th>
                                                                                                </tr>
                                                                                            </thead>
                                                                                            <tbody >
                                                                                                {getRespuestaUsuarios ? listarCoincidencias() : null}
                                                                                            </tbody>
                                                                                        </table>
                                                                                    </div>
                                                                                </div>


                                                                            </div>
                                                                        ) : null
                                                                    }

                                                                </div>

                                                            </div>
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>




                                        </div>
                                    ) : (
                                        <div>
                                            <div className='text-right'>

                                                <Link to={`/TrasladoProcesos/`} title='Regresar a buscar usuario'>
                                                    <button type="button" className="btn btn-success"><i className="fas fa-backward"></i> </button>
                                                </Link>
                                            </div>
                                            <div className="block-content block-content-full text-white border border-success mt-1 bg-info">
                                                <div className="row">
                                                    <div className="col-md-12 text-center">
                                                        <label>El usuario no cuenta con casos pendientes</label>
                                                    </div>

                                                </div>
                                            </div>
                                        </div>
                                    )
                            }
                        </>

                    </Form>
                )}
            </Formik>
        </>
    )

}

export default CasosActivos;
