    const [errorApi, setErrorApi] = useState('');
    const [isRedirect, setIsRedirect] = useState(false);
    const [estadoEtapaCapturaReparto, setEstadoEtapaCapturaReparto] = useState(false);

    const location = useLocation()
    const { from } = location.state
    let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;
    let vigencia = from.vigencia;


    const [getRoutes, setRoutes] = useState({
        id_etapa: 1,
        id_fase: 3,
        crear_registro: "/EntidadInvestigadoForm",
        consultar_registros: "/EntidadInvestigadoLista",
        adjuntar_documento: "/SoporteRadicadoForm",
        repositorio_documentos: ""
    });


    const redirectToRoutes = () => {
        return <Navigate to={`/EntidadInvestigadoLista/`} state={{ from: from }} />;
    }
    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);
            
            cargarDatosInteresados(true);

            // VALIDA CIERRE DE ETAPA EVALUACION
            const dataCierreEtapa = {

                "data": {
                    "type": "cerrar_etapa",
                    "attributes": {
                        "id_proceso_disciplinario": "id_proceso_disciplinario",
                        "id_etapa": "id_etapa",
                        "id_delegada": "id_delegada",
                        "id_funcionario": "id_funcionario"
                    }
                }

            }

            CierreEtapaApi.getCierreEtapaByIdProDisciplinario(dataCierreEtapa, procesoDisciplinarioId, 1).then(
                datos => {
                    if (!datos.error) {
                        if (datos["data"].length > 0) {
                            setEstadoEtapaCapturaReparto(true);
                        }
                    }
                    else {
                        setErrorApi(datos.error.toString())
                        window.showModal(1)
                    }
                }
            )
        }
        fetchData();
    }, []);



    ///////////////////////////////////////////////////////////
    /////////////////// PARA LA BÚSQUEDA //////////////////////
    ///////////////////////////////////////////////////////////

    const [respuestaEntidadInvestigado, setRespuestaEntidadInvestigado] = useState(false);
    const [entidadInvestigadoLista, setEntidadInvestigadoLista] = useState({ data: [], links: [], meta: [] });

    const [respuestaEntidadInvestigadoTotal, setRespuestaEntidadInvestigadoTotal] = useState(false);
    const [entidadInvestigadoListaTotal, setEntidadInvestigadoListaTotal] = useState({ data: [], links: [], meta: [] });

    const handleSearch = (event) => {
        try {
            if ((event.target.value.trim().trim() == "") || (event.target.value.trim().length == 0)) {

                setEntidadInvestigadoLista(entidadInvestigadoListaTotal);
            }
            else {

                setEntidadInvestigadoLista(entidadInvestigadoListaTotal.data.filter(
                    suggestion => ((suggestion.attributes.created_at
                        + suggestion.attributes.nombre_investigado + suggestion.attributes.cargo +
                        suggestion.attributes.observaciones + suggestion.attributes.nombre_entidad).toLowerCase().includes(event.target.value.toLowerCase()))
                ));
            }
        } catch (error) {
            console.error(error);
        }

    };


    const listaEntidadInvestigado = () => {

        if (entidadInvestigadoLista.data != null && typeof (entidadInvestigadoLista.data) != 'undefined') {
            return (


                entidadInvestigadoLista.data.map((entidad, i) => {
                    return (
                        cuerpoTabla(entidad)
                    )
                })
            )
        }
        else {
            return (

                entidadInvestigadoLista.map((entidad, i) => {
                    return (
                        cuerpoTabla(entidad)
                    )
                })
            )
        }

    }

    const cambiarEstado = (estado, id) => {

        try {


            //actualizamos el estado
            window.showSpinner(true);
            const data = {

                "data": {
                    "type": "entidad_investigado",
                    "attributes": {
                        "estado": estado,
                        "id_proceso_disciplinario": procesoDisciplinarioId
                    }
                }
            }

            console.log(JSON.stringify(data));
            EntidadInvestigadoApi.updateEntidadInvestigado(data, id).then(
                datos => {

                    if (!datos.error) {
                        EntidadInvestigadoApi.getAllEntidadInvestigadoByIdProDisciplinario(data, procesoDisciplinarioId).then(
                            datos => {
                                if (!datos.error) {
                                    setEntidadInvestigadoListaTotal(datos);
                                    setRespuestaEntidadInvestigadoTotal(true);
                                    setEntidadInvestigadoLista(datos);
                                    setRespuestaEntidadInvestigado(true);
                                    //window.showModal(2); 
                                    window.showSpinner(false);

                                }
                                else {
                                    window.showModal(1);
                                    window.showSpinner(false);
                                }
                            }
                        )

                        //setIsRedirect(true);
                    }
                    else {
                        //console.log("hay error");
                        console.log(datos.error);
                        window.showSpinner(false)
                    }

                }
            )

        } catch (error) {
            window.showSpinner(false);
        }
    }

    const cuerpoTabla = (entidad, i) => {
        return (
            <tr key={entidad.id}>
                <td>{entidad.attributes.nombre_entidad}</td>
                <td>
                    <small><strong>Fecha de registro:</strong> {entidad.attributes.created_at}<br /></small>
                    <small><strong>Sector:</strong> Pendiente<br /></small>
                    <small><strong>Nombre investigado:</strong> {entidad.attributes.nombre_investigado}<br /></small>
                    <small><strong>Cargo investigado:</strong> {entidad.attributes.cargo}<br /></small>
                    <small><strong>Observaciones:</strong> {entidad.attributes.observaciones}<br /></small>
                    <small><strong>Registrado por:</strong> {entidad.attributes.nombre_entidad}<br /></small>
                    <small><strong>Registrado en:</strong> {entidad.attributes.nombre_entidad}<br /></small>
                    <small><strong>Estado:</strong> {entidad.attributes.estado == '1' ? 'Activo' : 'Inactivo'}<br /></small>
                </td>
                <td>

                    {
                        (hasAccess('CR_EntidadInvestigado', 'Inactivar')) ? (
                            <div>
                                {
                                    (entidad.attributes.estado == 0) ? (
                                        <button type='button' title='Activar' onClick={() => cambiarEstado(1, entidad.id)} className='btn btn-sm btn-success'><span className="fas fa-toggle-off"> </span></button>
                                    ) : null
                                }

                                {
                                    (entidad.attributes.estado == 1) ? (
                                        <button type='button' title='Inacivar' onClick={() => cambiarEstado(0, entidad.id)} className='btn btn-sm btn-danger'><span className="fas fa-toggle-off"></span></button>
                                    ) : null
                                }

{
                                        (entidad.attributes.estado == 0) ? (
                                            <Link to={`/EntidadInteresadoCambiarEstadoForm/`} state={{ from: from }}>
                                                <button type='button' title='Activar Datos Interesado' className='btn btn-sm btn-success' data-toggle="modal" data-target={'#modal-cambiar-estado' + i}><i className="fas fa-plus-circle"></i></button>
                                            </Link>
                                        ) : null
                                    }

                                    {
                                        (entidad.attributes.estado == 1) ? (
                                            <Link to={`/EntidadInteresadoCambiarEstadoForm/`} state={{ from: from }}>
                                                <button type='button' title='Inactivar Antecedente' className='btn btn-sm btn-danger'><i className="fas fa-minus-circle"></i></button>
                                            </Link>
                                        ) : null
                                    }


                            </div>
                        ) : null
                    }
                </td>
            </tr>
        )
    }

    ///////////////////////////////////////////////////////////
    /////////////////// PARA LA BÚSQUEDA //////////////////////
    ///////////////////////////////////////////////////////////


    const cargarDatosInteresados = (estado) => {

      console.log("Sandra: "+estado)

            const data = {
            "data": {
                "type": "entidad_investigado",
                "attributes": {
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "id_etapa": "1",
                    "requiere_registro": true,
                    "estado": true,
                }
            }
        }

        if (hasAccess('CR_EntidadInvestigado', 'Consultar')) {
            EntidadInvestigadoApi.getAllEntidadInvestigadoByIdProDisciplinario(data, procesoDisciplinarioId).then(
                datos => {
                    if (!datos.error) {
                        setEntidadInvestigadoLista(datos)
                        setRespuestaEntidadInvestigado(true)
                        setEntidadInvestigadoListaTotal(datos)
                        setRespuestaEntidadInvestigadoTotal(true)
                        window.showSpinner(false);
                    }
                    else {
                        setErrorApi(datos.error.toString())
                        window.showModal();
                        window.showSpinner(false);
                    }

                }
            )
        }
    }



    
    const handleCallback = (childData) => {
        try {
        
            cargarDatosInteresados(childData);
        } catch (error) {

        }

    }


    return (
        <>

            
            <Formik>
                <Form>
                    <div className="col-md-12">
                        <div className="block block-rounded block-bordered">
                            <div className="block-content">
                                <nav aria-label="breadcrumb">
                                    <ol className="breadcrumb breadcrumb-alt push">

                                        <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                        <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`} state={{ from: from }}><small>Ramas del proceso</small></Link></li>
                                        <li class="breadcrumb-item"> <small>Entidad del investigado Lista</small></li>

                                    </ol>
                                </nav>
                            </div>
                        </div>
                    </div>

                    <div className="block block-themed">
                        <div className="block-header">
                            <h3 className="block-title">SINPROC No {radicado} :: Entidad del Investigado</h3>
                        </div>

                        <div className="block-content">
                            <>
                                {
                                    (hasAccess('CR_EntidadInvestigado', 'Consultar')) ? (
                                        <div className='row'>

                                            <div className='col-md-3'>
                                                <div className="form-group ">
                                                    <Field type="text" id="search" name="search" onChange={handleSearch} onKeyPress={(e) => { e.key === 'Enter' && e.preventDefault(); }} className="form-control border border-success" placeholder="Buscar" />

                                                </div>
                                            </div>

                                             {/*ListaBotones(getRoutes)*/}
                                             <ListaBotones getRoutes={getRoutes} from={from} parentCallback={handleCallback} id="botonesNavegacion" name="botonesNavegacion" />


                                        </div>
                                    ) : null
                                }

                                {
                                    (hasAccess('CR_EntidadInvestigado', 'Consultar')) ? (
                                        <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                            <thead>
                                                <tr>
                                                    <th>Entidad</th>
                                                    <th>Detalle</th>
                                                </tr>
                                            </thead>
                                            <tbody>
                                                {respuestaEntidadInvestigado ? listaEntidadInvestigado() : null}
                                            </tbody>
                                        </table>
                                    ) : null
                                }
                            </>
                         
                        </div>
                    </div>
                </Form>
            </Formik>
        </>
    );


}
export default EntidadInvestigadoLista;
