import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import { useParams } from "react-router";
import ClasificacionRadicadoApi from '../../Api/Services/ClasificacionRadicadoApi';
import EntidadInvestigadoApi from "../../Api/Services/EntidadInvestigadoApi";
import { Navigate } from "react-router-dom";
import Spinner from '../../Utils/Spinner';
import InfoErrorApi from '../../Utils/InfoErrorApi';
import InfoExitoApi from '../../Utils/InfoExitoApi';

function EntidadInvestigadoLista() {

    const [respuestaEntidadInvestigado, setRespuestaEntidadInvestigado] = useState(false);
    const [entidadInvestigadoLista, setEntidadInvestigadoLista] = useState({ data: [], links: [], meta: [] });
    const [errorApi, setErrorApi] = useState('');
    const [isRedirect, setIsRedirect] = useState(false);

    let { radicado, procesoDisciplinarioId } = useParams();

    const redirectToRoutes = () => {
        return <Navigate to={`/EntidadInvestigadoLista/${procesoDisciplinarioId}`} />;
    }
    useEffect(() => {
        async function fetchData() {

            const data = {
                "data": {
                    "type": "clasificacion_radicado",
                    "attributes": {
                        "id_proceso_disciplinario": procesoDisciplinarioId,
                        "id_etapa": "1",
                        "requiere_registro": true
                    }
                }
            }

            EntidadInvestigadoApi.getAllEntidadInvestigadoByIdProDisciplinario(data, procesoDisciplinarioId).then(
                datos => {
                    if(!datos.error){
                        setEntidadInvestigadoLista(datos)
                        setRespuestaEntidadInvestigado(true)
                    }
                    else{
                        setErrorApi(datos.error.toString())
                        window.showModal()
                    }
                    window.showSpinner(false);
                }
            )
        }
        fetchData();
    }, []);


    const listaEntidadInvestigado = () => {

        return(
            entidadInvestigadoLista.data.map((entidad, i) => {
                return (
                    <tr key={entidad.id}>
                        <td>{ entidad.attributes.nombre_entidad }</td>
                        <td>
                            <small><strong>Fecha de registro:</strong> { entidad.attributes.created_at }<br/></small>
                            <small><strong>Sector:</strong> Pendiente<br/></small>
                            <small><strong>Nombre investigado:</strong> { entidad.attributes.nombre_investigado }<br/></small>
                            <small><strong>Cargo investigado:</strong> { entidad.attributes.cargo }<br/></small>
                            <small><strong>Observaciones:</strong> { entidad.attributes.observaciones }<br/></small>
                            <small><strong>Registrado por:</strong> { entidad.attributes.nombre_entidad }<br/></small>
                            <small><strong>Registrado en:</strong> { entidad.attributes.nombre_entidad }<br/></small>
                        </td>
                    </tr>
                )
            })
        )
    }

    return(
        <>
            <div className="col-md-12">
                <div className="block block-rounded block-bordered">
                    <div className="block-content">
                        <nav aria-label="breadcrumb">
                            <ol className="breadcrumb breadcrumb-alt push">

                                <li class="breadcrumb-item"> <Link underline="hover" className="text-dark"  to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                <li class="breadcrumb-item"> <Link underline="hover" className="text-dark"  to={`/RamasProceso/${radicado}/${procesoDisciplinarioId}`}><small>Ramas del proceso</small></Link></li>
                                <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" aria-current="page"  to={`/EntidadInvestigadoLista/${radicado}/${procesoDisciplinarioId}`}><small>Entidad del investigado</small></Link></li>

                            </ol>
                        </nav>
                    </div>
                </div>
            </div>

            <div className="block block-themed">
                <div className="block-header">
                    <h3 className="block-title">SINPROC No {radicado} :: Clasificaci√≥n del radicado</h3>
                    <Link to={`/EntidadInvestigadoForm/${radicado}/${procesoDisciplinarioId}`}>
                        <button type="button" className="btn btn-sm btn-dark mr-1 mb-3"><i class="far fa-plus-square"></i> Agregar Entidad del investigado</button>
                    </Link>
                </div>

                <div className="block-content">

                <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                    <thead>
                        <tr>
                            <th>Entidad</th>
                            <th>Detalle</th>
                        </tr>
                    </thead>
                    <tbody>
                        { respuestaEntidadInvestigado ? listaEntidadInvestigado() : null }
                    </tbody>
                </table>

                <div className="text-right">
                        <Link to={`/RamasProceso/${radicado}/${procesoDisciplinarioId}`}>
                            <button type="button" className="btn btn-sm btn-success mr-1 mb-3"><i class="fas fa-backward"></i> Regresar </button>
                        </Link>
                    </div> 

                </div>
            </div>
        </>
    );

  
}
export default EntidadInvestigadoLista;
