    const [listaDependenciaOrigen, setListaDependenciaOrigen] = useState({ data: {} });
    const [respuestaDependenciaOrigen, setDependenciaOrigen] = useState(false);
    const [estadoEtapaCapturaReparto, setEstadoEtapaCapturaReparto] = useState(false);

    // Se utiliza en el modal panel para traer la información detallada de un antecedente
    const [antecedente, setAntecedente] = useState({});

    const location = useLocation()
    const { from } = location.state
	let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;
	let vigencia = from.vigencia;

    ///////////////////////////////////////////////////////////
    /////////////////// PARA LA BÚSQUEDA //////////////////////
    ///////////////////////////////////////////////////////////

    const [respuestaAntecedentes, setRespuestaAntecedentes] = useState(false);
    const [antecedentesLista, setAntecedentesLista] = useState({ data: [], links: [], meta: [] });

    const [respuestaAntecedentesTotal, setRespuestaAntecedentesTotal] = useState(false);
    const [antecedentesListaTotal, setAntecedentesListaTotal] = useState({ data: [], links: [], meta: [] });

    const handleSearch = (event) => {
        try {
            if ((event.target.value.trim().trim() == "") || (event.target.value.trim().length == 0)) {

                setAntecedentesLista(antecedentesListaTotal);
            }
            else {

                setAntecedentesLista(antecedentesListaTotal.data.filter(
                    suggestion => ((suggestion.attributes.descripcion
                        + suggestion.attributes.nombre_dependencia + suggestion.attributes.nombre_etapa +
                        suggestion.attributes.estado + suggestion.attributes.fecha_registro).toLowerCase().includes(event.target.value.toLowerCase()))
                ));
            }
        } catch (error) {
            console.error(error);
        }

    };

    const cambiarEstado = (id, positionLista) => {

        setRespuestaAntecedentes(false);

        let data;

        data = {
            "data": {
                "type": 'antecedente',
                "attributes": {
                    "antecedentes": 3,
                    "descripcion": "descripcion",
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "fecha_registro": Date.now(),
                    "created_user": "created_user",
                    "estado": "estado",

                }
            }
        }

        AntecedentesApi.updateAntecedente(data, id).then(
            datos => {
                if (!datos.error) {
                    antecedentesLista.data[positionLista].attributes.estado = datos.data.attributes.estado;
                    setAntecedentesLista(antecedentesLista);
                    setRespuestaAntecedentes(true)
                }
                else {
                    setErrorApi(datos.error.toString())
                    window.showModal()
                }
                //window.showSpinner(false);
            }
        )

    }


    const showModal = (id) => {
        window.showSpinner(true);
        //buscamos el detalle
        AntecedentesApi.getAntecedenteById(id).then(
            datos => {
                if (!datos.error) {
                    console.log(datos);
                    console.log(datos["data"]["attributes"]);
                    if (datos["data"]["attributes"]) {
                        setAntecedente(datos["data"]["attributes"]);
                        window.ModalDetalleAntecedente(datos["data"]["attributes"]);
                        window.showSpinner(false);
                    }
                }
                else {
                    setErrorApi(datos.error.toString())
                    window.showSpinner(false);
                    window.showModal(1)
                }
            }
        )
    }



    const listaAntecedentes = () => {

        if (antecedentesLista.data != null && typeof (antecedentesLista.data) != 'undefined') {
            return (


                antecedentesLista.data.map((antecedente, i) => {
                    return (
                        <tr key={antecedente.id}>
                            <td className="text-center">{i + 1}</td>
                            <td>{antecedente.attributes.created_user}</td>
                            <td>{antecedente.attributes.fecha_registro}</td>
                            <td>{antecedente.attributes.nombre_dependencia}</td>
                            <td data-toggle="popover" data-placement="top" title={antecedente.attributes.descripcion}>{antecedente.attributes.descripcion_corta}</td>
                            <td>{antecedente.attributes.nombre_etapa}</td>
                            <td>
                                <button className="btn btn-sm btn-light" onClick={() => cambiarEstado(antecedente.id, i)}><i className="fas fa-toggle-off col"></i></button>
                                <td><button className='btn btn-sm btn-light' onClick={() => showModal(antecedente.id)}><i class="fas fa-search"></i></button></td>
                            </td> 
                        </tr>


                    )
                })
            )
        }
        else {
            return (

                antecedentesLista.map((antecedente, i) => {
                    return (
                        <tr key={antecedente.id}>
                            <td className="text-center">{i + 1}</td>
                            <td>{antecedente.attributes.created_user}</td>
                            <td>{antecedente.attributes.fecha_registro}</td>
                            <td>{antecedente.attributes.nombre_dependencia}</td>
                            <td>{antecedente.attributes.descripcion}</td>
                            <td>{antecedente.attributes.nombre_etapa}</td>
                            <td>{antecedente.attributes.estado ? 'Activo' : 'Inactivo'}</td> 
                            <td>
                                <button className="btn btn-sm btn-light" onClick={() => cambiarEstado(antecedente.id, i)}><i className="fas fa-toggle-off col"></i></button>
                            </td> 
                        </tr>
                    )
                })
            )
        }

    }

    ///////////////////////////////////////////////////////////
    /////////////////// PARA LA BÚSQUEDA //////////////////////
    ///////////////////////////////////////////////////////////

    useEffect(() => {
        async function fetchData() {

            const data = {
                "data": {
                    "type": 'antecedente',
                    "attributes": {
                        "antecedentes": 3,
                        "descripcion": "abc",
                        "id_proceso_disciplinario": procesoDisciplinarioId,
                        "fecha_registro": Date.now(),
                        "id_dependencia": -1
                    }
                }
            }

            AntecedentesApi.getAllAntecedente(data, procesoDisciplinarioId).then(
                datos => !datos.error ? (setAntecedentesListaTotal(datos), setRespuestaAntecedentesTotal(true),
                    setAntecedentesLista(datos), setRespuestaAntecedentes(true)) : window.showModal(1)
            )

            ParametrosMasApi.getAllTiposDependenciaOrigen().then(
                datos => !datos.error ? (setListaDependenciaOrigen(datos), setDependenciaOrigen(true)) : window.showModal(1)
            )

             // VALIDA CIERRE DE ETAPA EVALUACION
             const dataCierreEtapa = {

                "data": {
                    "type": "cerrar_etapa",
                    "attributes": {
                        "id_proceso_disciplinario": "id_proceso_disciplinario",
                        "id_etapa": "id_etapa",
                        "id_delegada": "id_delegada",
                        "id_funcionario": "id_funcionario"
                    }
                }

            }

            CierreEtapaApi.getCierreEtapaByIdProDisciplinario(dataCierreEtapa, procesoDisciplinarioId, 1).then(
                datos => {
                    if (!datos.error) {
                        if (datos["data"].length > 0) {
                            setEstadoEtapaCapturaReparto(true);
                        }
                    }
                    else {
                        setErrorApi(datos.error.toString())
                        window.showModal(1)
                    }
                }
            )

        }
        fetchData();
    }, []);



    return (
        <> <Spinner />
         {<ModalDetalleAntecedente antecedente={antecedente} />}
            {<InfoErrorApi error={errorApi} />}
            {<InfoExitoApi />}

            <Formik>
                <Form>
                    <div className="col-md-12">
                        <div className="block block-rounded block-bordered">
                            <div className="block-content">
                                <nav aria-label="breadcrumb">
                                    <ol className="breadcrumb breadcrumb-alt push">

                                        <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                        <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`} state={{ from: new ParametroModel(radicado, procesoDisciplinarioId, vigencia)}}><small>Ramas del proceso</small></Link></li>
                                        <li class="breadcrumb-item"> <small>Antecedentes</small></li>
                                    </ol>
                                </nav>
                            </div>
                        </div>
                    </div>


                    <div className="block block-themed">
                        <div className="block-header">
                            <h3 className="block-title">SINPROC No {radicado} :: Antecedentes</h3>
                      
                            <Link to={`/AntencentesForm/`} state={{ from: new ParametroModel(radicado, procesoDisciplinarioId, vigencia)}}>
                                <button type="button" className="btn btn-sm btn-dark mr-1 mb-3"><i class="far fa-plus-square"></i> Agregar Antecedente</button>
                            </Link>
                        
                        </div>
                        <div className="block-content">
                            <div className='row'>

                                <div className='col-md-12'>
                                    <div className="form-group ">
                                        <label htmlFor='search'>Buscar: </label>
                                        <Field type="text" id="search" name="search" onChange={handleSearch} onKeyPress={(e) => { e.key === 'Enter' && e.preventDefault(); }} className="form-control border border-success" placeholder="Buscar" />

                                    </div>
                                </div>
                            </div>


                            <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                <thead>
                                    <tr>
                                        <th className="text-center">No</th>
                                        <th>Registrado por</th>
                                        <th>Fecha registro</th>
                                        <th>Dependencia</th>
                                        <th>Descripción</th>
                                        <th>Etapa</th>
                                        <th>Estado</th>
                                        <th>Acciones</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {respuestaAntecedentes ? listaAntecedentes() : null}
                                </tbody>
                            </table>

                            <div className="text-right">
                                <Link to={`/RamasProceso/`} state={{ from: new ParametroModel(radicado, procesoDisciplinarioId, vigencia)}}>
                                    <button type="button" className="btn btn-sm btn-success mr-1 mb-3"><i class="fas fa-backward"></i> Regresar </button>
                                </Link>
                            </div>
                        </div>
                    </div>

                </Form>
            </Formik>
        </>
    );
}


export default AntecedentesLista;
