import React, { useEffect, useState } from 'react';
import 'rhfa-emergency-styles/dist/styles.css'
import Spinner from '../Utils/Spinner';
import { ErrorMessage, Field, Form, Formik } from 'formik';
import { useParams } from "react-router";
import { Link, } from "react-router-dom";
import InfoErrorApi from '../Utils/InfoErrorApi';
import InfoExitoApi from '../Utils/InfoExitoApi';
import GenericApi from '../Api/Services/GenericApi';
import ModalGen from '../Utils/Modals/ModalGeneric';
import { Navigate } from "react-router-dom";
import { getUser } from '../Utils/Common';

function FaseDetalle() {

    let {id} = useParams();
    const [getNombre, setNombre] = useState('');
    const [getEstado, setEstado] = useState('');
    const [getListaPreguntas, setListaPreguntas] = useState({ data: {} });


    useEffect(() => {
        window.showSpinner(true);
        async function fetchData() {
            console.log("VALOR DE ID: "+id);
            cargarInfoFase();
        }
        fetchData();
    }, []);


    const handleInputChange = (event) => {
        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;

        if (name == "nombre") {
            setNombre(value);
        }

        if (name == "estado") {
            setEstado(value);
        }
    }


    const cargarInfoFase = () => {

        GenericApi.getByIdGeneric("mas-fase",id).then(
            datos => {
                if (!datos.error) {
                    setNombre(datos.data.attributes.nombre);
                    setEstado(datos.data.attributes.estado);
                    preguntasDocCierre();
                }
                else {
                   
                }
               
            }
        )
    }

    const preguntasDocCierre = () => {

        GenericApi.getGeneric("preguntas-doc-cierre-activas",id).then(
            datos => {
                if (!datos.error) {
                    setListaPreguntas(datos.data.attributes.nombre);
                }
                else {
                   
                }
                window.showSpinner(false);
            }
        )
    }



    


    const listarPreguntas = () => {

        return (
            getTiposAccesos.data.map((acceso, i) => {
                return (
                    <tr key={(acceso.id + 1)}>
                        <td>
                            <div className="custom-control custom-switch custom-control-lg mb-2">
                                <>
                                    {
                                        (getAccesosAsociados.length > 0) ? (
                                            (getAccesosAsociados.indexOf(acceso.id.toString()) > -1) ? (
                                                <>
                                                    <div>
                                                        <input defaultChecked={true} type="checkbox" onChange={e => agregarCheck(e.target.checked, acceso.id, i)} className="custom-control-input" id={acceso.id} name={acceso.id} />
                                                        <label className="custom-control-label" htmlFor={acceso.id}></label>
                                                    </div>
                                                </>
                                            ) :
                                                <div>
                                                    <input type="checkbox" onChange={e => agregarCheck(e.target.checked, acceso.id, i)} className="custom-control-input" id={acceso.id} name={acceso.id} />
                                                    <label className="custom-control-label" htmlFor={acceso.id}></label>
                                                </div>

                                        ) :
                                            <div>
                                                <input type="checkbox" onChange={e => agregarCheck(e.target.checked, acceso.id, i)} className="custom-control-input" id={acceso.id} name={acceso.id} />
                                                <label className="custom-control-label" htmlFor={acceso.id}></label>
                                            </div>
                                    }
                                </>


                            </div>
                        </td>
                        <td>
                            {acceso.attributes.nombre}
                        </td>
                        <td>
                            {
                                global.Constants.ACCESO_DEPENDENCIA.REMISION_QUEJA_COMISORIO_EJE == acceso.id && acceso.attributes.checked
                                    ?
                                    <input type="number" id={'porcentaje_' + i} name={'porcentaje_' + i} min='0' max='100' defaultValue={getPorcentajesAsociados[getAccesosAsociados.indexOf(acceso.id.toString())] || 100} className="form-control" onChange={e => agregarPorcentaje(e.target.value, acceso.id)} />
                                    :
                                    null
                            }
                        </td>
                    </tr>

                )
            })

        )

    }


    const componentFormularioFase = () => {

        return (
            <>
                <Spinner />
                <Formik
                    initialValues={{
                        nombre: '',
                        estado: '',
                    }
                    }
                    enableReinitialize
                    validate={(valores) => {

                        let errores = {}

                        return errores
                    }}
                    onSubmit={(valores, { resetForm }) => {
                        //enviarDatos();
                    }}
                >
                    {({ errors }) => (
                        <Form>
                            <div className='row'>
                                <div className="col-md-12">
                                    <div className="block block-themed">
                                        <div className="block-header">
                                            <h3 className="block-title">Datos Básicos</h3>
                                        </div>
                                        <div className="block-content">
                                            <div className="row">
                                                <div className="col-md-6">
                                                    <div className="form-group">
                                                        <label htmlFor='nombre'>Nombre </label>
                                                        <Field value={getNombre} type="text" id="nombre" name="nombre" className="form-control" placeholder="Nombre" onChange={handleInputChange}/>
                                                        <ErrorMessage name="nombre" component={() => (<span className="text-danger">{errors.nombre}</span>)} />
                                                    </div>
                                                </div>

                                                <div className="col-md-6">
                                                    <div className="form-group">
                                                        <label htmlFor='user'>Estado </label>
                                                        <Field as="select" value={getEstado}  className="form-control" id="estado" name="estado" onChange={handleInputChange}>
                                                            <option value="">Por favor seleccione</option>
                                                            <option value="1">Activo</option>
                                                            <option value="0">Inactivo</option>
                                                        </Field>
                                                        <ErrorMessage name="estado" component={() => (<span className="text-danger">{errors.estado}</span>)} />
                                                    </div>
                                                </div>

                                            </div>

                                            <div className="row mt-3" >

                                                <div className="col-md-12">
                                                    <ErrorMessage name="accesos" component={() => (<span className="text-danger">{errors.accesos}</span>)} />
                                                </div>

                                                <div className="col-md-12">
                                                    <label>¿Preguntas Documento Cierre? </label>
                                                </div>

                                                <div className="col-md-12" style={{ 'height': '500px', 'overflow': 'scroll', 'display': 'block' }}>
                                                    <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                                        <thead>
                                                            <tr>
                                                                <th>Activar/Inactivar</th>
                                                                <th>Pregunta</th>
                                                            </tr>
                                                        </thead>
                                                        <tbody >
                                                            {/*getListaAccesosAsociados ? listarAccessoAsociados() : null*/}
                                                        </tbody>
                                                    </table>
                                                </div>
                                            </div>
                                        </div>

                                        <div className="block-content block-content-full text-right">
                                            <button type="submit" className="btn btn-rounded btn-primary">
                                                 Actualizar
                                            </button>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </Form>
                    )}
                </Formik>
            </>
        )
    }


    return (

        <>
           

            <div className="w2d_block">
                <nav aria-label="breadcrumb">
                    <ol className="breadcrumb breadcrumb-alt push">
                        <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/Fase`}><small>Lista de fases</small></Link></li>
                        <li class="breadcrumb-item"> <small>Editar Fase</small></li>
                    </ol>
                </nav>
            </div>

            <div className="col-md-12">
                {componentFormularioFase()}
            </div>
        </>
    )



};

export default FaseDetalle;
