import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import { getUser } from '../../Utils/Common';
import { useLocation } from 'react-router-dom';
import '../../Utils/Constants';
import ModalGen from '../../Utils/Modals/ModalGeneric';
import GenericApi from '../../Api/Services/GenericApi';
import Spinner from '../../Utils/Spinner';

function AntecedentesMigracionLista() {

    const [getModalState, setModalState] = useState({ title: "", message: "", show: false });
    const [getListaAntecedentes, setListaAntecedentes] = useState({ data: [], links: [], meta: [] });

   
    const location = useLocation()
    const { from } = location.state


    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);
            listaAntecedentes();
            
           
        }
        fetchData();
    }, []);


    const listaAntecedentes= () => {
        try {

            //buscamos el parametro
            GenericApi.getGeneric("migracion-lista-antecedentes/51238/1/1",).then(
                datos => {

                    if (!datos.error) {
                        setListaAntecedentes(datos);

                    } else {
                        setModalState({ title: "Antecedentes", message: datos.error.toString(), show: true, redirect: '/AntecedentesForm', from: {from}, alert: global.Constants.TIPO_ALERTA.ERROR });
                    }
                    window.showSpinner(false);
                }
            )
        } catch (error) {
            // console.log(error);
        }
    }


    const procesoDisciplinario = () => {

        if (getListaAntecedentes.data != null && typeof (getListaAntecedentes.data) != 'undefined') {
            return (

                getListaAntecedentes.data.map((item, i) => {
                    return (
                        <tr>
                            <td>{i=i+1}</td>
                            <td>{item.attributes.antecedente}</td>
                            <td><Link className="text-dark" to={`/CargarMigracionAntecedentes/${item.attributes.id}`} state={{ from: from }}><small>Completar información</small></Link></td>
                        </tr>
                    )
                })
            )
        }
    }


    return (
        <>
            {<Spinner />}   
            {<ModalGen data={getModalState} />}
          
            <div className="w2d_block">
                <nav aria-label="breadcrumb">
                    <ol className="breadcrumb breadcrumb-alt push">
                        <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/Buscador`}><small>Buscador</small></Link></li>
                        <li class="breadcrumb-item"> <small>Lista de antecedentes</small></li>
                    </ol>
                </nav>
            </div>

            <div className="block block-themed">
                <div className="block-header">
                    <h3 className="block-title">Proceso de migración</h3>
                </div>

                <div className="block-content">
                    
                    <div className="col-md-12">

                        <table class="table">
                            <thead>
                                <tr>
                                    <th scope="col">#</th>
                                    <th scope="col" style={{width: "80%"}}>Antecedente</th>
                                    <th scope="col">Acciones</th>
                                </tr>
                            </thead>
                            <tbody>
                                   {procesoDisciplinario()}                 
                            </tbody>
                        </table>
                        
                    </div>
                </div>
                
            </div>

                   
        </>
    );
}


export default AntecedentesMigracionLista;
