    /*Redirects*/
    const redirectToRoutes = () => {
        return <Navigate to={`/AntecedentesLista/`} state={{ from: from}}/>;
    }

    useEffect(() => {
        async function fetchData() {

            //window.showSpinner(true);
            setNombreUsuario(getUser().nombre);

            console.log(antecedentesId);

            
        }
        fetchData();
    }, []);



    
    const getTablaInfoAntecedente = () => {

        AntecedentesApi.getAntecedenteById(antecedentesId).then(
            datos => {

                console.log(datos['data']);

                return(
                    <tr>

                    </tr>

                )

               
            }
        )

    }






    const cargarTabla = (antecedente, i) => {
        return (
            <tr key={antecedente.id}>
                <td style={{width: "3%"}} className="text-center">{i + 1}</td>
                <td style={{width: "15%"}}> 
                {antecedente.attributes.created_user}<br/>
                <strong>Etapa: </strong>{antecedente.attributes.etapa.nombre}
                </td>
                <td style={{width: "15%"}}>{antecedente.attributes.fecha_registro}</td>
                <td style={{width: "15%"}}>{antecedente.attributes.nombre_dependencia}</td>
                <td style={{width: "30%"}}  data-toggle="popover" data-placement="top" title={antecedente.attributes.descripcion}>{antecedente.attributes.descripcion_corta} <i class="fas fa-ellipsis-h"></i></td>
                <td>
                    <button type='button' title='Consultar Antecedente' className='btn btn-sm btn-primary' data-toggle="modal" data-target={'#modal-consultar-detalle'+i}><i class="fas fa-search"></i></button>
                        {
                            (antecedente.attributes.estado == 0) ? (
                                <Link to={`/AntencentesCambiarEstadoForm/`} state={{ from: from }}>
                                    <button type='button' title='Activar Antecedente' className='btn btn-sm btn-success' data-toggle="modal" data-target={'#modal-cambiar-estado'+i}><i className="fas fa-plus-circle"></i></button>
                                </Link>
                                ) : null
                        }

                        {
                            (antecedente.attributes.estado == 1) ? (
                                <Link to={`/AntencentesCambiarEstadoForm/`} state={{ from: from }}>
                                     <button type='button' title='Inactivar Antecedente' className='btn btn-sm btn-danger'><i className="fas fa-minus-circle"></i></button>
                                </Link>

                               
                            ) : null
                        }
                </td>

                <div class="modal" id={'modal-consultar-detalle'+i} tabindex="-1" role="dialog" aria-labelledby="modal-block-normal" aria-hidden="true">
                    <div class="modal-dialog" role="document">
                        <div class="modal-content">
                            <div class="block block-themed block-transparent mb-0">
                                <div class="block-header bg-primary-dark">
                                    <h3 class="block-title">Descripción del antecedente</h3>
                                    <div class="block-options">
                                        <button type="button" class="btn-block-option" data-dismiss="modal" aria-label="Close">
                                            <i class="fa fa-fw fa-times"></i>
                                        </button>
                                    </div>
                                </div>
                                <div class="block-content">
                                    {antecedente.attributes.descripcion}
                                </div>
                                <div class="block-content block-content-full text-right bg-light">
                                    <button type="button" class="btn btn-sm btn-primary" data-dismiss="modal">Cerrar</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>

            </tr>

        )
    }



    return (
        <>
            {isRedirect ? redirectToRoutes() : null}
        
            {<InfoErrorApi error={errorApi} />}
            {<InfoExitoApi />}
            <Formik
                initialValues={{
                    descripcion: ''
                }}
                enableReinitialize
                validate={(valores) => {
                    let errores = {}
                    setCountTextArea(valores.descripcion.length)
                    if (!valores.descripcion) {
                        errores.descripcion = 'Debe ingresar un antecedente'
                    }
                    else if (valores.descripcion.length < 10) {
                        errores.descripcion = 'Debe ingresar mínimo 10 caracteres'
                    }
                    else if (valores.descripcion.length > 4000) {
                        errores.descripcion = 'Debe ingresar mínimo 10 caracteres'
                    }
                    return errores
                }}
                onSubmit={(valores, { resetForm }) => {
                    //enviarDatos(valores);
                }}
            >

                {({ errors }) => (
                    <Form>
                        <div className="col-md-12">
                            <div className="block block-rounded block-bordered">
                                <div className="block-content">
                                    <nav aria-label="breadcrumb">
                                        <ol className="breadcrumb breadcrumb-alt push">
                                            <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                            <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`} state={{ from: from}}><small>Ramas del proceso</small></Link></li>
                                            <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/AntecedentesLista/`} state={{ from: from}}><small>Antecedentes</small></Link></li>
                                            <li class="breadcrumb-item"> <small>Cambiar estado de antecedente</small></li>
                                        </ol>
                                    </nav>
                                </div>
                            </div>
                        </div>

                        <div className="block block-themed">
                            <div className="block-header">
                                <h3 className="block-title">SINPROC No {radicado} :: Antecedentes</h3>
                            </div>
                            <div className="block-content">

                            
                            <div className='col-md-12'>

                                <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                    <thead>
                                        <tr>
                                            <th className="text-center">No</th>
                                            <th>Registrado por</th>
                                            <th>Fecha registro</th>
                                            <th>Dependencia</th>
                                            <th>Descripción</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                      
                                    </tbody>
                                </table>
                            </div>
                             



                                <div className="col-md-12">
                                    <div className="form-group">
                                        <label htmlFor="descripcion">Descripción <span className="text-danger">*</span></label>
                                        <Field as="textarea" className="form-control" id="descripcion" name="descripcion" rows="4" placeholder="Escriba en este espacio los antencedentes"></Field>
                                        <div className="text-right">
                                            <span className="text-primary">{countTextArea} / 4000</span>
                                        </div>
                                        <ErrorMessage name="descripcion" component={() => (<span className="text-danger">{errors.descripcion}</span>)} />
                                    </div>
                                </div>
                            </div>
                            <div className="block-content block-content-full text-right bg-light">
                                <button type="submit" className="btn btn-primary"><i className="fas fa-save"></i> Registrar</button>

                                <Link to={`/AntecedentesLista/`} state={{ from: from}}>
                                    <button type="button" className="btn btn-danger"><i class="fas fa-window-close"></i> Cancelar</button>
                                </Link>
                            </div>
                        </div>

                    </Form>
                )}
            </Formik>
        </>
    );
}


export default AntecedentesCambiarEstadoForm;
