import { Navigate } from "react-router-dom";
import { DragDropContext, Draggable, Droppable } from "react-beautiful-dnd";
import ParametrosMasApi from '../Api/Services/ParametrosMasApi';


function FuncionarioListaGestorRespuesta() {

    const [getSeach, setSeach] = useState('');
    const [errorApi, setErrorApi] = useState('');
    const [isRedirect, setIsRedirect] = useState(false);

    const [getRolesListaSearch, setRolesListaSearch] = useState([]);
    const paganationPerPages = process.env.PAGINATION_PER_PAGE;
    const [perPage, setPerPage] = useState(process.env.PAGINATION_PER_PAGE);
    const [pageActual, setPageActual] = useState(1);
    
    const [getRolesList, setRolesList] = useState([]);
    
    const [getAlert, setAlert] = useState({ titulo: null, cuerpo: null, boton_aceptar: null, boton_cancelar: null });
    const [getShowAlert, setShowAlert] = useState(false);

    const redirectToRoutes = () => {
        return <Navigate to={`/RolesLista/`} />;
    }

    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);
            GenericApi.getAllGeneric('modulo/get-funcionalidad-modulo/TiposUsuario').then(
                datos => {
                    if (!datos.error) {
                        if (datos["data"] != "") {
                            setRolesListaSearch(datos["data"][0]?.attributes?.funcionalidades)
                            
                            GenericApi.getAllGeneric('mas-orden-funcionario').then(
                                datos_funcionarios => {
                                    if (!datos_funcionarios.error) {
                                        if (datos_funcionarios["data"] != "") {
                                            setRolesList(datos_funcionarios["data"]);
                                        }
                                    }
                                    else {
                                        setErrorApi(datos_funcionarios.error.toString())
                                        window.showModal(1)
                                    }
                                }
                            )

                            window.showSpinner(false);
                        }
                        else {
                            setErrorApi(datos.error.toString())
                            window.showModal(1)
                            window.showSpinner(false);    
                        }
                    }
                    else {
                        setErrorApi(datos.error.toString())
                        window.showModal(1)
                        window.showSpinner(false);
                    }
                }
            )
        }
        fetchData();
    }, []);

    const agregarUsuario = (valores) => {
        //setRolesList([...getRolesList,  getRolesListaSearch.find(rol => rol.id == valores.rol)]);
        let funcionarios = getRolesList;
        setRolesList([]);
        funcionarios.push(
            {
                "id": valores.rol,
                "id_lista_funcionarios": getRolesList.length + "",
                "nombre": getRolesListaSearch.find(rol => rol.id == valores.rol).nombre,
                "orden": getRolesList.length + ""
            }
        )
        setRolesList([...funcionarios]);
    }

    const eliminarUsuario = (index) => {
        setRolesList(getRolesList.filter((img, i) => i !== index));
    }

    const handleDragEnd = (e) => {
        if (!e.destination) return;
        let tempData = Array.from(getRolesList);
        let [source_data] = tempData.splice(e.source.index, 1);
        tempData.splice(e.destination.index, 0, source_data);
        setRolesList(tempData);
    };

    const confirmacionRegistro = () => {        
        setShowAlert(true);
        console.log("Entro");
        setAlert(
            {
                titulo: 'Confirmación de guardado',
                cuerpo: '¿Desea modificar la lista de funcionarios? Recuerde que la modificación de la lista afectara a los nuevos procesos disciplinarios que estén a punto de realizar la fase de GESTOR RESPUESTA',
                boton_aceptar: 'Si',
                boton_cancelar: 'No'
            }
        );
        window.showModal(7);
    }

    const cancelarAlerta = () => {
        setShowAlert(false);
    }

    const guardarDatos = () => {

        setShowAlert(false);

        const attributes = [];

        getRolesList.forEach((funcionario, index) => {
            attributes.push({
                "id_funcionario": funcionario.id,
                "orden": (index + 1)
            })
        });

        const data = {
            "data": {
                "type": "remision_queja",
                "attributes": attributes
            }
        }

        window.showSpinner(true);

        ParametrosMasApi.addFuncionariosGestorRespuesta(data).then(
            datos => {
                if (!datos.error) {                   
                    window.showModal(2);
                    window.showSpinner(false);
                }
                else {
                    console.log(datos.error);
                    setErrorApi(datos.error);
                    window.showModal(1);
                    window.showSpinner(false);
                }
            }
        )
    }

    /*
    * Componentes
    */

    const componentAlertConfirmacion = () => {
        window.showModal(6);
        return (
            <>
                <div className="modal fade" id="modal-block-popout-exito-texto-personalizado" tabIndex="-1" role="dialog" aria-labelledby="modal-block-popout" aria-hidden="true">
                    <div className="modal-dialog modal-dialog-popout" role="document">
                        <div className="modal-content">
                            <div className="block block-themed block-transparent mb-0">
                                <div className="block-header bg-primary-dark">
                                    <h3 className="block-title">{ getAlert.titulo }</h3>
                                    <div className="block-options">
                                        <button type="button" className="btn-block-option" data-dismiss="modal" aria-label="Close">
                                            <i className="fa fa-fw fa-times"></i>
                                        </button>
                                    </div>
                                </div>
                                <div className="block-content">
                                    <p>{ getAlert.cuerpo }</p>
                                </div>
                                <div className="block-content block-content-full text-right bg-light">
                                    <button type="button" className="btn btn-sm btn-light" data-dismiss="modal" onClick={() => cancelarAlerta()}>{ getAlert.boton_cancelar }</button>
                                    <button type="button" className="btn btn-sm btn-primary" data-dismiss="modal" onClick={() => guardarDatos()}>{ getAlert.boton_aceptar }</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </>
        );

    }

    const componentSelectRol = () => {
        return (
            getRolesListaSearch.map((rol, i) => {
                return (
                    <option key={rol.id} value={rol.id}>{rol.nombre}</option>
                )
            })
        )
    }

    const componentRolForm = () => {
        return (
            <>
                <Formik
                    initialValues={{
                        rol: ''
                    }}
                    enableReinitialize
                    validate={(valores) => { 
                        let errores = {};

                        if(!valores.rol){
                            errores.rol = 'Debe seleccionar el rol';
                        }

                        return errores;
                    }}
                    onSubmit={(valores, {resetForm}) => {
                        agregarUsuario(valores);
                    }}
                >
                    {({ errors }) => (
                        <Form>
                            <div className="block-content">
                                <div className="row">
                                    <div className="col-md-10">
                                        <label htmlFor='rol'>Tipo funcionario <span className='text-danger'>*</span></label>
                                        <Field as='select' className='form-control' id='rol' name='rol'>
                                            <option value=''>Seleccione un tipo funcionario</option>                                            
                                            {getRolesListaSearch.length > 0 ? componentSelectRol() : null}
                                        </Field>
                                        <ErrorMessage name='rol'component={() => (<span className='text-danger'>{errors.rol}</span>)} />
                                    </div>

                                    <div className="col-md-12">
                                        <div className="block-content block-content-full text-right bg-light">
                                            <button type="submit" className="btn btn-primary"><i className="fas fa-plus"></i> Agregar</button>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </Form>
                    )}
                </Formik>
            </>
        );
    }

    return (
        <>
            { getShowAlert ? componentAlertConfirmacion() : null }
            {isRedirect ? redirectToRoutes() : null}
            {<Spinner />}
            {<InfoErrorApi error={errorApi} />}
            {<InfoExitoApi />}
            <div className="block block-themed">
                <div className="col-md-12">

                    <div className="block-content">
                        <nav aria-label="breadcrumb">
                            <ol className="breadcrumb breadcrumb-alt push">
                                <li className="breadcrumb-item"> <small>Lista de Funcionarios</small></li>
                            </ol>
                        </nav>
                    </div>

                </div>
                <div className="block-header">
                    <h3 className="block-title">Tipos de usuario</h3>

                    <Link to={`/Rol/Add`} >
                        <button type="button" className="btn btn-sm btn-light mr-1 mb-3"><i className="fas fa-plus"></i> Agregar Rol</button>
                    </Link>
                </div>

                { componentRolForm() }

                <div className="block-content">
                    <div className="row">
                        <div className="col-md-12">
                            <DragDropContext onDragEnd={handleDragEnd}>
                                <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                    <thead>
                                        <tr>
                                            <th width="5%">Mover</th>
                                            <th width="5%">No</th>
                                            <th width="45%">Rol</th>
                                            <th width="30%">Acciones</th>
                                        </tr>
                                    </thead>
                                    <Droppable droppableId="droppable-1">
                                        {(provider) => (
                                            <tbody
                                                className="text-capitalize"
                                                ref={provider.innerRef}
                                                {...provider.droppableProps}
                                            >
                                                {getRolesList?.map((funcionario, index) => (
                                                    <Draggable
                                                        key={funcionario.id_lista_funcionarios}
                                                        draggableId={funcionario.id_lista_funcionarios}
                                                        index={index}
                                                    >
                                                        {(provider) => (
                                                            <tr {...provider.draggableProps} ref={provider.innerRef}>
                                                                <td {...provider.dragHandleProps}> <i className="fas fa-hand-paper"></i> </td>
                                                                <td>{ index + 1 }</td>
                                                                <td>{funcionario.nombre}</td>
                                                                <td>
                                                                    {
                                                                        getRolesList.length > 1 ?
                                                                        (
                                                                            <>
                                                                                <button type='button' title='Eliminar Rol' className='btn btn-sm btn-danger' onClick={() => eliminarUsuario(index)}><i className="fas fa-minus-circle"></i></button>
                                                                            </>
                                                                        )
                                                                        :
                                                                        null
                                                                    }
                                                                </td>
                                                            </tr>
                                                        )}
                                                    </Draggable>
                                                ))}
                                                {provider.placeholder}
                                            </tbody>
                                        )}
                                    </Droppable>
                                </table>
                            </DragDropContext>
                        </div>

                        <div className="col-md-12">
                            <div className="block-content block-content-full text-right bg-light">
                                <button type="button" className="btn btn-primary" onClick={() => confirmacionRegistro()}><i className="fas fa-save"></i> Registrar</button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}

export default FuncionarioListaGestorRespuesta;
