import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { Link, } from "react-router-dom";
import InfoErrorApi from '../Utils/InfoErrorApi';
import InfoExitoApi from '../Utils/InfoExitoApi';
import GenericApi from '../Api/Services/GenericApi';
import Spinner from '../Utils/Spinner';
import { Navigate } from "react-router-dom";
import { DragDropContext, Draggable, Droppable } from "react-beautiful-dnd";
import ParametrosMasApi from '../Api/Services/ParametrosMasApi';
import ModalListaVersionesGestorRespuestaAdministracion from '../Utils/Modals/ModalListaVersionesGestorRespuestaAdministracion';
import '../Utils/Constants';

function FuncionarioListaGestorRespuesta() {

    const [getSeach, setSeach] = useState('');
    const [errorApi, setErrorApi] = useState('');
    const [isRedirect, setIsRedirect] = useState(false);

    const paganationPerPages = global.Constants.DATA_TABLE.PAGINATION_PER_PAGE;
    const [perPage, setPerPage] = useState(global.Constants.DATA_TABLE.PAGINATION_PER_PAGE);
    const [pageActual, setPageActual] = useState(1);
    
    const [getListaResultadoEvaluacion, setListaResultadoEvaluacion] = useState([]);
    const [getFuncionalidadList, setFuncionalidadList] = useState([]);
    const [getFuncionalidadListaSearch, setFuncionalidadListaSearch] = useState([]);
    const [getEvaluacion, setEvaluacion] = useState();
    const [getEvaluacionNombre, setEvaluacionNombre] = useState("REALIZANDO");

    const redirectToRoutes = () => {
        return <Navigate to={`/RolesLista/`} />;
    }

    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);
            GenericApi.getAllGeneric('modulo/get-funcionalidad-modulo/TiposUsuario').then(
                datos => {
                    if (!datos.error) {
                        if (datos["data"] != "") {
                            setFuncionalidadListaSearch(datos["data"][0]?.attributes?.funcionalidades)
                            window.showSpinner(false);
                        }
                        else {
                            setErrorApi(datos.error.toString())
                            window.showModal(1)
                            window.showSpinner(false);    
                        }
                    }
                    else {
                        setErrorApi(datos.error.toString())
                        window.showModal(1)
                        window.showSpinner(false);
                    }
                }
            );

            ParametrosMasApi.getAllResultadoEvaluacion().then(
                data => {
                    if (!data.error) {
                        setListaResultadoEvaluacion(data["data"])
                    }
                    else {
                        window.showModal(1);
                    }
                }
            )

        }
        fetchData();
    }, []);

    const agregarUsuario = (valores) => {
        let funcionarios = getFuncionalidadList;
        setFuncionalidadList([]);
        funcionarios.push(
            {
                "id": valores.rol,
                "id_lista_funcionarios": getFuncionalidadList.length + "",
                "nombre": getFuncionalidadListaSearch.find(rol => rol.id == valores.rol).nombre,
                "orden": getFuncionalidadList.length + "",
                "id_evaluacion": getEvaluacion
            }
        )
        setFuncionalidadList([...funcionarios]);        
        window.showModalAgregarFuncionarioGestorRespuesta(false);
    }

    const eliminarUsuario = (index) => {
        setFuncionalidadList(getFuncionalidadList.filter((img, i) => i !== index));
    }

    const handleDragEnd = (e) => {
        if (!e.destination) return;
        let tempData = Array.from(getFuncionalidadList);
        let [source_data] = tempData.splice(e.source.index, 1);
        tempData.splice(e.destination.index, 0, source_data);
        setFuncionalidadList(tempData);
    };

    const showModalConfirmacion = () => {
        window.showModalConfirmacionAdministradorGestorRespuesta();
    }

    const showModalAgregarFuncionario = () => {
        window.showModalAgregarFuncionarioGestorRespuesta(true);
    }

    const showModalListaParametrizadaPrevias = () => {
        window.showModalListaVersionesGestorRespuestaAdminstracion();
    }

    const guardarDatos = () => {
        const attributes = [];

        getFuncionalidadList.forEach((funcionario, index) => {
            attributes.push({
                "id_funcionario": funcionario.id,
                "orden": (index + 1),
                "id_evaluacion": funcionario.id_evaluacion
            })
        });

        const data = {
            "data": {
                "type": "remision_queja",
                "attributes": attributes
            }
        }

        window.showSpinner(true);

        ParametrosMasApi.addFuncionariosGestorRespuesta(data).then(
            datos => {
                if (!datos.error) {                   
                    window.showModal(2);
                    window.showSpinner(false);
                }
                else {
                    console.log(datos.error);
                    setErrorApi(datos.error);
                    window.showModal(1);
                    window.showSpinner(false);
                }
            }
        )
    }

    const obtenerDatosLista = (id_evaluacion) => {
        setEvaluacion(id_evaluacion);
        setEvaluacionNombre(getListaResultadoEvaluacion.find(data => data.id == id_evaluacion).attributes.nombre);
        window.showSpinner(true);
        GenericApi.getAllGeneric('mas-orden-funcionario/'+id_evaluacion).then(
            datos_funcionarios => {
                if (!datos_funcionarios.error) {
                    setFuncionalidadList(datos_funcionarios["data"]);
                }
                else {
                    setErrorApi(datos_funcionarios.error.toString())
                    window.showModal(1)
                }
                window.showSpinner(false);
            }
        )
    }

    /*
    * Componentes
    */

    const componentAlertConfirmacion = () => {
        return (
            <>
                <div className="modal fade" id="modal-block-popout-confirmacion-administracion-gestor-respuesta" tabIndex="-1" role="dialog" aria-labelledby="modal-block-popout" aria-hidden="true">
                    <div className="modal-dialog modal-dialog-popout" role="document">
                        <div className="modal-content">
                            <div className="block block-themed block-transparent mb-0">
                                <div className="block-header bg-primary-dark">
                                    <h3 className="block-title">Confirmación de guardado</h3>
                                    <div className="block-options">
                                        <button type="button" className="btn-block-option" data-dismiss="modal" aria-label="Close">
                                            <i className="fa fa-fw fa-times"></i>
                                        </button>
                                    </div>
                                </div>
                                <div className="block-content">
                                    <p>¿Desea modificar la lista de funcionarios? Recuerde que la modificación de la lista afectara a los nuevos procesos disciplinarios que estén a punto de realizar la fase de GESTOR RESPUESTA</p>
                                </div>
                                <div className="block-content block-content-full text-right bg-light">
                                    <button type="button" className="btn btn-sm btn-light" data-dismiss="modal">Cancelar</button>
                                    <button type="button" className="btn btn-sm btn-primary" data-dismiss="modal" onClick={() => guardarDatos()}>Registrar</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </>
        );
    }

    const componentSelectRol = () => {
        return (
            getFuncionalidadListaSearch.map((rol, i) => {
                return (
                    <option key={rol.id} value={rol.id}>{rol.nombre}</option>
                )
            })
        )
    }

    const componentSelectResultadoEvaluacion = () => {
        return (
            getListaResultadoEvaluacion.map((resultado, i) => {
                return (
                    <option key={resultado.id} value={resultado.id}>{resultado.attributes.nombre}</option>
                )
            })
        )
    }

    const componentAlertAgregarUsuario = () => {
        return (
            <>
                <Formik
                    initialValues={{
                        rol: '',
                    }}
                    enableReinitialize
                    validate={(valores) => { 
                        let errores = {};

                        if(!valores.rol){
                            errores.rol = 'Debe seleccionar el rol';
                        }

                        return errores;
                    }}
                    onSubmit={(valores, {resetForm}) => {
                        agregarUsuario(valores);
                        resetForm();
                    }}
                >
                    {({ errors, values }) => (
                        <Form>
                            <div className="modal fade" id="modal-block-popout-agregar-funcionario-gestor-respuesta" tabIndex="-1" role="dialog" aria-labelledby="modal-block-popout" aria-hidden="true">
                                <div className="modal-dialog modal-dialog-popout" role="document">
                                    <div className="modal-content">
                                        <div className="block block-themed block-transparent mb-0">
                                            <div className="block-header bg-primary-dark">
                                                <h3 className="block-title">Agregar Usuario</h3>
                                                <div className="block-options">
                                                    <button type="button" className="btn-block-option" data-dismiss="modal" aria-label="Close">
                                                        <i className="fa fa-fw fa-times"></i>
                                                    </button>
                                                </div>
                                            </div>
                                            <div className="block-content">
                                                <div className="row">
                                                    <div className="col-md-12">
                                                        <label htmlFor='rol'>Tipo funcionario <span className='text-danger'>*</span></label>
                                                        <Field as='select' className='form-control' id='rol' name='rol'>
                                                            <option value=''>Seleccione un tipo funcionario</option>                                            
                                                            { getFuncionalidadListaSearch.length > 0 ? componentSelectRol() : null }
                                                        </Field>
                                                        <ErrorMessage name='rol'component={() => (<span className='text-danger'>{errors.rol}</span>)} />
                                                    </div>
                                                </div>
                                                <br></br>
                                            </div>
                                            <div className="block-content block-content-full text-right bg-light">
                                                <button type="button" className="btn btn-sm btn-light" data-dismiss="modal">Cancelar</button>
                                                <button type="submit" className="btn btn-sm btn-primary">Agregar Usuario</button>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </Form>
                    )}
                </Formik>
            </>
        );
    }

    return (
        <>
            { componentAlertAgregarUsuario()  }
            { componentAlertConfirmacion() }
            { getEvaluacion ? <ModalListaVersionesGestorRespuestaAdministracion object={getEvaluacion} nombre={getEvaluacionNombre}></ModalListaVersionesGestorRespuestaAdministracion> : null }
            {isRedirect ? redirectToRoutes() : null}
            {<Spinner />}
            {<InfoErrorApi error={errorApi} />}
            {<InfoExitoApi />}
            <div className="block block-themed">
                <div className="block-content">
                    <nav aria-label="breadcrumb">
                        <ol className="breadcrumb breadcrumb-alt push">
                            <li className="breadcrumb-item"> <small>Lista de Funcionarios</small></li>
                        </ol>
                    </nav>
                </div>
                <div className="block-header">
                    <h3 className="block-title">Tipos de usuario</h3>
                </div>
                
                <div className="block-content">
                    <div className='row'>
                        <div className='col-md-9'>
                            <div className="form-group">
                                <label htmlFor='evaluacion'>Tipo de evaluación <span className='text-danger'>*</span></label>
                                <select className='form-control' id='evaluacion' name='evaluacion' onChange={e => obtenerDatosLista(e.target.value)}>
                                    <option value=''>Seleccione el tipo de evaluación</option>                                            
                                    { getListaResultadoEvaluacion.length > 0 ? componentSelectResultadoEvaluacion() : null }
                                </select>
                            </div>
                        </div>
                        <div className='col-md-3 text-right'>
                            {
                                getEvaluacion
                                ?
                                <>
                                    <button type="button" className="btn btn btn-primary mr-1 mb-3" data-toggle="tooltip" data-html="true" title="Consultar Listas Previas" onClick={() => showModalListaParametrizadaPrevias()} data-original-title="Consultar Versiones"><span className="fas fa-clipboard"> </span></button>
                                    <button type="button" className="btn btn btn-success mr-1 mb-3" data-toggle="tooltip" data-html="true" title="Registrar" onClick={() => showModalAgregarFuncionario()} data-original-title="Consultar Versiones"><span className="fas fa-plus"> </span></button>
                                </>
                                :
                                null
                            }
                        </div>
                    </div>
                </div>
                {
                    getEvaluacion
                    ?
                    (
                        getFuncionalidadList.length > 0
                        ?
                            <>
                                <div className="block-content">
                                    <div className="row">
                                        <div className="col-md-12">
                                            <DragDropContext onDragEnd={handleDragEnd}>
                                                <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                                    <thead>
                                                        <tr>
                                                            <th width="5%">Mover</th>
                                                            <th width="5%">No</th>
                                                            <th width="45%">Rol</th>
                                                            <th width="30%">Acciones</th>
                                                        </tr>
                                                    </thead>
                                                    <Droppable droppableId="droppable-1">
                                                        {(provider) => (
                                                            <tbody
                                                                className="text-capitalize"
                                                                ref={provider.innerRef}
                                                                {...provider.droppableProps}
                                                            >
                                                                {getFuncionalidadList.map((funcionario, index) => (
                                                                    <Draggable
                                                                        key={funcionario.id_lista_funcionarios}
                                                                        draggableId={funcionario.id_lista_funcionarios}
                                                                        index={index}
                                                                    >
                                                                        {(provider) => (
                                                                            <tr {...provider.draggableProps} ref={provider.innerRef}>
                                                                                <td {...provider.dragHandleProps}> <i className="fas fa-hand-paper"></i> </td>
                                                                                <td>{ index + 1 }</td>
                                                                                <td>{funcionario.nombre}</td>
                                                                                <td>
                                                                                    {
                                                                                        getFuncionalidadList.length > 1 ?
                                                                                        (
                                                                                            <>
                                                                                                <button type='button' title='Eliminar Rol' className='btn btn-sm btn-danger' onClick={() => eliminarUsuario(index)}><i className="fas fa-minus-circle"></i></button>
                                                                                            </>
                                                                                        )
                                                                                        :
                                                                                        null
                                                                                    }
                                                                                </td>
                                                                            </tr>
                                                                        )}
                                                                    </Draggable>
                                                                ))}
                                                                {provider.placeholder}
                                                            </tbody>
                                                        )}
                                                    </Droppable>
                                                </table>
                                            </DragDropContext>
                                        </div>
                                    </div>
                                    <div className="row">
                                        <div className="col-md-12">
                                            <div className="block-content block-content-full text-right bg-light">
                                                <button type="button" className="btn btn-primary mr-2" data-toggle="tooltip" data-html="true" title="Registrar" onClick={() => showModalConfirmacion()} data-original-title="Consultar Versiones"><span className="fas fa-save"> </span> Registrar</button>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </>
                        :
                        (
                            <>
                                <div className="row">
                                    <div className="col-md-12 text-center">
                                        <b>Sin información, registrada</b>
                                    </div>
                                </div>
                                <br></br>
                            </>
                        )
                    )                    
                    :
                    null
                }
            </div>
        </>
    );
}

export default FuncionarioListaGestorRespuesta;
