                                </div>

                                <div className="col-md-12">
                                    <div className="form-group">
                                        <i className="fa fa-paper-plane text-success fa-2x mr-3"></i>
                                        <label htmlFor="enviarAOtraDependencia">Enviar a otra dependencia</label>
                                        <Field as="textarea" className="form-control" id="enviarAOtraDependencia" rows="3" name="enviarAOtraDependencia" placeholder="Información para enviar a otra dependencia...." value={from != null ? getTextoTransaccion4 : null} onChange={changeTextoTransaccion4}></Field>
                                        <div className="text-right">
                                            <span className="text-primary">{countTextArea4} / {getMaximoTextArea}</span>
                                        </div>
                                        <ErrorMessage name="enviarAOtraDependencia" component={() => (<span className="text-danger">{errors.enviarAOtraDependencia}</span>)} />
                                    </div>
                                </div>

                                <div className="col-md-12">
                                    <div className="form-group">
                                        <i className="fa fa-undo-alt text-success fa-2x mr-3"></i>
                                        <label htmlFor="regresarProcesoAlUltimoUsuario">Regresar proceso al último usuario</label>
                                        <Field as="textarea" className="form-control" id="regresarProcesoAlUltimoUsuario" rows="3" name="regresarProcesoAlUltimoUsuario" placeholder="Información para regresar proceso al último usuario...." value={from != null ? getTextoTransaccion5 : null} onChange={changeTextoTransaccion5}></Field>
                                        <div className="text-right">
                                            <span className="text-primary">{countTextArea5} / {getMaximoTextArea}</span>
                                        </div>
                                        <ErrorMessage name="regresarProcesoAlUltimoUsuario" component={() => (<span className="text-danger">{errors.regresarProcesoAlUltimoUsuario}</span>)} />
                                    </div>
                                </div>

                            </div>
                            <div className="block-content block-content-full text-right">
                                <button type="submit" className="btn btn-primary" >
                                    {from != null ? "Actualizar" : "Crear"}
                                </button>
                                <Link to={'/ActuacionesAdministracion'} className="font-size-h5 font-w600" >
                                    <button type="button" className="btn btn-danger" >Cancelar</button>
                                </Link>
                            </div>
                        </div>

                    </Form>
                )}
            </Formik>
        </>
    );
};
