import { ErrorMessage, Field, Form, Formik } from 'formik';
import { useState } from 'react';
import RemisionQuejaApi from '../../Api/Services/RemisionQuejaApi';
import InfoErrorApi from '../../Utils/InfoErrorApi';
import InfoExitoApi from '../../Utils/InfoExitoApi';
import { useParams } from "react-router";

function RemisionQuejaForm() {

    const [errorApi, setErrorApi] = useState('');
    const [errorVariablesApi, setErrorVariablesApi] = useState({});
    const [respuestaVariablesApi, setRespuestaVariablesApi] = useState(false);
    const [expedienteApi, setExpedienteApi] = useState({});
    const [respuestaExpedienteApi, setRespuestaExpedienteApi] = useState(false);
    const [datosFormExpediente, setDatosFormExpediente] = useState({});

    let { radicado, procesoDisciplinarioId } = useParams();

    const validarDatos = (datos) => {

        setDatosFormExpediente(datos);
        
        const data = {
            "data": {
                "type": "remision_queja",
                "attributes": {
                    "accion": 1,
                    "dependencia": datos.dependencia,
                    "expediente": datos.expediente,
                    "vigencia": datos.vigencia
                }
            }
        }

        RemisionQuejaApi.consultarValidacionExpediente(data).then(
            datos => {
                if(!datos.error){
                    console.log(datos);
                    setExpedienteApi(datos.data.attributes);
                    setRespuestaVariablesApi(false);
                    setRespuestaExpedienteApi(true);
                }
                else{
                    
                    setRespuestaExpedienteApi(false);
                    setRespuestaVariablesApi(false);

                    if(datos.variables){
                        setErrorVariablesApi(datos.variables);
                        setRespuestaVariablesApi(true);
                    }
                    else{
                        setErrorApi(datos.error);
                        window.showModal(1);
                    }
                }
            }
        )
    }

    const guardarDatos = () => {

        const data = {
            "data": {
                "type": "remision_queja",
                "attributes": {
                    "accion": 2,
                    "dependencia": datosFormExpediente.dependencia,
                    "expediente": datosFormExpediente.expediente,
                    "vigencia": datosFormExpediente.vigencia,
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "version": 305
                }
            }
        }
        
        console.log(data);

        RemisionQuejaApi.addIncorporacionExpediente(data).then(
            datos => {
                if(!datos.error){                    
                    window.showModal(2);
                }
                else{
                    console.log(datos.error);
                    //setErrorApi(datos.error);
                    window.showModal(1);
                }
            }
        )

    }

    /*
    * Componentes
    */

    const componentExpedienteForm = () => {
        return(
            <>
                <Formik
                    initialValues={{
                        dependencia: '',
                        expediente: '',
                        vigencia: ''
                    }}
                    validate={(valores) => { 
                        let errores = {};

                        if(!valores.dependencia){
                            errores.dependencia = 'Debe seleccionar la dependencia';
                        }

                        if(!valores.expediente){
                            errores.expediente = 'Debe digitar el número de expediente';
                        }

                        if(!valores.vigencia){
                            errores.vigencia = 'Debe digitar la vigencia';
                        }

                        return errores;
                    }}
                    onSubmit={(valores, {resetForm}) => {
                        validarDatos(valores);
                    }}>
                        {({ errors }) => (
                            <Form>                                
                                <div className='row'>
                                    <div className='col-md-12'>
                                        <div className='alert alert-primary' role='alert'>
                                            <p className='mb-0'> ATENCIÓN: </p>
                                            <p className='mb-0'> POR PARÁMETROS DEL SISTEMA, PARA UNA QUEJA CLASIFICADA EN LA ETAPA DE EVALUACIÓN COMO: <b><u>INCOPORACION</u></b>; SE DEBE SELECCIONAR LA DEPENDENCIA DEL EJE DE POTESTAD DISCIPLINARIA QUE CONTINUARÁ CON EL TRÁMITE DEL EXPEDIENTE.</p>
                                        </div>
                                    </div>

                                    <div className='col-md-12'>
                                        <label htmlFor='dependencia'>Dependencia <span className='text-danger'>*</span></label>
                                        <Field as='select' className='form-control' id='dependencia' name='dependencia'>
                                            <option value=''>Seleccione la dependencia</option>
                                            <option value='50'>DIRECCIÓN DE INVESTIGACIONES ESPECIALES Y APOYO TÉCNICO</option>
                                            <option value='64'>P.D. PARA ASUNTOS DISCIPLINARIOS I</option>
                                            <option value='31'>P.D. PARA ASUNTOS DISCIPLINARIOS II</option>
                                            <option value='5'>P.D. PARA ASUNTOS DISCIPLINARIOS III</option>
                                            <option value='61'>P.D. PARA ASUNTOS DISCIPLINARIOS IV</option>
                                            <option value='318'>P.D. PARA LA COORDINACIÓN DE ASUNTOS DISCIPLINARIOS</option>
                                            <option value='300'>P.D. PARA LA SEGUNDA INSTANCIA</option>
                                            <option value='310'>SECRETARÍA COMÚN</option>
                                        </Field>
                                        <ErrorMessage name='dependencia'component={() => (<span className='text-danger'>{errors.dependencia}</span>)} />
                                    </div>

                                    <div className='col-md-6'>
                                        <label htmlFor='expediente'>No de expediente <span className='text-danger'>*</span></label>
                                        <Field className='form-control' id='expediente' name='expediente'></Field>
                                        <ErrorMessage name='expediente'component={() => (<span className='text-danger'>{errors.expediente}</span>)} />
                                    </div>

                                    <div className='col-md-6'>
                                        <label htmlFor='vigencia'>Vigencia del expediente <span className='text-danger'>*</span></label>
                                        <Field className='form-control' id='vigencia' name='vigencia'></Field>
                                        <ErrorMessage name='vigencia'component={() => (<span className='text-danger'>{errors.vigencia}</span>)} />
                                    </div>

                                    <div className='block-content block-content-full text-right'>                                        
                                        <button type='submit' className='btn btn-danger'>
                                            <i className='fa fa-fw fa-search'></i> Validar Expediente
                                        </button>
                                    </div>
                                </div>
                            </Form>
                        )}
                </Formik>
            </>
        );
    }

    const componentErrorExpendiente = () => {
        return(
            <>
                <div className='row'>
                    <div className='col-md-12'>
                        <div className='alert alert-danger' role='alert'>
                            <p className='mb-0 text-center'><b>INCONSISTENCIA EN LOS DATOS INGRESADOS</b></p>
                            <p className='mb-0'>No se encontró registrado ningún Proceso Disciplinario asociado a los datos ingresados, es necesario que confirme la información para continuar con el trámite del expediente. <br></br>Si posterior a la revisión de los datos del expediente a incorporar persiste el presente mensaje, por favor contacte a la Dirección de TIC e informe de la presente alerta.</p><br></br>
                            <b>* Dependencia cosultada: </b>{ errorVariablesApi.dependencia ? errorVariablesApi.dependencia : null }<br></br>
                            <b>* SINPROC consultado: </b>{ errorVariablesApi.expediente ? errorVariablesApi.expediente : null }<br></br>
                            <b>* Para la vigencia: </b>{ errorVariablesApi.vigencia ? errorVariablesApi.vigencia : null }<br></br>
                        </div>
                    </div>
                </div>
            </>
        );
    }

    const componentInformeExpendiente = () => {
        return(
            <>
                <div className='row'>
                    <div className='col-md-12 text-center'>
                        <label>DATOS DEL EXPEDIENTE A INCORPORAR</label>
                    </div>

                    <div className='col-md-12'>
                        <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                            <tbody>
                                <tr>
                                    <th>Datos consultados</th>
                                    <td>SINPROC {datosFormExpediente.expediente} - VIGENCIA {datosFormExpediente.vigencia}</td>
                                </tr>
                                <tr>
                                    <th>Hechos</th>
                                    <td>{ expedienteApi?.hechos ? expedienteApi?.hechos : null }</td>
                                </tr>
                                <tr>
                                    <th>Dependencia de gestión</th>
                                    <td>{ expedienteApi?.dependencia_actual ? expedienteApi?.dependencia_actual : null }</td>
                                </tr>
                                <tr>
                                    <th>Etapa actual en SINPROC</th>
                                    <td>{ expedienteApi?.etapa_actual ? expedienteApi?.etapa_actual : null }</td>
                                </tr>                                
                                <tr>
                                    <th>Estado</th>
                                    <td>{ expedienteApi?.estado ? expedienteApi?.estado : null }</td>
                                </tr>                               
                                <tr>
                                    <th>Vigencia</th>
                                    <td>{ expedienteApi?.id_tramite ? expedienteApi?.id_tramite : null }</td>
                                </tr>
                            </tbody>
                        </table>
                    </div>

                    { 
                        (expedienteApi?.id_dependencia_asig && datosFormExpediente.dependencia && expedienteApi?.id_dependencia_asig !== datosFormExpediente.dependencia) ?
                        (
                            <div className='col-md-12'>
                                <div className='alert alert-danger' role='alert'>
                                    <p className='mb-0 text-center'><b>ATENCIÓN</b></p>
                                    <p className='mb-0'> La dependencia seleccionada <b>NO</b> corresponde con la dependencia actual del expediente.</p>
                                </div>
                            </div>
                        ) : 
                        (
                            <div className='col-md-12'>
                                <div className='block-content block-content-full text-center'>                                        
                                    <button onClick={() => guardarDatos()} className='btn btn-primary'>
                                        <i className='fa fa-fw fa-save'></i> Guardar y asignar
                                    </button>
                                </div>
                            </div>
                        ) 
                    }

                </div>
            </>
        );
    }

    return(
        <>
            { <InfoErrorApi error={errorApi}/> }
            { <InfoExitoApi /> }
            <div className='content'>
                <div className='row'>
                    <div className='col-md-12'>
                        <div className='block block-themed'>
                            <div className='block-header'>
                                <h3 className='block-title'>SINPROC NO {radicado} ::: REMISIÓN QUEJA</h3>
                            </div>
                            <div className='block-content'>
                                { componentExpedienteForm() }
                                { respuestaVariablesApi ? componentErrorExpendiente() : null }
                                { respuestaExpedienteApi ? componentInformeExpendiente() : null }
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );

}

export default RemisionQuejaForm;
